import React, { useState, useEffect, useCallback } from 'react';
import BridgeGameUI from './BridgeGameUI';
import ScoreAdjustment from './components/ScoreAdjustment';
import { 
  determineVulnerability,
  parseContract, 
  calculateBridgeScore 
} from './utils/scoring';
import './global.css';
import './styles/scoreAdjustment.css';

const App = () => {
  // State to track current view (main game or score adjustment)
  const [currentView, setCurrentView] = useState('game');
  
  // Create a function to generate a new initial state
  const createInitialState = () => {
    return {
      gameNumber: 1,
      dealNumber: 1,
      scores: {
        nsScore: 0,
        ewScore: 0
      },
      deals: [],
      currentDeal: {
        contract: '',
        declarer: '',
        result: null,
        tricks: null,
        vulnerable: determineVulnerability(1)
      },
      isAwake: false,
      showScorePopup: false
    };
  };

  const [gameState, setGameState] = useState(() => {
    // Try to load game state from local storage
    const savedState = localStorage.getItem('bridgeGameState');
    
    // Check if we have a valid saved state
    if (savedState) {
      try {
        const parsedState = JSON.parse(savedState);
        
        // Check if it's a new session (you can use a timestamp or other method)
        // For simplicity, we'll just always start fresh
        // If you want to resume the previous game, you can remove this condition
        const shouldStartFresh = true; // Change this logic if you want to resume games
        
        if (shouldStartFresh) {
          // Start a new game
          return createInitialState();
        }
        
        return parsedState;
      } catch (e) {
        console.error('Error parsing saved game state', e);
        return createInitialState();
      }
    }
    
    // Default initial state
    return createInitialState();
  });
  
  // Save game state to local storage whenever it changes
  // Use a more specific dependency array to avoid infinite loops
  useEffect(() => {
    localStorage.setItem('bridgeGameState', JSON.stringify(gameState));
  }, [
    gameState.gameNumber, 
    gameState.dealNumber, 
    gameState.scores.nsScore, 
    gameState.scores.ewScore,
    gameState.isAwake,
    gameState.showScorePopup,
    // We don't want to deeply track all changes to currentDeal as it would cause too many updates
    // Instead, just track these key properties:
    gameState.currentDeal.contract,
    gameState.currentDeal.result
  ]);
  
  // For storing score adjustment data
  const [adjustmentData, setAdjustmentData] = useState(null);
  
  // Handle choosing Bonus Bridge scoring
  const handleChooseBonusBridge = useCallback(() => {
    // Hide the score popup and show the Score Adjustment view
    setGameState(prev => ({
      ...prev,
      showScorePopup: false
    }));
    
    // Switch to score adjustment view
    setCurrentView('scoreAdjustment');
  }, []);
  
  // Handle choosing Party Bridge scoring
  const handleChoosePartyBridge = useCallback(() => {
    // Hide the score popup and proceed directly to next deal
    setGameState(prev => ({
      ...prev,
      showScorePopup: false
    }));
    
    // Process the deal with standard scoring
    handleNextDeal();
  }, []);
  
  // Handle saving score adjustments
  const handleSaveAdjustment = useCallback((data) => {
    // Save the adjustment data
    setAdjustmentData(data);
    
    // Return to game view and proceed to next deal
    setCurrentView('game');
    handleNextDeal();
  }, []);
  
  // Handle canceling score adjustment
  const handleCancelAdjustment = useCallback(() => {
    // Return to game view without saving
    setCurrentView('game');
    
    // Make sure popup is closed
    setGameState(prev => ({
      ...prev,
      showScorePopup: false
    }));
  }, []);

  // Handle starting a new deal
  const handleNextDeal = useCallback(() => {
    setGameState(prev => {
      // Store the completed deal in history first
      const completedDeal = {
        ...prev.currentDeal,
        dealNumber: prev.dealNumber
      };
      
      // Calculate the scores from the current deal
      let nsScore = prev.scores.nsScore;
      let ewScore = prev.scores.ewScore;
      
      if (prev.currentDeal.contract && prev.currentDeal.result !== null) {
        const contractDetails = parseContract(
          prev.currentDeal.contract, 
          prev.currentDeal.result, 
          prev.currentDeal.vulnerable
        );
        
        if (contractDetails) {
          const dealScore = calculateBridgeScore(contractDetails);
          nsScore += dealScore.nsPoints;
          ewScore += dealScore.ewPoints;
        }
      }
      
      return {
        ...prev,
        dealNumber: prev.dealNumber + 1,
        showScorePopup: false,
        scores: {
          nsScore: nsScore,
          ewScore: ewScore
        },
        deals: [...prev.deals, completedDeal],
        currentDeal: {
          contract: '',
          declarer: '',
          result: null,
          tricks: null,
          vulnerable: determineVulnerability(prev.dealNumber + 1)
        }
      };
    });
  }, []);
  
  const handleNewGame = useCallback(() => {
    setGameState(prev => ({
      gameNumber: prev.gameNumber + 1,
      dealNumber: 1,
      scores: {
        nsScore: 0,
        ewScore: 0
      },
      deals: [],
      currentDeal: {
        contract: '',
        declarer: '',
        result: null,
        tricks: null,
        vulnerable: determineVulnerability(1)
      },
      isAwake: prev.isAwake,
      showScorePopup: false
    }));
  }, []);
  
  const updateCurrentDeal = useCallback((updatedDeal) => {
    setGameState(prev => ({
      ...prev,
      currentDeal: {
        ...prev.currentDeal,
        ...updatedDeal
      }
    }));
  }, []);
  
  return (
    <div className="app-container">
      {currentView === 'game' ? (
        <BridgeGameUI 
          gameState={gameState} 
          updateCurrentDeal={updateCurrentDeal}
          onNextDeal={handleNextDeal}
          onNewGame={handleNewGame}
          setGameState={setGameState}
          onChooseBonusBridge={handleChooseBonusBridge}
          onChoosePartyBridge={handleChoosePartyBridge}
        />
      ) : (
        <ScoreAdjustment 
          currentDeal={gameState.currentDeal}
          onSaveAdjustment={handleSaveAdjustment}
          onCancel={handleCancelAdjustment}
        />
      )}
    </div>
  );
};

export default App;
