import React, { useState, useEffect } from 'react';
import './ContractInput.css';

const ContractInput = ({ onContractChange, initialContract = '', onComplete }) => {
  const [level, setLevel] = useState('');
  const [suit, setSuit] = useState('');
  const [declarer, setDeclarer] = useState('');
  const [doubled, setDoubled] = useState('');
  const [isComplete, setIsComplete] = useState(false);
  const [localContract, setLocalContract] = useState('');
  
  // Parse initialContract if provided
  useEffect(() => {
    if (initialContract) {
      const contractMatch = initialContract.match(/(\d)([♣♦♥♠]|NT)\s+([NESW])(X{0,2})/);
      if (contractMatch) {
        setLevel(contractMatch[1]);
        setSuit(contractMatch[2]);
        setDeclarer(contractMatch[3]);
        setDoubled(contractMatch[4] || '');
        setLocalContract(initialContract);
        setIsComplete(true);
      }
    } else {
      setLevel('');
      setSuit('');
      setDeclarer('');
      setDoubled('');
      setLocalContract('');
      setIsComplete(false);
    }
  }, [initialContract]);
  
  // Update local contract when components change
  useEffect(() => {
    if (level && suit && declarer) {
      const newContract = `${level}${suit} ${declarer}${doubled}`;
      setLocalContract(newContract);
      setIsComplete(true);
    } else {
      setIsComplete(false);
    }
  }, [level, suit, declarer, doubled]);
  
  // Handle level selection
  const handleLevelClick = (selectedLevel) => {
    setLevel(selectedLevel);
  };
  
  // Handle suit selection
  const handleSuitClick = (selectedSuit) => {
    setSuit(selectedSuit);
  };
  
  // Handle declarer selection
  const handleDeclarerClick = (selectedDeclarer) => {
    setDeclarer(selectedDeclarer);
  };
  
  // Handle doubled selection
  const handleDoubledClick = (selectedDoubled) => {
    if (doubled === selectedDoubled) {
      setDoubled('');
    } else {
      setDoubled(selectedDoubled);
    }
  };
  
  // Handle confirm button click
  const handleConfirmClick = () => {
    if (isComplete) {
      onContractChange(localContract);
      if (onComplete && typeof onComplete === 'function') {
        onComplete();
      }
    }
  };
  
  // More compact styles
  const containerStyle = {
    padding: '5px 0',
    maxWidth: '100%',
    boxSizing: 'border-box'
  };
  
  const sectionStyle = {
    marginBottom: '8px' // Reduced from 15px
  };
  
  const labelStyle = {
    fontWeight: 'bold',
    marginBottom: '4px', // Reduced from 8px
    fontSize: '14px', // Reduced from 16px
    color: '#1e5c8b'
  };
  
  const buttonRowStyle = {
    display: 'flex',
    gap: '4px', // Reduced from 6px
    marginBottom: '8px'
  };
  
  return (
    <div className="contract-input-container" style={containerStyle}>
      {/* Level and Suit combined section */}
      <div style={sectionStyle}>
        <div style={labelStyle}>Level and Suit:</div>
        
        {/* Level Buttons (1-7) */}
        <div style={buttonRowStyle}>
          {[1, 2, 3, 4, 5, 6, 7].map((num) => (
            <button
              key={`level-${num}`}
              className={`level-btn ${level === num.toString() ? 'selected' : ''}`}
              onClick={() => handleLevelClick(num.toString())}
              data-level={num}
              style={{ height: '40px' }}
            >
              {num}
            </button>
          ))}
        </div>
        
        {/* Suit Buttons */}
        <div style={buttonRowStyle}>
          {['♣', '♦', '♥', '♠', 'NT'].map((s) => (
            <button
              key={`suit-${s}`}
              className={`suit-btn ${suit === s ? 'selected' : ''}`}
              onClick={() => handleSuitClick(s)}
              data-suit={s}
              style={{ height: '40px' }}
            >
              {s}
            </button>
          ))}
        </div>
      </div>
      
      {/* Declarer and Doubled in a combined row for space efficiency */}
      <div style={{ display: 'flex', gap: '10px' }}>
        {/* Declarer Input */}
        <div style={{ flex: 1, ...sectionStyle }}>
          <div style={labelStyle}>Declarer:</div>
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '4px' }}>
            {['N', 'S', 'E', 'W'].map((d) => (
              <button
                key={`declarer-${d}`}
                className={`declarer-btn ${declarer === d ? 'selected' : ''}`}
                onClick={() => handleDeclarerClick(d)}
                style={{ height: '40px' }}
              >
                {d}
              </button>
            ))}
          </div>
        </div>
        
        {/* Doubled Input */}
        <div style={{ flex: 1, ...sectionStyle }}>
          <div style={labelStyle}>Doubled:</div>
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '4px' }}>
            <button
              className={`doubled-btn ${doubled === '' ? 'selected' : ''}`}
              onClick={() => handleDoubledClick('')}
              style={{ height: '40px' }}
            >
              -
            </button>
            <button
              className={`doubled-btn ${doubled === 'X' ? 'selected' : ''}`}
              onClick={() => handleDoubledClick('X')}
              style={{ height: '40px' }}
            >
              X
            </button>
            <button
              className={`doubled-btn ${doubled === 'XX' ? 'selected' : ''}`}
              onClick={() => handleDoubledClick('XX')}
              style={{ height: '40px' }}
            >
              XX
            </button>
          </div>
        </div>
      </div>
      
      {/* Contract preview and confirm in a single row */}
      <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginTop: '8px' }}>
        {/* Confirm button */}
        <button
          style={{
            flex: 3,
            padding: '8px',
            backgroundColor: isComplete ? '#3498db' : '#ccc',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            fontSize: '16px',
            fontWeight: 'bold',
            cursor: isComplete ? 'pointer' : 'not-allowed',
            height: '40px'
          }}
          onClick={handleConfirmClick}
          disabled={!isComplete}
        >
          Confirm Contract
        </button>
        
        {/* Compact preview */}
        {level && suit && declarer && (
          <div style={{
            flex: 2,
            padding: '8px',
            backgroundColor: '#f5f5f5',
            borderRadius: '6px',
            textAlign: 'center',
            fontSize: '14px',
            height: '24px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}>
            {level}{suit} {declarer}{doubled}
          </div>
        )}
      </div>
    </div>
  );
};

export default ContractInput;
