import React from 'react';

const ScoreTracking = ({ scores, currentDeal }) => {
  // Calculate raw bridge score (placeholder - you'll use your actual scoring logic)
  const calculateRawScore = () => {
    // For demonstration, just showing a basic score
    // In real implementation, this would use your complex scoring logic
    return currentDeal && currentDeal.result < 0 ? -200 : 0;
  };

  // Extract contract details for display
  const getContractString = () => {
    if (!currentDeal || !currentDeal.contract) return '';
    return currentDeal.contract;
  };

  // Get result description
  const getResultString = () => {
    if (!currentDeal || currentDeal.result === null) return '';
    
    if (currentDeal.result > 0) {
      return `Made +${currentDeal.result}`;
    } else if (currentDeal.result < 0) {
      return `Down ${Math.abs(currentDeal.result)}`;
    } else {
      return 'Made exactly';
    }
  };

  // Get deal-specific score
  const getDealScore = () => {
    const rawScore = calculateRawScore();
    const isNS = currentDeal && (currentDeal.contract || '').includes('N') || 
                (currentDeal.contract || '').includes('S');
    
    if (isNS) {
      return { ns: rawScore, ew: 0 };
    } else {
      return { ns: 0, ew: rawScore };
    }
  };

  const dealScore = getDealScore();

  return (
    <div className="score-tracking">
      <h3>Score Summary</h3>
      
      <div className="score-table">
        <div className="score-header">
          <div>Team</div>
          <div>This Deal</div>
          <div>Running Total</div>
        </div>
        
        <div className="score-row">
          <div>North-South</div>
          <div>{dealScore.ns}</div>
          <div>{scores.nsScore}</div>
        </div>
        
        <div className="score-row">
          <div>East-West</div>
          <div>{dealScore.ew}</div>
          <div>{scores.ewScore}</div>
        </div>
      </div>
      
      <div className="contract-info">
        <div>Contract: {getContractString()}</div>
        <div>Result: {getResultString()}</div>
      </div>
    </div>
  );
};

export default ScoreTracking;
