import React, { useState, useEffect } from 'react';
import './TrickInput.css';

const TrickInput = ({ onTrickChange, initialResult = null, contract = '', onChangeContract }) => {
  const [selectedTrick, setSelectedTrick] = useState(null);
  const [requiredTricks, setRequiredTricks] = useState(7);
  
  // Calculate required tricks when contract changes
  useEffect(() => {
    if (contract) {
      const contractMatch = contract.match(/(\d)([♣♦♥♠]|NT)\s+([NESW])(X{0,2})/);
      if (contractMatch) {
        const level = parseInt(contractMatch[1]);
        setRequiredTricks(level + 6);
      }
    }
  }, [contract]);
  
  // Set selected trick from initialResult if provided
  useEffect(() => {
    if (initialResult !== null && requiredTricks) {
      const actualTricks = requiredTricks + initialResult;
      setSelectedTrick(actualTricks);
    }
  }, [initialResult, requiredTricks]);
  
  // Handle trick button click
  const handleTrickClick = (numTricks) => {
    setSelectedTrick(numTricks);
    const result = numTricks - requiredTricks;
    onTrickChange(result, numTricks);
  };
  
  // Container style
  const containerStyle = {
    display: 'flex',
    flexDirection: 'column',
    gap: '8px',
    padding: '5px 0'
  };
  
  // Required tricks banner style
  const requiredTricksStyle = {
    backgroundColor: '#f0f3f8',
    padding: '8px',
    borderRadius: '6px',
    textAlign: 'center',
    fontSize: '14px',
    fontWeight: 'bold',
    color: '#2c3e50',
    border: '1px solid #dfe6e9',
    marginBottom: '5px'
  };
  
  // Create a more efficient grid layout for trick buttons
  const renderTrickGrid = () => {
    // Create a 4x4 grid (with the last row having only 2 buttons)
    return (
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: '6px' }}>
        {Array.from({ length: 14 }, (_, i) => {
          // Only create buttons for tricks 0-13
          if (i <= 13) {
            const isExact = i === requiredTricks;
            const isMade = i > requiredTricks;
            const isDown = i < requiredTricks;
            
            let backgroundColor, borderColor;
            if (selectedTrick === i) {
              if (isExact) {
                backgroundColor = '#ff9800';
                borderColor = '#ff9800';
              } else if (isMade) {
                backgroundColor = '#4caf50';
                borderColor = '#4caf50';
              } else {
                backgroundColor = '#f44336';
                borderColor = '#f44336';
              }
            } else {
              if (isExact) {
                backgroundColor = '#fff3e0';
                borderColor = '#ffe0b2';
              } else if (isMade) {
                backgroundColor = '#e8f5e9';
                borderColor = '#c8e6c9';
              } else {
                backgroundColor = '#ffebee';
                borderColor = '#ffcdd2';
              }
            }
            
            return (
              <button
                key={`trick-${i}`}
                style={{
                  height: '38px',
                  border: `1px solid ${borderColor}`,
                  borderRadius: '6px',
                  backgroundColor,
                  color: selectedTrick === i ? 'white' : 'black',
                  fontWeight: 'bold',
                  fontSize: '15px',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  cursor: 'pointer',
                  padding: '0',
                  // Make tricks 12 and 13 wider (2 columns each)
                  gridColumn: i >= 12 ? 'span 2' : 'span 1'
                }}
                onClick={() => handleTrickClick(i)}
              >
                {i}
              </button>
            );
          }
          return null;
        })}
      </div>
    );
  };
  
  return (
    <div style={containerStyle}>
      <div style={requiredTricksStyle}>
        Required tricks: {requiredTricks}
      </div>
      
      {/* Trick Grid */}
      {renderTrickGrid()}
      
      {/* Change Contract Button - only one button */}
      <button 
        style={{
          width: '100%',
          height: '40px',
          marginTop: '8px',
          border: '1px solid #ddd',
          borderRadius: '6px',
          backgroundColor: '#f5f5f5',
          color: '#2c3e50',
          fontSize: '15px',
          fontWeight: 'bold',
          cursor: 'pointer'
        }}
        onClick={onChangeContract}
      >
        Change Contract
      </button>
    </div>
  );
};

export default TrickInput;
