import React, { useState } from 'react';

const ContractInput = ({ onContractChange }) => {
  const [level, setLevel] = useState('');
  const [suit, setSuit] = useState('');
  const [declarer, setDeclarer] = useState('');
  const [doubled, setDoubled] = useState('');

  // Suits in bridge: Clubs (♣), Diamonds (♦), Hearts (♥), Spades (♠), No Trump (NT)
  const suits = ['♣', '♦', '♥', '♠', 'NT'];
  const levels = [1, 2, 3, 4, 5, 6, 7];
  const players = ['N', 'E', 'S', 'W'];
  const doubledOptions = ['', 'X', 'XX'];

  // Update the contract whenever any value changes
  const updateContract = (newLevel, newSuit, newDeclarer, newDoubled) => {
    // Only form a contract if we have the minimum required values
    if (newLevel && newSuit && newDeclarer) {
      const contractString = `${newLevel}${newSuit} ${newDeclarer}${newDoubled}`;
      onContractChange(contractString);
    } else {
      onContractChange('');
    }
  };

  const handleLevelChange = (newLevel) => {
    setLevel(newLevel);
    updateContract(newLevel, suit, declarer, doubled);
  };

  const handleSuitChange = (newSuit) => {
    setSuit(newSuit);
    updateContract(level, newSuit, declarer, doubled);
  };

  const handleDeclarerChange = (newDeclarer) => {
    setDeclarer(newDeclarer);
    updateContract(level, suit, newDeclarer, doubled);
  };

  const handleDoubledChange = (newDoubled) => {
    setDoubled(newDoubled);
    updateContract(level, suit, declarer, newDoubled);
  };

  return (
    <div className="contract-input">
      <h3>Contract</h3>
      
      <div className="input-group">
        <div className="level-selector">
          {levels.map(l => (
            <button 
              key={`level-${l}`}
              className={level === l ? 'selected' : ''}
              onClick={() => handleLevelChange(l)}
            >
              {l}
            </button>
          ))}
        </div>

        <div className="suit-selector">
          {suits.map(s => (
            <button 
              key={`suit-${s}`}
              className={`suit-btn ${suit === s ? 'selected' : ''} ${
                s === '♥' || s === '♦' ? 'red' : ''
              }`}
              onClick={() => handleSuitChange(s)}
            >
              {s}
            </button>
          ))}
        </div>
      </div>

      <div className="input-group">
        <div className="declarer-selector">
          {players.map(p => (
            <button 
              key={`declarer-${p}`}
              className={declarer === p ? 'selected' : ''}
              onClick={() => handleDeclarerChange(p)}
            >
              {p}
            </button>
          ))}
        </div>

        <div className="doubled-selector">
          {doubledOptions.map(d => (
            <button 
              key={`doubled-${d || 'none'}`}
              className={doubled === d ? 'selected' : ''}
              onClick={() => handleDoubledChange(d)}
            >
              {d || '-'}
            </button>
          ))}
        </div>
      </div>

      <div className="current-contract">
        {level && suit && declarer ? `${level}${suit} ${declarer}${doubled}` : 'No Contract'}
      </div>
    </div>
  );
};

export default ContractInput;
