import React from 'react';
import { parseContract, calculateBridgeScore } from '../utils/scoring';

const ScoreTracking = ({ scores, currentDeal, onChooseBonusBridge, onChoosePartyBridge }) => {
  const contractDetails = currentDeal && currentDeal.contract 
    ? parseContract(currentDeal.contract, currentDeal.result, currentDeal.vulnerable) 
    : null;
  
  const score = contractDetails 
    ? calculateBridgeScore(contractDetails) 
    : { nsPoints: 0, ewPoints: 0 };
  
  const madeContract = currentDeal.result >= 0;
  const isNS = contractDetails ? contractDetails.isNS : false;
  
  let nsScoreForDeal = 0;
  let ewScoreForDeal = 0;
  
  if (contractDetails) {
    if (madeContract) {
      // If contract was made, declarer's side gets the score
      if (isNS) {
        nsScoreForDeal = score.nsPoints;
      } else {
        ewScoreForDeal = score.ewPoints;
      }
    } else {
      // If contract was defeated, defenders get the score
      if (isNS) {
        ewScoreForDeal = score.ewPoints;
      } else {
        nsScoreForDeal = score.nsPoints;
      }
    }
  }
  
  const getContractSummary = () => {
    if (!contractDetails) return "";
    
    const level = contractDetails.level;
    const suit = contractDetails.suit;
    const declarer = currentDeal.contract.match(/\s+([NESW])/)[1];
    const doubled = contractDetails.isDoubled ? "X" : 
                    contractDetails.isRedoubled ? "XX" : "";
    
    return `${level}${suit} ${declarer}${doubled}`;
  };
  
  const getResultSummary = () => {
    if (!contractDetails) return "";
    
    const undertricks = Math.abs(currentDeal.result);
    return currentDeal.result >= 0 
      ? `Made +${currentDeal.result}` 
      : `Down ${undertricks}`;
  };
  
  // Get required tricks based on contract level
  const getRequiredTricks = () => {
    if (!contractDetails) return 0;
    return contractDetails.level + 6;
  };
  
  // Get actual tricks based on required tricks and result
  const getActualTricks = () => {
    if (!contractDetails) return 0;
    return getRequiredTricks() + currentDeal.result;
  };
  
  // Determine which side gets the score
  const getScoringDirection = () => {
    if (!contractDetails) return "";
    
    if (madeContract) {
      // If contract was made, declarer's side gets the score
      return isNS ? "North-South" : "East-West";
    } else {
      // If contract was defeated, defenders get the score
      return isNS ? "East-West" : "North-South";
    }
  };
  
  // Get the scoring amount for the current deal
  const getScoringAmount = () => {
    if (!contractDetails) return 0;
    return isNS ? (madeContract ? score.nsPoints : score.ewPoints) : 
                  (madeContract ? score.ewPoints : score.nsPoints);
  };
  
  const [showDetails, setShowDetails] = React.useState(false);
  
  const toggleDetails = () => {
    setShowDetails(!showDetails);
  };
  
  return (
    <div className="score-tracking">
      <div className="score-summary">
        <h3>Score Summary</h3>
        <table className="score-table">
          <thead>
            <tr>
              <th>Team</th>
              <th>This Deal</th>
              <th>Running Total</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>North-South</td>
              <td>{nsScoreForDeal}</td>
              <td>{scores.nsScore}</td>
            </tr>
            <tr>
              <td>East-West</td>
              <td>{ewScoreForDeal}</td>
              <td>{scores.ewScore}</td>
            </tr>
          </tbody>
        </table>
      </div>
      
      <div className="contract-info">
        <div><strong>Contract:</strong> {getContractSummary()}</div>
        <div><strong>Result:</strong> {getResultSummary()}</div>
      </div>
      
      <button className="details-btn" onClick={toggleDetails}>
        {showDetails ? "Hide Score Details" : "Show Score Details"}
      </button>
      
      {showDetails && (
        <div className="score-details">
          <h4>Score Calculation</h4>
          <ul>
            <li>Contract: {getContractSummary()} by {contractDetails.isNS ? "NS" : "EW"} ({contractDetails.isVulnerable ? "vulnerable" : "not vulnerable"})</li>
            <li>Required tricks: {contractDetails.level} + 6 = {getRequiredTricks()}</li>
            <li>Actual tricks: {getActualTricks()}</li>
            {currentDeal.result < 0 && (
              <li>Undertricks ({Math.abs(currentDeal.result)}): {getScoringAmount()}</li>
            )}
            <li>Total: {getScoringAmount()} points to {getScoringDirection()}</li>
          </ul>
        </div>
      )}
      
      {/* Scoring options buttons */}
      <div className="scoring-options">
        <button 
          className="bonus-bridge-btn" 
          onClick={onChooseBonusBridge}
        >
          Bonus Bridge Score Adjustments (recommended)
        </button>
        <button 
          className="party-bridge-btn" 
          onClick={onChoosePartyBridge}
        >
          Skip Adjustment (Party Bridge scoring)
        </button>
      </div>
    </div>
  );
};

export default ScoreTracking;
