// File: src/components/TricksInput.js
import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';


const TricksInput = () => {
  const { gameState, dispatch } = useContext(GameContext);
  const { contract, tricksRequired, tricksMade } = gameState.currentDeal;

  // Calculate the number of tricks required based on contract level
  const calculateTricksRequired = (level) => {
    return level === null ? null : level + 6;
  };

  // Handle tricks made input
  const handleTricksMadeChange = (e) => {
    const value = parseInt(e.target.value);
    if (isNaN(value) || value < 0 || value > 13) return; // Validate input
    
    dispatch({
      type: 'SET_TRICKS_MADE',
      payload: value
    });
  };

  // Generate options for tricks selector
  const renderTricksOptions = () => {
    const options = [];
    for (let i = 0; i <= 13; i++) {
      options.push(
        <option key={i} value={i}>
          {i}
        </option>
      );
    }
    return options;
  };

  // Calculate result text (Made +2, Down 1, etc.)
  const getResultText = () => {
    if (tricksRequired === null || tricksMade === null) return '';
    
    const difference = tricksMade - tricksRequired;
    if (difference === 0) return 'Made exactly';
    if (difference > 0) return `Made +${difference}`;
    return `Down ${Math.abs(difference)}`;
  };

  return (
    <div className="component-container">
      <h2>Tricks Made</h2>
      
      <div className="input-group">
        <label htmlFor="tricks-made">Tricks:</label>
        <select
          id="tricks-made"
          value={tricksMade === null ? '' : tricksMade}
          onChange={handleTricksMadeChange}
          disabled={contract === null}
          className="input-select"
        >
          <option value="" disabled>Select</option>
          {renderTricksOptions()}
        </select>
        
        {tricksRequired !== null && (
          <div className="tricks-info">
            <span>Required: {tricksRequired}</span>
            <span className={`result-text ${tricksMade >= tricksRequired ? 'made' : 'down'}`}>
              {getResultText()}
            </span>
          </div>
        )}
      </div>
    </div>
  );
};

export default TricksInput;
