import React, { useState, useEffect } from 'react';

const TrickInput = ({ onTrickChange, initialResult, contract }) => {
  const [tricks, setTricks] = useState(null);
  const [contractLevel, setContractLevel] = useState(null);
  
  // Initialize from props if provided
  useEffect(() => {
    if (initialResult !== null && contract) {
      const match = contract.match(/(\d)/);
      if (match) {
        const level = parseInt(match[1]);
        setContractLevel(level);
        
        // Calculate tricks from result and level
        const calculatedTricks = level + 6 + initialResult;
        setTricks(calculatedTricks);
      }
    }
  }, [initialResult, contract]);
  
  // Extract contract level when contract changes
  useEffect(() => {
    if (contract) {
      const match = contract.match(/(\d)/);
      if (match) {
        const level = parseInt(match[1]);
        setContractLevel(level);
      }
    } else {
      setContractLevel(null);
    }
  }, [contract]);
  
  // In bridge, contracts range from 1-7, requiring 7-13 tricks
  const calculateRequiredTricks = (level) => {
    return level ? parseInt(level) + 6 : null;
  };
  
  // Update the tricks taken
  const handleTricksChange = (numTricks) => {
    setTricks(numTricks);
    
    // If contract level is set, calculate the result
    if (contractLevel !== null) {
      const requiredTricks = calculateRequiredTricks(contractLevel);
      const result = numTricks - requiredTricks;
      onTrickChange(result, numTricks);
    }
  };
  
  return (
    <div className="trick-input">
      <div className="tricks-grid">
        <button 
          className={`bridge-button ${tricks === 0 ? "selected" : ""}`}
          onClick={() => handleTricksChange(0)}
        >
          0
        </button>
        <button 
          className={`bridge-button ${tricks === 1 ? "selected" : ""}`}
          onClick={() => handleTricksChange(1)}
        >
          1
        </button>
        <button 
          className={`bridge-button ${tricks === 2 ? "selected" : ""}`}
          onClick={() => handleTricksChange(2)}
        >
          2
        </button>
        
        <button 
          className={`bridge-button ${tricks === 3 ? "selected" : ""}`}
          onClick={() => handleTricksChange(3)}
        >
          3
        </button>
        <button 
          className={`bridge-button ${tricks === 4 ? "selected" : ""}`}
          onClick={() => handleTricksChange(4)}
        >
          4
        </button>
        <button 
          className={`bridge-button ${tricks === 5 ? "selected" : ""}`}
          onClick={() => handleTricksChange(5)}
        >
          5
        </button>
        
        <button 
          className={`bridge-button ${tricks === 6 ? "selected" : ""}`}
          onClick={() => handleTricksChange(6)}
        >
          6
        </button>
        <button 
          className={`bridge-button ${tricks === 7 ? "selected" : ""}`}
          onClick={() => handleTricksChange(7)}
        >
          7
        </button>
        <button 
          className={`bridge-button ${tricks === 8 ? "selected" : ""}`}
          onClick={() => handleTricksChange(8)}
        >
          8
        </button>
        
        <button 
          className={`bridge-button ${tricks === 9 ? "selected" : ""}`}
          onClick={() => handleTricksChange(9)}
        >
          9
        </button>
        <button 
          className={`bridge-button ${tricks === 10 ? "selected" : ""}`}
          onClick={() => handleTricksChange(10)}
        >
          10
        </button>
        <button 
          className={`bridge-button ${tricks === 11 ? "selected" : ""}`}
          onClick={() => handleTricksChange(11)}
        >
          11
        </button>
        
        <button 
          className={`bridge-button ${tricks === 12 ? "selected" : ""}`}
          onClick={() => handleTricksChange(12)}
        >
          12
        </button>
        <button 
          className={`bridge-button ${tricks === 13 ? "selected" : ""}`}
          onClick={() => handleTricksChange(13)}
        >
          13
        </button>
      </div>
    </div>
  );
};

export default TrickInput;
