import React, { useState, useEffect } from 'react';
import './ContractInput.css';

/**
 * Contract Input Component for Bridge Game
 * Allows selecting level, suit, declarer and doubled status
 */
const ContractInput = ({ onContractChange, initialContract }) => {
  // State for contract parameters
  const [selectedLevel, setSelectedLevel] = useState(null);
  const [selectedSuit, setSelectedSuit] = useState(null);
  const [selectedDeclarer, setSelectedDeclarer] = useState(null);
  const [selectedDoubled, setSelectedDoubled] = useState('-'); // Default to no doubling
  
  // Set initial values if provided
  useEffect(() => {
    if (initialContract) {
      const contractMatch = initialContract.match(/(\d)([♣♦♥♠]|NT)\s+([NESW])(X{0,2})/);
      if (contractMatch) {
        const [_, level, suit, declarer, doubled] = contractMatch;
        setSelectedLevel(level);
        setSelectedSuit(suit);
        setSelectedDeclarer(declarer);
        setSelectedDoubled(doubled || '-');
      }
    } else {
      // Reset selections when initialContract is cleared
      setSelectedLevel(null);
      setSelectedSuit(null);
      setSelectedDeclarer(null);
      setSelectedDoubled('-');
    }
  }, [initialContract]);
  
  // Handlers for selections
  const handleLevelSelect = (level) => {
    setSelectedLevel(level);
    updateContract(level, selectedSuit, selectedDeclarer, selectedDoubled);
  };
  
  const handleSuitSelect = (suit) => {
    setSelectedSuit(suit);
    updateContract(selectedLevel, suit, selectedDeclarer, selectedDoubled);
  };
  
  const handleDeclarerSelect = (declarer) => {
    setSelectedDeclarer(declarer);
    updateContract(selectedLevel, selectedSuit, declarer, selectedDoubled);
  };
  
  const handleDoubledSelect = (doubled) => {
    setSelectedDoubled(doubled);
    updateContract(selectedLevel, selectedSuit, selectedDeclarer, doubled);
  };
  
  // Update the complete contract and notify parent
  const updateContract = (level, suit, declarer, doubled) => {
    if (level && suit && declarer) {
      // Format the contract string
      let contractStr = `${level}${suit} ${declarer}`;
      
      // Add doubled status if applicable
      if (doubled && doubled !== '-') {
        contractStr += doubled;
      }
      
      // Notify parent component
      onContractChange(contractStr);
    }
  };
  
  return (
    <div className="contract-input">
      <h3>Contract:</h3>
      
      {/* Level Selection */}
      <div className="input-section">
        <div className="input-label">Level</div>
        <div className="input-buttons">
          {[1, 2, 3, 4, 5, 6, 7].map(level => (
            <button
              key={level}
              className={`input-btn ${selectedLevel === level.toString() ? 'selected' : ''}`}
              onClick={() => handleLevelSelect(level.toString())}
            >
              {level}
            </button>
          ))}
        </div>
      </div>
      
      {/* Suit Selection */}
      <div className="input-section">
        <div className="input-label">Suit</div>
        <div className="input-buttons">
          <button
            className={`input-btn ${selectedSuit === '♣' ? 'selected' : ''}`}
            onClick={() => handleSuitSelect('♣')}
          >
            ♣
          </button>
          <button
            className={`input-btn ${selectedSuit === '♦' ? 'selected' : ''}`}
            onClick={() => handleSuitSelect('♦')}
          >
            ♦
          </button>
          <button
            className={`input-btn ${selectedSuit === '♥' ? 'selected' : ''}`}
            onClick={() => handleSuitSelect('♥')}
          >
            ♥
          </button>
          <button
            className={`input-btn ${selectedSuit === '♠' ? 'selected' : ''}`}
            onClick={() => handleSuitSelect('♠')}
          >
            ♠
          </button>
          <button
            className={`input-btn ${selectedSuit === 'NT' ? 'selected' : ''}`}
            onClick={() => handleSuitSelect('NT')}
          >
            NT
          </button>
        </div>
      </div>
      
      {/* Declarer Selection */}
      <div className="input-section">
        <div className="input-label">Declarer</div>
        <div className="input-buttons">
          <button
            className={`input-btn ${selectedDeclarer === 'N' ? 'selected' : ''}`}
            onClick={() => handleDeclarerSelect('N')}
          >
            N
          </button>
          <button
            className={`input-btn ${selectedDeclarer === 'E' ? 'selected' : ''}`}
            onClick={() => handleDeclarerSelect('E')}
          >
            E
          </button>
          <button
            className={`input-btn ${selectedDeclarer === 'S' ? 'selected' : ''}`}
            onClick={() => handleDeclarerSelect('S')}
          >
            S
          </button>
          <button
            className={`input-btn ${selectedDeclarer === 'W' ? 'selected' : ''}`}
            onClick={() => handleDeclarerSelect('W')}
          >
            W
          </button>
        </div>
      </div>
      
      {/* Doubled Status */}
      <div className="input-section">
        <div className="input-label">Doubled</div>
        <div className="input-buttons">
          <button
            className={`input-btn ${selectedDoubled === '-' ? 'selected' : ''}`}
            onClick={() => handleDoubledSelect('-')}
          >
            -
          </button>
          <button
            className={`input-btn ${selectedDoubled === 'X' ? 'selected' : ''}`}
            onClick={() => handleDoubledSelect('X')}
          >
            X
          </button>
          <button
            className={`input-btn ${selectedDoubled === 'XX' ? 'selected' : ''}`}
            onClick={() => handleDoubledSelect('XX')}
          >
            XX
          </button>
        </div>
      </div>
    </div>
  );
};

export default ContractInput;
