import React, { useState, useEffect } from 'react';

// This component handles the final score calculation and display
const FinalScoreAnalysis = ({ analysisData, currentDeal, onSave, onEdit }) => {
  // Extract data from the props
  const {
    totalHCP,
    singletons,
    voids,
    longSuits,
    expectedTricks,
    adjustmentPercent,
    preliminaryScore
  } = analysisData;
  
  // Calculate various adjustments and bonuses
  const calculateFinalScores = () => {
    // Base values from the preliminary analysis
    const rawScore = currentDeal.rawScore || 420; // Placeholder
    const actualTricks = getActualTricks();
    const expectedTricksHCP = getExpectedTricksHCP();
    
    // Contract difficulty factor
    const contractLevel = getContractLevel();
    let difficultyFactor = 1.0;
    if (contractLevel >= 6) {
      // Slam bonus
      difficultyFactor = 1.2;
    } else if (contractLevel >= 5) {
      // Game bonus
      difficultyFactor = 1.1;
    }
    
    // Performance factor
    let performanceFactor = 1.0;
    if (actualTricks > expectedTricks) {
      // Over-performance bonus
      performanceFactor = 1.0 + ((actualTricks - expectedTricks) * 0.1);
    } else if (actualTricks < expectedTricks) {
      // Under-performance penalty
      performanceFactor = 1.0 - ((expectedTricks - actualTricks) * 0.05);
    }
    
    // Distribution adjustment
    const distributionPoints = singletons + (voids * 3) + longSuits;
    let distributionFactor = 1.0;
    if (distributionPoints > 4) {
      // High distribution penalty
      distributionFactor = 0.9;
    } else if (distributionPoints === 0) {
      // Balanced hand bonus
      distributionFactor = 1.1;
    }
    
    // HCP adjustment
    let hcpFactor = 1.0;
    if (totalHCP >= 26) {
      // Strong hand penalty
      hcpFactor = 0.9;
    } else if (totalHCP <= 18) {
      // Weak hand bonus
      hcpFactor = 1.2;
    }
    
    // Final score calculation
    const adjustmentMultiplier = (adjustmentPercent / 100);
    const finalScore = Math.round(
      rawScore * adjustmentMultiplier * difficultyFactor * 
      performanceFactor * distributionFactor * hcpFactor
    );
    
    // Return all the calculated factors for display
    return {
      rawScore,
      adjustmentMultiplier,
      difficultyFactor,
      performanceFactor,
      distributionFactor,
      hcpFactor,
      finalScore
    };
  };
  
  // Get contract level from the deal
  const getContractLevel = () => {
    if (!currentDeal || !currentDeal.contract) return 1;
    
    const match = currentDeal.contract.match(/(\d)([♣♦♥♠]|NT)\s+([NESW])(X{0,2})/);
    if (!match) return 1;
    
    return parseInt(match[1]);
  };
  
  // Get actual tricks taken
  const getActualTricks = () => {
    if (!currentDeal) return 0;
    
    const level = getContractLevel();
    const requiredTricks = level + 6;
    const result = currentDeal.result || 0;
    
    return requiredTricks + result;
  };
  
  // Get expected tricks based on HCP
  const getExpectedTricksHCP = () => {
    const hcpAbove20 = Math.max(0, totalHCP - 20);
    return Math.min(13, 6 + Math.floor(hcpAbove20 / 3));
  };
  
  // Get contract summary for display
  const getContractSummary = () => {
    if (!currentDeal || !currentDeal.contract) return "";
    
    const match = currentDeal.contract.match(/(\d)([♣♦♥♠]|NT)\s+([NESW])(X{0,2})/);
    if (!match) return "";
    
    const level = match[1];
    const suit = match[2];
    const declarer = match[3];
    const doubled = match[4];
    
    // Get full declarer name
    let declarerName = "";
    switch (declarer) {
      case 'N': declarerName = "North"; break;
      case 'E': declarerName = "East"; break;
      case 'S': declarerName = "South"; break;
      case 'W': declarerName = "West"; break;
      default: declarerName = declarer;
    }
    
    // Get result display
    const madeContract = currentDeal.result >= 0;
    const resultDisplay = madeContract ? 
      (currentDeal.result > 0 ? `Made +${currentDeal.result}` : "Made") :
      `Down ${Math.abs(currentDeal.result)}`;
    
    // Format doubled
    const doubledDisplay = doubled ? ` ${doubled}` : '';
    
    return `${level}${suit} by ${declarerName}${doubledDisplay} ${resultDisplay}`;
  };
  
  // Get which team is scoring
  const getScoringTeam = () => {
    if (!currentDeal || !currentDeal.contract) return "NS";
    
    const match = currentDeal.contract.match(/(\d)([♣♦♥♠]|NT)\s+([NESW])(X{0,2})/);
    if (!match) return "NS";
    
    const declarer = match[3];
    const isNS = declarer === 'N' || declarer === 'S';
    const madeContract = currentDeal.result >= 0;
    
    // If made contract, declarer's side scores
    // If defeated, defenders score
    if (madeContract) {
      return isNS ? "NS" : "EW";
    } else {
      return isNS ? "EW" : "NS";
    }
  };
  
  // Calculate all the scores and factors
  const scoreDetails = calculateFinalScores();
  
  // Inline styles for a consistent look
  const styles = {
    container: {
      border: '2px solid #2c3e50',
      borderRadius: '10px',
      overflow: 'hidden',
      margin: '10px',
      backgroundColor: '#f5f5f5',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
      height: 'calc(100% - 20px)',
      display: 'flex',
      flexDirection: 'column'
    },
    header: {
      backgroundColor: '#1e5c8b',
      color: 'white',
      padding: '20px',
      textAlign: 'center'
    },
    headerTitle: {
      fontSize: '24px',
      fontWeight: 'bold',
      margin: 0
    },
    subHeader: {
      backgroundColor: '#1e5c8b',
      color: 'white',
      padding: '15px',
      textAlign: 'center'
    },
    subHeaderTitle: {
      fontSize: '20px',
      fontWeight: 'bold',
      margin: 0
    },
    contractSection: {
      padding: '15px',
      backgroundColor: '#e8f4ff',
      textAlign: 'center',
      borderBottom: '1px solid #d0d0d0'
    },
    contractText: {
      fontSize: '18px',
      fontWeight: 'bold',
      margin: '0 0 5px'
    },
    rawScoreText: {
      fontSize: '16px',
      margin: 0
    },
    contentArea: {
      flex: 1,
      padding: '15px',
      overflow: 'auto'
    },
    section: {
      backgroundColor: 'white',
      border: '1px solid #d0d0d0',
      borderRadius: '8px',
      padding: '15px',
      marginBottom: '15px'
    },
    sectionTitle: {
      fontSize: '18px',
      fontWeight: 'bold',
      textAlign: 'center',
      margin: '0 0 15px',
      color: '#1e5c8b',
      borderBottom: '1px solid #d0d0d0',
      paddingBottom: '10px'
    },
    row: {
      display: 'flex',
      justifyContent: 'space-between',
      padding: '8px 0',
      borderBottom: '1px solid #eeeeee'
    },
    label: {
      fontWeight: '500'
    },
    value: {
      fontWeight: 'bold',
      color: '#1e5c8b'
    },
    highlightedRow: {
      display: 'flex',
      justifyContent: 'space-between',
      padding: '12px 0',
      margin: '10px 0',
      backgroundColor: '#e8f4ff',
      borderRadius: '5px',
      fontSize: '20px',
      fontWeight: 'bold'
    },
    highlightedLabel: {
      color: '#1e5c8b',
      paddingLeft: '10px'
    },
    highlightedValue: {
      color: '#1e5c8b',
      paddingRight: '10px'
    },
    footer: {
      display: 'flex',
      padding: '15px',
      gap: '15px',
      backgroundColor: '#1e5c8b'
    },
    editBtn: {
      flex: 1,
      height: '60px',
      backgroundColor: '#468bbf',
      color: 'white',
      border: 'none',
      borderRadius: '5px',
      fontSize: '18px',
      fontWeight: 'bold',
      cursor: 'pointer'
    },
    saveBtn: {
      flex: 1,
      height: '60px',
      backgroundColor: '#3c8c5c',
      color: 'white',
      border: 'none',
      borderRadius: '5px',
      fontSize: '18px',
      fontWeight: 'bold',
      cursor: 'pointer'
    }
  };
  
  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.headerTitle}>Final Score Analysis</h1>
      </div>
      
      <div style={styles.subHeader}>
        <h2 style={styles.subHeaderTitle}>Bonus Bridge Score Calculation</h2>
      </div>
      
      <div style={styles.contractSection}>
        <p style={styles.contractText}>Contract: {getContractSummary()}</p>
        <p style={styles.rawScoreText}>Raw score: {scoreDetails.rawScore} points</p>
      </div>
      
      <div style={styles.contentArea}>
        <div style={styles.section}>
          <h3 style={styles.sectionTitle}>Hand Analysis</h3>
          <div style={styles.row}>
            <div style={styles.label}>Combined HCP (Declarer + Dummy):</div>
            <div style={styles.value}>{totalHCP}</div>
          </div>
          <div style={styles.row}>
            <div style={styles.label}>Number of Singletons:</div>
            <div style={styles.value}>{singletons}</div>
          </div>
          <div style={styles.row}>
            <div style={styles.label}>Number of Voids:</div>
            <div style={styles.value}>{voids}</div>
          </div>
          <div style={styles.row}>
            <div style={styles.label}>Number of Long Suits (6+ cards):</div>
            <div style={styles.value}>{longSuits}</div>
          </div>
          <div style={styles.row}>
            <div style={styles.label}>Expected tricks based on HCP:</div>
            <div style={styles.value}>{getExpectedTricksHCP()}</div>
          </div>
          <div style={{...styles.row, borderBottom: 'none'}}>
            <div style={styles.label}>Expected tricks with distribution:</div>
            <div style={styles.value}>{expectedTricks}</div>
          </div>
        </div>
        
        <div style={styles.section}>
          <h3 style={styles.sectionTitle}>Score Adjustments</h3>
          <div style={styles.row}>
            <div style={styles.label}>Base Score Adjustment:</div>
            <div style={styles.value}>{adjustmentPercent}%</div>
          </div>
          <div style={styles.row}>
            <div style={styles.label}>Contract Difficulty Factor:</div>
            <div style={styles.value}>{(scoreDetails.difficultyFactor * 100).toFixed(0)}%</div>
          </div>
          <div style={styles.row}>
            <div style={styles.label}>Performance Factor:</div>
            <div style={styles.value}>{(scoreDetails.performanceFactor * 100).toFixed(0)}%</div>
          </div>
          <div style={styles.row}>
            <div style={styles.label}>Distribution Factor:</div>
            <div style={styles.value}>{(scoreDetails.distributionFactor * 100).toFixed(0)}%</div>
          </div>
          <div style={{...styles.row, borderBottom: 'none'}}>
            <div style={styles.label}>HCP Factor:</div>
            <div style={styles.value}>{(scoreDetails.hcpFactor * 100).toFixed(0)}%</div>
          </div>
        </div>
        
        <div style={styles.section}>
          <h3 style={styles.sectionTitle}>Final Score</h3>
          <div style={styles.row}>
            <div style={styles.label}>Raw Score:</div>
            <div style={styles.value}>{scoreDetails.rawScore} points</div>
          </div>
          <div style={styles.row}>
            <div style={styles.label}>Total Adjustment:</div>
            <div style={styles.value}>
              {(
                scoreDetails.adjustmentMultiplier * 
                scoreDetails.difficultyFactor * 
                scoreDetails.performanceFactor * 
                scoreDetails.distributionFactor * 
                scoreDetails.hcpFactor * 100
              ).toFixed(0)}%
            </div>
          </div>
          <div style={styles.highlightedRow}>
            <div style={styles.highlightedLabel}>Final Score for {getScoringTeam()}:</div>
            <div style={styles.highlightedValue}>{scoreDetails.finalScore} points</div>
          </div>
        </div>
      </div>
      
      <div style={styles.footer}>
        <button style={styles.editBtn} onClick={onEdit}>
          Edit Adjustments
        </button>
        <button style={styles.saveBtn} onClick={() => onSave(scoreDetails.finalScore)}>
          Continue to Next Deal
        </button>
      </div>
    </div>
  );
};

export default FinalScoreAnalysis;
