import React, { useState, useEffect } from 'react';
import './FinalScoreAnalysis.css';
import { determineVulnerability } from '../utils/scoring';

/**
 * FinalScoreAnalysis Component
 * Displays the final score analysis after bonus bridge calculations
 * @param {Object} props - Component props
 * @param {Object} props.analysisData - Analysis data from ScoreAdjustment
 * @param {Object} props.currentDeal - Current deal information
 * @param {Function} props.onSave - Function to save final analysis
 * @param {Function} props.onEdit - Function to return to ScoreAdjustment
 * @param {Function} props.setGameState - Function to update game state
 * @param {Object} props.scores - Current game scores
 */
const FinalScoreAnalysis = ({ 
  analysisData, 
  currentDeal, 
  onSave, 
  onEdit, 
  setGameState, 
  scores 
}) => {
  const [showDetails, setShowDetails] = useState(true);
  
  // Ensure we have analysis data
  useEffect(() => {
    if (!analysisData) {
      console.error('No analysis data provided to FinalScoreAnalysis');
    } else {
      console.log('Analysis data received:', analysisData);
      console.log('Current deal:', currentDeal);
    }
  }, [analysisData, currentDeal]);
  
  // Toggle calculation details visibility
  const toggleDetails = () => {
    setShowDetails(prev => !prev);
  };
  
  // Format a number to display with correct sign
  const formatNumber = (num) => {
    if (num === undefined || num === null) return '0';
    
    const formattedNum = parseFloat(num).toFixed(2);
    if (formattedNum > 0) {
      return `+${formattedNum}`;
    }
    return formattedNum;
  };
  
  // Handle continue to next deal button - MAIN FIX IS HERE
  const handleContinueToNextDeal = () => {
    // First, ensure we have the complete deal data with bonus scoring
    const currentDealWithBonusScores = {
      ...currentDeal,
      dealNumber: currentDeal.dealNumber,
      contract: currentDeal.contract,
      result: currentDeal.result,
      tricks: currentDeal.tricks,
      vulnerable: currentDeal.vulnerable,
      nsPoints: analysisData.nsPoints || 0, // Bonus scoring points
      ewPoints: analysisData.ewPoints || 0, // Bonus scoring points
      rawScore: currentDeal.rawScore || 0,  // Original raw score
      rawNsPoints: currentDeal.nsPoints || 0, // Original NS points
      rawEwPoints: currentDeal.ewPoints || 0, // Original EW points
      bonusScoringEnabled: true,
      handAnalysis: analysisData, // Store all analysis data for historical reference
      calculationSteps: analysisData.calculationSteps || [] // Store calculation steps
    };
    
    // Log the deal being saved
    console.log('Saving deal with bonus scoring:', currentDealWithBonusScores);
    
    // Calculate running totals using the bonus scoring points
    const nsScores = currentDealWithBonusScores.nsPoints > 0;
    const ewScores = currentDealWithBonusScores.ewPoints > 0;
    
    // Get the absolute max value for NS or EW points as the raw score
    const bonusRawScore = Math.max(
      Math.abs(currentDealWithBonusScores.nsPoints || 0),
      Math.abs(currentDealWithBonusScores.ewPoints || 0)
    );
    
    // Calculate new running totals
    const nsRawTotal = nsScores ? 
      (scores.nsTotal || 0) + bonusRawScore : 
      (scores.nsTotal || 0);
    
    const ewRawTotal = ewScores ? 
      (scores.ewTotal || 0) + bonusRawScore : 
      (scores.ewTotal || 0);
    
    console.log('Calculating new running totals:', {
      nsScores,
      ewScores,
      bonusRawScore,
      previousNsTotal: scores.nsTotal || 0,
      previousEwTotal: scores.ewTotal || 0,
      newNsTotal: nsRawTotal,
      newEwTotal: ewRawTotal
    });
    
    // Create updated scores object
    const updatedScores = {
      nsPoints: currentDealWithBonusScores.nsPoints,
      ewPoints: currentDealWithBonusScores.ewPoints,
      nsTotal: nsRawTotal,
      ewTotal: ewRawTotal
    };
    
    // Pass the final analysis to the parent component
    onSave({
      currentDealWithBonusScores,
      updatedScores,
      nsPoints: currentDealWithBonusScores.nsPoints,
      ewPoints: currentDealWithBonusScores.ewPoints
    });
  };
  
  // If no analysis data, show loading
  if (!analysisData) {
    return (
      <div className="final-score-analysis">
        <div className="loading">Loading analysis data...</div>
      </div>
    );
  }
  
  // Get calculation steps from analysis data
  const {
    rawScore,
    hcpAdjustment,
    afterHcpAdjustment,
    performanceVariance,
    afterPerformanceAssessment,
    contractTypeAdjustment,
    contractTypeDescription,
    afterContractAdjustments,
    distributionAdjustment,
    defenderReward,
    nsPoints,
    ewPoints
  } = analysisData;
  
  return (
    <div className="final-score-analysis">
      <header className="analysis-header">
        <h1>Final Score Analysis</h1>
        <h2>Bonus Bridge Score Calculation</h2>
      </header>
      
      <div className="contract-summary">
        <h3>
          Contract: {currentDeal.contract} {currentDeal.result >= 0 ? 
            `Made ${currentDeal.result > 0 ? '+' + currentDeal.result : 'exactly'}` : 
            `Down ${Math.abs(currentDeal.result)}`}
        </h3>
        <p>Raw score: {currentDeal.rawScore} points</p>
      </div>
      
      <div className="analysis-section">
        <h3>Hand Analysis</h3>
        <div className="analysis-details">
          <div className="detail-row">
            <span className="detail-label">Combined HCP (Declarer + Dummy):</span>
            <span className="detail-value">{analysisData.totalHCP}</span>
          </div>
          <div className="detail-row">
            <span className="detail-label">Declarer HCP Percentage:</span>
            <span className="detail-value">{analysisData.declarerHCPPercentage}%</span>
          </div>
          <div className="detail-row">
            <span className="detail-label">Defender HCP Percentage:</span>
            <span className="detail-value">{analysisData.defenderHCPPercentage}%</span>
          </div>
          <div className="detail-row">
            <span className="detail-label">HCP Advantage:</span>
            <span className="detail-value">{analysisData.hcpAdvantage}% to {analysisData.advantageSide}</span>
          </div>
          <div className="detail-row">
            <span className="detail-label">Number of Singletons:</span>
            <span className="detail-value">{analysisData.singletons}</span>
          </div>
          <div className="detail-row">
            <span className="detail-label">Number of Voids:</span>
            <span className="detail-value">{analysisData.voids}</span>
          </div>
          <div className="detail-row">
            <span className="detail-label">Number of Long Suits (6+ cards):</span>
            <span className="detail-value">{analysisData.longSuits}</span>
          </div>
          <div className="detail-row">
            <span className="detail-label">Distribution Points:</span>
            <span className="detail-value">{analysisData.distributionPoints}</span>
          </div>
          <div className="detail-row">
            <span className="detail-label">Expected HCP for Contract:</span>
            <span className="detail-value">{analysisData.expectedHCP}</span>
          </div>
          <div className="detail-row">
            <span className="detail-label">Contract Expected Tricks:</span>
            <span className="detail-value">{analysisData.contractExpectedTricks}</span>
          </div>
          <div className="detail-row">
            <span className="detail-label">Hand Expected Tricks:</span>
            <span className="detail-value">{analysisData.handExpectedTricks}</span>
          </div>
        </div>
      </div>
      
      <div className="analysis-section">
        <h3>
          Calculation Steps ({currentDeal.result >= 0 ? 'Made Contract' : 'Defeated Contract'})
        </h3>
        {showDetails && (
          <div className="calculation-steps">
            <div className="step-row">
              <span className="step-label">1. Raw Score / 20:</span>
              <span className="step-value">{rawScore} points</span>
            </div>
            <div className="step-row">
              <span className="step-label">2. HCP Adjustment:</span>
              <span className="step-value">{formatNumber(hcpAdjustment)} points</span>
            </div>
            <div className="step-row">
              <span className="step-label">3. After HCP Adjustment:</span>
              <span className="step-value">{afterHcpAdjustment} points</span>
            </div>
            <div className="step-row">
              <span className="step-label">4. Performance Variance:</span>
              <span className="step-value">{performanceVariance} tricks</span>
            </div>
            <div className="step-row">
              <span className="step-label">5. After Performance Assessment:</span>
              <span className="step-value">{afterPerformanceAssessment} points</span>
            </div>
            <div className="step-row">
              <span className="step-label">6. Contract Type Adjustment:</span>
              <span className="step-value">{formatNumber(contractTypeAdjustment)} {contractTypeDescription || '(Game)'}</span>
            </div>
            <div className="step-row">
              <span className="step-label">7. After Contract Adjustments:</span>
              <span className="step-value">{afterContractAdjustments} points</span>
            </div>
            <div className="step-row">
              <span className="step-label">8. Distribution Adjustment:</span>
              <span className="step-value">{distributionAdjustment} points</span>
            </div>
            <div className="step-row">
              <span className="step-label">9. Defender Reward:</span>
              <span className="step-value">{defenderReward} points</span>
            </div>
          </div>
        )}
      </div>
      
      <div className="final-scores-container">
        <h3>Final Scores</h3>
        <div className="final-scores">
          <div className="team-score ns-score">
            <span className="team-label">NS Score:</span>
            <span className="team-value">{nsPoints} points</span>
          </div>
          <div className="team-score ew-score">
            <span className="team-label">EW Score:</span>
            <span className="team-value">{ewPoints} points</span>
          </div>
        </div>
      </div>
      
      <div className="action-buttons">
        <button 
          className="edit-btn"
          onClick={onEdit}
        >
          Edit Adjustments
        </button>
        
        <button 
          className="continue-btn"
          onClick={handleContinueToNextDeal}
        >
          Continue to Next Deal
        </button>
      </div>
    </div>
  );
};

export default FinalScoreAnalysis;
