// File: src/components/ContractInput.js
import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';


const ContractInput = () => {
  const { gameState, dispatch } = useContext(GameContext);
  const { contract, doubled, redoubled, vulnerable, declarer } = gameState.currentDeal;

  // Handle contract level and suit selection
  const handleContractSelect = (selectedContract) => {
    dispatch({
      type: 'SET_CONTRACT',
      payload: selectedContract
    });
  };

  // Handle double/redouble toggles
  const handleDoubledToggle = () => {
    dispatch({
      type: 'SET_DOUBLED',
      payload: !doubled
    });
  };

  const handleRedoubledToggle = () => {
    dispatch({
      type: 'SET_REDOUBLED',
      payload: !redoubled
    });
  };

  // Handle vulnerability toggle
  const handleVulnerableToggle = () => {
    dispatch({
      type: 'SET_VULNERABLE',
      payload: !vulnerable
    });
  };

  // Handle declarer selection
  const handleDeclarerSelect = (selectedDeclarer) => {
    dispatch({
      type: 'SET_DECLARER',
      payload: selectedDeclarer
    });
  };

  // Generate contract level buttons (1-7)
  const renderLevelButtons = () => {
    const levels = [1, 2, 3, 4, 5, 6, 7];
    
    return levels.map(level => {
      const contractLevel = level.toString();
      
      return (
        <button
          key={contractLevel}
          className={`level-button ${contract && contract.startsWith(contractLevel) ? 'selected' : ''}`}
          onClick={() => {
            if (contract && contract.substring(1) && contract.startsWith(contractLevel)) {
              // If clicking the same level, do nothing
              return;
            }
            
            // If a suit is already selected, use it with the new level
            if (contract && contract.substring(1)) {
              handleContractSelect(contractLevel + contract.substring(1));
            }
          }}
        >
          {contractLevel}
        </button>
      );
    });
  };

  // Generate contract suit buttons (C, D, H, S, NT)
  const renderSuitButtons = () => {
    const suits = [
      { id: 'C', symbol: '♣', class: 'suit-clubs' },
      { id: 'D', symbol: '♦', class: 'suit-diamonds' },
      { id: 'H', symbol: '♥', class: 'suit-hearts' },
      { id: 'S', symbol: '♠', class: 'suit-spades' },
      { id: 'NT', symbol: 'NT', class: '' }
    ];
    
    return suits.map(suit => {
      return (
        <button
          key={suit.id}
          className={`suit-button ${suit.class} ${contract && contract.endsWith(suit.id) ? 'selected' : ''}`}
          onClick={() => {
            if (contract && contract.endsWith(suit.id) && contract.substring(0, 1)) {
              // If clicking the same suit, do nothing
              return;
            }
            
            // If a level is already selected, use it with the new suit
            if (contract && contract.substring(0, 1)) {
              handleContractSelect(contract.substring(0, 1) + suit.id);
            }
          }}
        >
          {suit.symbol}
        </button>
      );
    });
  };

  // Generate full contract buttons (1C, 1D, ... 7NT)
  const renderContractButtons = () => {
    const levels = [1, 2, 3, 4, 5, 6, 7];
    const suits = ['C', 'D', 'H', 'S', 'NT'];
    const buttons = [];
    
    for (let level of levels) {
      for (let suit of suits) {
        const contractName = `${level}${suit}`;
        buttons.push(
          <button
            key={contractName}
            className={`contract-button ${contract === contractName ? 'selected' : ''}`}
            onClick={() => handleContractSelect(contractName)}
          >
            {level}
            <span className={`suit-icon suit-${suit.toLowerCase()}`}>
              {suit === 'C' ? '♣' : 
               suit === 'D' ? '♦' : 
               suit === 'H' ? '♥' : 
               suit === 'S' ? '♠' : 'NT'}
            </span>
          </button>
        );
      }
    }
    
    return buttons;
  };

  // Generate declarer buttons (N, E, S, W)
  const renderDeclarerButtons = () => {
    const declarers = ['N', 'E', 'S', 'W'];
    
    return declarers.map(dir => (
      <button
        key={dir}
        className={`declarer-button ${declarer === dir ? 'selected' : ''}`}
        onClick={() => handleDeclarerSelect(dir)}
        disabled={!contract}
      >
        {dir}
      </button>
    ));
  };

  return (
    <div className="component-container">
      <h2>Contract Input</h2>
      
      <div className="contract-grid">
        <div className="contract-section">
          <h3>Select Contract</h3>
          <div className="contract-buttons">
            {renderContractButtons()}
          </div>
        </div>
        
        <div className="contract-section">
          <h3>Contract Options</h3>
          
          <div className="options-group">
            <div className="checkbox-group">
              <input
                type="checkbox"
                id="doubled"
                checked={doubled}
                onChange={handleDoubledToggle}
                disabled={!contract || redoubled}
              />
              <label htmlFor="doubled">Doubled</label>
            </div>
            
            <div className="checkbox-group">
              <input
                type="checkbox"
                id="redoubled"
                checked={redoubled}
                onChange={handleRedoubledToggle}
                disabled={!contract || !doubled}
              />
              <label htmlFor="redoubled">Redoubled</label>
            </div>
            
            <div className="checkbox-group">
              <input
                type="checkbox"
                id="vulnerable"
                checked={vulnerable}
                onChange={handleVulnerableToggle}
                disabled={!contract}
              />
              <label htmlFor="vulnerable">Vulnerable</label>
            </div>
          </div>
          
          <div className="declarer-section">
            <h3>Declarer</h3>
            <div className="declarer-buttons">
              {renderDeclarerButtons()}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ContractInput;
