import React, { useState, useEffect } from 'react';

// Ultra-compact ContractInput component with extreme override styling
const ContractInput = ({ onContractChange, initialContract = '' }) => {
  // Define state variables
  const [level, setLevel] = useState('');
  const [suit, setSuit] = useState('');
  const [declarer, setDeclarer] = useState('');
  const [doubled, setDoubled] = useState(null); // Initialize as null to avoid any default selection
  
  // Master container style with important flags and minimal size
  const containerStyle = {
    padding: '5px',
    maxWidth: '100%',
    boxSizing: 'border-box',
    maxHeight: '270px !important', // Very strict height limit with important flag
    overflow: 'hidden !important',
    margin: '0 !important',
    background: 'white !important',
    borderRadius: '8px !important'
  };
  
  // Ultra-compact section style
  const sectionStyle = {
    marginBottom: '5px !important',
    padding: '0 !important'
  };
  
  // Minimalist label style
  const labelStyle = {
    fontWeight: 'bold',
    marginBottom: '2px !important',
    fontSize: '13px !important',
    color: '#1e5c8b',
    display: 'block'
  };
  
  // Extremely compact button row
  const buttonRowStyle = {
    display: 'flex',
    gap: '3px !important',
    marginBottom: '5px !important',
    width: '100%'
  };
  
  // Parse initialContract if provided
  useEffect(() => {
    if (initialContract) {
      const contractMatch = initialContract.match(/(\d)([♣♦♥♠]|NT)\s+([NESW])(X{0,2})/);
      if (contractMatch) {
        setLevel(contractMatch[1]);
        setSuit(contractMatch[2]);
        setDeclarer(contractMatch[3]);
        setDoubled(contractMatch[4] || null);
      }
    } else {
      // Initialize with no doubled (null)
      setDoubled(null);
    }
  }, [initialContract]);
  
  // Update contract when any component changes
  useEffect(() => {
    if (level && suit && declarer) {
      // Only include doubled if it's not null and not empty
      const doubledPart = doubled ? doubled : '';
      const newContract = `${level}${suit} ${declarer}${doubledPart}`;
      onContractChange(newContract);
    }
  }, [level, suit, declarer, doubled, onContractChange]);
  
  // Ultra compact button style
  const getButtonStyle = (isSelected) => {
    return {
      flex: '1',
      height: '35px !important', // Very small height with important flag
      minHeight: '35px !important',
      maxHeight: '35px !important',
      border: isSelected ? '2px solid #3498db !important' : '1px solid #ddd !important',
      backgroundColor: isSelected ? '#3498db !important' : '#f8f8f8 !important',
      borderRadius: '4px !important',
      fontSize: '15px !important', // Small font
      fontWeight: isSelected ? 'bold' : 'normal',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      color: isSelected ? 'white !important' : 'black !important',
      padding: '0 !important', // No padding
      margin: '0 !important', // No margin
      boxShadow: 'none !important' // No shadow
    };
  };
  
  // Suit button style with colors
  const getSuitButtonStyle = (s, isSelected) => {
    const baseStyle = getButtonStyle(isSelected);
    
    // Determine color based on suit
    let color = 'black !important';
    let fontSize = '20px !important'; // Small font size
    
    if (s === '♦' || s === '♥') {
      color = '#e74c3c !important'; // Red for diamonds and hearts
    } else if (s === 'NT') {
      fontSize = '16px !important'; // Smaller for NT
    }
    
    return {
      ...baseStyle,
      fontSize: fontSize,
      color: isSelected ? 'white !important' : color
    };
  };

  // Special handling for doubled buttons
  const handleDoubledClick = (value) => {
    // Only set value if it's different from current (no toggle behavior)
    if (doubled !== value) {
      setDoubled(value);
    }
  };
  
  return (
    <div style={containerStyle}>
      {/* Level Input */}
      <div style={sectionStyle}>
        <div style={labelStyle}>Level and Suit:</div>
        
        {/* Level Buttons (1-7) */}
        <div style={buttonRowStyle}>
          {[1, 2, 3, 4, 5, 6, 7].map((num) => (
            <button
              key={`level-${num}`}
              style={getButtonStyle(level === num.toString())}
              onClick={() => setLevel(num.toString())}
            >
              {num}
            </button>
          ))}
        </div>
        
        {/* Suit Buttons */}
        <div style={buttonRowStyle}>
          {['♣', '♦', '♥', '♠', 'NT'].map((s) => (
            <button
              key={`suit-${s}`}
              style={getSuitButtonStyle(s, suit === s)}
              onClick={() => setSuit(s)}
            >
              {s}
            </button>
          ))}
        </div>
      </div>
      
      {/* Declarer Input */}
      <div style={sectionStyle}>
        <div style={labelStyle}>Declarer:</div>
        <div style={buttonRowStyle}>
          {['N', 'S', 'E', 'W'].map((d) => (
            <button
              key={`declarer-${d}`}
              style={getButtonStyle(declarer === d)}
              onClick={() => setDeclarer(d)}
            >
              {d}
            </button>
          ))}
        </div>
      </div>
      
      {/* Doubled Input - with special handling */}
      <div style={sectionStyle}>
        <div style={labelStyle}>Doubled:</div>
        <div style={buttonRowStyle}>
          <button
            style={getButtonStyle(doubled === null)}
            onClick={() => handleDoubledClick(null)}
          >
            -
          </button>
          <button
            style={getButtonStyle(doubled === 'X')}
            onClick={() => handleDoubledClick('X')}
          >
            X
          </button>
          <button
            style={getButtonStyle(doubled === 'XX')}
            onClick={() => handleDoubledClick('XX')}
          >
            XX
          </button>
        </div>
      </div>
    </div>
  );
};

export default ContractInput;
