import React, { useState } from 'react';
import BonusBridgeExplanation from './BonusBridgeExplanation';
import './GameScoreSheet.css';

const GameScoreSheet = ({ gameState, onNewGame }) => {
  const [selectedDeal, setSelectedDeal] = useState(null);
  const [showDetailPopup, setShowDetailPopup] = useState(false);
  const [showExplanation, setShowExplanation] = useState(false);
  
  // Calculate totals for display
  const partyBridgeNS = gameState.scores.nsTotal || 0;
  const partyBridgeEW = gameState.scores.ewTotal || 0;
  const bonusBridgeNS = gameState.scores.bonusNsTotal || 0;
  const bonusBridgeEW = gameState.scores.bonusEwTotal || 0;
  
  // Determine winners
  const partyBridgeWinner = partyBridgeNS > partyBridgeEW ? 'North-South' : 'East-West';
  const bonusBridgeWinner = bonusBridgeNS > bonusBridgeEW ? 'North-South' : 'East-West';
  
  // Handle opening the detail popup
  const openDetailPopup = (deal) => {
    setSelectedDeal(deal);
    setShowDetailPopup(true);
  };
  
  // Handle closing the detail popup
  const closeDetailPopup = () => {
    setShowDetailPopup(false);
    setSelectedDeal(null);
  };
  
  // Handle opening the explanation popup
  const handleOpenExplanation = () => {
    setShowExplanation(true);
  };
  
  // Handle closing the explanation popup
  const handleCloseExplanation = () => {
    setShowExplanation(false);
  };
  
  // Format vulnerability as a string for display
  const formatVulnerability = (vulnerable) => {
    if (!vulnerable) return "None";
    
    if (typeof vulnerable === 'object') {
      const nsVul = vulnerable.ns ? "NS" : "";
      const ewVul = vulnerable.ew ? "EW" : "";
      
      if (nsVul && ewVul) return "Both";
      if (nsVul) return "NS";
      if (ewVul) return "EW";
      return "None";
    }
    
    return String(vulnerable);
  };

  return (
    <div className="game-score-sheet">
      <h2>Game Score Summary</h2>
      
      <div className="score-summaries">
        <div className="score-summary party-bridge">
          <h3>Party Bridge (Standard Scoring)</h3>
          <div className="final-scores">
            <div className="team-score">
              <span className="team-label">North-South:</span>
              <span className="team-value">{partyBridgeNS}</span>
            </div>
            <div className="team-score">
              <span className="team-label">East-West:</span>
              <span className="team-value">{partyBridgeEW}</span>
            </div>
            <div className="winner">
              <span className="winner-label">Winner:</span>
              <span className="winner-value">{partyBridgeWinner}</span>
            </div>
          </div>
        </div>
        
        <div className="score-summary bonus-bridge">
          <h3>Bonus Bridge Scoring</h3>
          <div className="final-scores">
            <div className="team-score">
              <span className="team-label">North-South:</span>
              <span className="team-value">{bonusBridgeNS}</span>
            </div>
            <div className="team-score">
              <span className="team-label">East-West:</span>
              <span className="team-value">{bonusBridgeEW}</span>
            </div>
            <div className="winner">
              <span className="winner-label">Winner:</span>
              <span className="winner-value">{bonusBridgeWinner}</span>
            </div>
          </div>
          <button className="explanation-button" onClick={handleOpenExplanation}>
            About Bonus Bridge Scoring
          </button>
        </div>
      </div>
      
      <div className="deals-table-container">
        <h3>Deals Summary</h3>
        <table className="deals-table">
          <thead>
            <tr>
              <th>Deal</th>
              <th>Contract</th>
              <th>Result</th>
              <th>Party Bridge</th>
              <th>Bonus Bridge</th>
              <th>Details</th>
            </tr>
          </thead>
          <tbody>
            {gameState.deals.map((deal) => (
              <tr key={deal.dealNumber}>
                <td>{deal.dealNumber}</td>
                <td>{deal.contract || 'Passed'}</td>
                <td>{deal.result !== null ? (deal.result >= 0 ? `+${deal.result}` : deal.result) : '-'}</td>
                <td>
                  {deal.nsPoints > 0 ? `NS: +${deal.nsPoints}` : ''}
                  {deal.ewPoints > 0 ? `EW: +${deal.ewPoints}` : ''}
                </td>
                <td>
                  {deal.bonusNsPoints > 0 ? `NS: +${deal.bonusNsPoints}` : ''}
                  {deal.bonusEwPoints > 0 ? `EW: +${deal.bonusEwPoints}` : ''}
                </td>
                <td>
                  <button 
                    className="detail-button"
                    onClick={() => openDetailPopup(deal)}>
                    View
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      
      <div className="sheet-actions">
        <button className="new-game-button" onClick={onNewGame}>
          Start New Game
        </button>
      </div>
      
      {/* Detail Popup */}
      {showDetailPopup && selectedDeal && (
        <div className="popup-overlay">
          <div className="detail-popup">
            <h3>Deal {selectedDeal.dealNumber} Details</h3>
            
            <div className="deal-info">
              <div className="deal-basic-info">
                <div className="info-row">
                  <div className="info-label">Contract:</div>
                  <div className="info-value">{selectedDeal.contract || 'Passed'}</div>
                </div>
                <div className="info-row">
                  <div className="info-label">Result:</div>
                  <div className="info-value">
                    {selectedDeal.result !== null 
                      ? (selectedDeal.result >= 0 ? `+${selectedDeal.result}` : selectedDeal.result) 
                      : '-'}
                  </div>
                </div>
                <div className="info-row">
                  <div className="info-label">Vulnerable:</div>
                  <div className="info-value">{formatVulnerability(selectedDeal.vulnerable)}</div>
                </div>
              </div>
              
              <div className="deal-scores">
                <div className="score-section">
                  <h4>Party Bridge Score</h4>
                  <div className="info-row">
                    <div className="info-label">NS Points:</div>
                    <div className="info-value">{selectedDeal.nsPoints}</div>
                  </div>
                  <div className="info-row">
                    <div className="info-label">EW Points:</div>
                    <div className="info-value">{selectedDeal.ewPoints}</div>
                  </div>
                </div>
                
                <div className="score-section">
                  <h4>Bonus Bridge Score</h4>
                  <div className="info-row">
                    <div className="info-label">NS Points:</div>
                    <div className="info-value">{selectedDeal.bonusNsPoints}</div>
                  </div>
                  <div className="info-row">
                    <div className="info-label">EW Points:</div>
                    <div className="info-value">{selectedDeal.bonusEwPoints}</div>
                  </div>
                </div>
              </div>
            </div>
            
            {selectedDeal.handAnalysis && (
              <div className="analysis-details">
                <h4>Hand Analysis</h4>
                <div className="hand-info">
                  <div className="info-row">
                    <div className="info-label">HCP:</div>
                    <div className="info-value">{selectedDeal.handAnalysis.totalHCP || '0'}</div>
                  </div>
                  <div className="info-row">
                    <div className="info-label">Singletons:</div>
                    <div className="info-value">{selectedDeal.handAnalysis.singletons || '0'}</div>
                  </div>
                  <div className="info-row">
                    <div className="info-label">Voids:</div>
                    <div className="info-value">{selectedDeal.handAnalysis.voids || '0'}</div>
                  </div>
                  <div className="info-row">
                    <div className="info-label">Long Suits:</div>
                    <div className="info-value">{selectedDeal.handAnalysis.longSuits || '0'}</div>
                  </div>
                </div>
                
                <div className="bonus-calculation">
                  <h4>Bonus Bridge Calculation Steps</h4>
                  <ol className="calculation-steps">
                    {selectedDeal.handAnalysis.rawScore && (
                      <li>Raw Score / 20: {(selectedDeal.handAnalysis.rawScore).toFixed(1)} points</li>
                    )}
                    {selectedDeal.handAnalysis.hcpAdjustment && (
                      <li>HCP Adjustment: {selectedDeal.handAnalysis.hcpAdjustment.toFixed(2)} points</li>
                    )}
                    {selectedDeal.handAnalysis.afterHcpAdjustment && (
                      <li>After HCP Adjustment: {selectedDeal.handAnalysis.afterHcpAdjustment.toFixed(1)} points</li>
                    )}
                    {selectedDeal.handAnalysis.performanceVariance !== undefined && (
                      <li>Performance Variance: {selectedDeal.handAnalysis.performanceVariance} tricks</li>
                    )}
                    {selectedDeal.handAnalysis.afterPerformanceAssessment && (
                      <li>After Performance Assessment: {selectedDeal.handAnalysis.afterPerformanceAssessment.toFixed(1)} points</li>
                    )}
                    {selectedDeal.handAnalysis.contractTypeAdjustment !== undefined && (
                      <li>Contract Type Adjustment: {selectedDeal.handAnalysis.contractTypeAdjustment.toFixed(1)} points</li>
                    )}
                    {selectedDeal.handAnalysis.afterContractAdjustments && (
                      <li>After Contract Adjustments: {selectedDeal.handAnalysis.afterContractAdjustments.toFixed(1)} points</li>
                    )}
                    {selectedDeal.handAnalysis.distributionAdjustment !== undefined && (
                      <li>Distribution Adjustment: {selectedDeal.handAnalysis.distributionAdjustment.toFixed(1)} points</li>
                    )}
                    {selectedDeal.handAnalysis.defenderReward !== undefined && (
                      <li>Defender Reward: {selectedDeal.handAnalysis.defenderReward.toFixed(1)} points</li>
                    )}
                  </ol>
                </div>
              </div>
            )}
            
            <div className="popup-actions">
              <button onClick={closeDetailPopup}>Close</button>
            </div>
          </div>
        </div>
      )}
      
      {/* Explanation Popup */}
      {showExplanation && (
        <BonusBridgeExplanation onClose={handleCloseExplanation} />
      )}
    </div>
  );
};

export default GameScoreSheet;
