import React, { useState, useEffect } from 'react';

// This component handles the final score calculation and display based on the Bonus Bridge Enhanced Scoring System
const FinalScoreAnalysis = ({ analysisData, currentDeal, onSave, onEdit }) => {
  // Extract data from the props
  const {
    totalHCP,
    singletons,
    voids,
    longSuits,
    expectedTricks,
    adjustmentPercent,
    preliminaryScore
  } = analysisData;
  
  // Calculate expected tricks based on HCP
  const calculateExpectedTricksHCP = () => {
    // Simple formula: 6 + (totalHCP - 20) / 3
    const hcpAbove20 = Math.max(0, totalHCP - 20);
    return Math.min(13, 6 + Math.floor(hcpAbove20 / 3));
  };

  // Parse contract details from the current deal
  const parseContractDetails = () => {
    if (!currentDeal || !currentDeal.contract) return null;
    
    // Parse contract string (e.g., "4♥ S")
    const contractMatch = currentDeal.contract.match(/(\d)([♣♦♥♠]|NT)\s+([NESW])(X{0,2})/);
    if (!contractMatch) return null;
    
    const level = parseInt(contractMatch[1]);
    const suit = contractMatch[2];
    const declarer = contractMatch[3];
    const doubled = contractMatch[4];
    
    // Determine if declarer is NS or EW
    const isNS = declarer === 'N' || declarer === 'S';
    
    // Get vulnerability
    const vulnerable = isNS ? 
      (currentDeal.vulnerable?.ns || false) : 
      (currentDeal.vulnerable?.ew || false);
    
    // Calculate actual tricks
    const requiredTricks = level + 6;
    const result = currentDeal.result || 0;
    const actualTricks = requiredTricks + result;
    const madeContract = result >= 0;
    
    // Get defensive HCP
    const defenderHCP = 40 - totalHCP;
    
    // Calculate distribution points
    const distributionPoints = (voids * 3) + (singletons * 2) + longSuits;
    
    return {
      level,
      suit,
      declarer,
      doubled,
      isNS,
      vulnerable,
      requiredTricks,
      actualTricks,
      madeContract,
      result,
      defenderHCP,
      distributionPoints,
      rawScore: currentDeal.rawScore || 420 // Default to 420 for 4♥ non-vulnerable
    };
  };
  
  // Calculate the final scores according to the Bonus Bridge Enhanced Scoring System
  const calculateBonusBridgeScores = () => {
    const details = parseContractDetails();
    if (!details) return { nsPoints: 0, ewPoints: 0 };
    
    let declarerPoints = 0;
    let defenderPoints = 0;
    
    if (details.madeContract) {
      // ***** SCORING FOR MADE CONTRACTS *****
      
      // STEP 1: Calculate Raw Score and Reduce Scale
      const rawScore = details.rawScore;
      const initialPoints = rawScore / 20;
      
      // STEP 2: Calculate HCP Advantage
      const declarerHCPPercentage = (totalHCP / 40) * 100;
      const defenderHCPPercentage = 100 - declarerHCPPercentage;
      const hcpAdvantage = Math.abs(declarerHCPPercentage - defenderHCPPercentage);
      const advantageSide = declarerHCPPercentage > 50 ? "declarer" : "defender";
      
      // STEP 3: Apply HCP Adjustment Based on Contract Type
      let expectedHCP;
      // Determine Expected HCP based on contract type
      if (details.level <= 2) {
        // Part Scores (1-2 level)
        expectedHCP = 21;
      } else if (details.level === 3 && details.suit === 'NT') {
        // 3NT Game
        expectedHCP = 25;
      } else if (details.level === 4 && (details.suit === '♥' || details.suit === '♠')) {
        // 4♥/♠ Game
        expectedHCP = 24;
      } else if (details.level === 5 && (details.suit === '♣' || details.suit === '♦')) {
        // 5♣/♦ Game
        expectedHCP = 27;
      } else if (details.level === 6) {
        // Small Slams (6 level)
        expectedHCP = 30;
      } else if (details.level === 7) {
        // Grand Slams (7 level)
        expectedHCP = 32;
      } else {
        // Other levels
        expectedHCP = 21 + (details.level * 1.5);
      }
      
      // Calculate adjustment
      const hcpAdjustment = (totalHCP - expectedHCP) * 0.75;
      let adjustedPoints = initialPoints;
      
      if (totalHCP > expectedHCP) {
        // If Declarer HCP > Expected HCP: Subtract adjustment
        adjustedPoints -= hcpAdjustment;
      } else if (totalHCP < expectedHCP) {
        // If Declarer HCP < Expected HCP: Add adjustment
        adjustedPoints += Math.abs(hcpAdjustment);
      }
      // No adjustment if Declarer HCP = Expected HCP
      
      // STEP 4: Calculate Expected Tricks
      const contractExpectedTricks = 6 + details.level;
      const handExpectedTricks = Math.min(13, 6 + Math.floor(totalHCP / 3) + Math.floor(details.distributionPoints / 4));
      
      // STEP 5: Performance Assessment
      let performancePoints = adjustedPoints;
      
      // For Contract Performance
      const performanceVariance = details.actualTricks - contractExpectedTricks;
      if (performanceVariance > 0) {
        // If Variance > 0 (overtricks): Add (Variance × 1.5) points
        performancePoints += (performanceVariance * 1.5);
      }
      
      // For Hand Potential Performance (only if Hand Expected > Contract Expected)
      if (handExpectedTricks > contractExpectedTricks) {
        const potentialVariance = details.actualTricks - handExpectedTricks;
        if (potentialVariance < 0) {
          // If Potential Variance < 0 (underperformance): Subtract (|Potential Variance| × 0.75) points
          performancePoints -= (Math.abs(potentialVariance) * 0.75);
        }
      }
      
      // STEP 6: Apply Contract Type Adjustments
      let contractPoints = performancePoints;
      
      // Check if it's a Game contract
      const isGameContract = (
        (details.level === 3 && details.suit === 'NT') ||
        (details.level === 4 && (details.suit === '♥' || details.suit === '♠')) ||
        (details.level === 5 && (details.suit === '♣' || details.suit === '♦')) ||
        details.level >= 6
      );
      
      if (isGameContract) {
        // For Game contracts
        contractPoints += 2;
      }
      
      if (details.level === 6) {
        // For Small Slam contracts
        contractPoints += 4;
      } else if (details.level === 7) {
        // For Grand Slam contracts
        contractPoints += 6;
      }
      
      if (details.suit === 'NT') {
        // For NT contracts
        contractPoints += 1;
      }
      
      const overtricks = details.actualTricks - contractExpectedTricks;
      
      if (overtricks >= 4) {
        // For contracts with 4+ overtricks
        contractPoints += 1;
        
        if (overtricks >= 7) {
          // For contracts with 7+ overtricks
          contractPoints += 2;
        }
      }
      
      // STEP 7: Distribution Adjustment (Suit Contracts Only)
      let distributionPoints = contractPoints;
      
      if (details.suit !== 'NT') {
        // Only apply for suit contracts
        if (details.distributionPoints >= 7) {
          distributionPoints -= 3;
        } else if (details.distributionPoints >= 5) {
          distributionPoints -= 2;
        } else if (details.distributionPoints >= 3) {
          distributionPoints -= 1;
        }
        // 1-2 dist points: No deduction
      }
      
      // STEP 8: Defender Reward Calculation
      if (handExpectedTricks > contractExpectedTricks && 
          details.actualTricks < handExpectedTricks) {
        // Defender Reward = (Hand Expected Tricks - Actual Tricks) × 2
        const defenderReward = (handExpectedTricks - details.actualTricks) * 2;
        
        // Extra reward if defending at disadvantage
        const extraReward = Math.min(3, hcpAdvantage / 10);
        
        defenderPoints = defenderReward + (advantageSide === "declarer" ? extraReward : 0);
      }
      
      // STEP 9: Finalize Scores
      declarerPoints = Math.max(1, Math.round(distributionPoints)); // Minimum declarer score for made contracts: 1 point
      defenderPoints = Math.round(defenderPoints);
      
      // Return calculation details for display
      return {
        declarerPoints,
        defenderPoints,
        
        // Include calculation steps for display
        rawScore,
        initialPoints,
        declarerHCPPercentage,
        defenderHCPPercentage,
        hcpAdvantage,
        advantageSide,
        expectedHCP,
        hcpAdjustment,
        adjustedPoints,
        contractExpectedTricks,
        handExpectedTricks,
        performanceVariance,
        potentialVariance: handExpectedTricks > contractExpectedTricks ? details.actualTricks - handExpectedTricks : 0,
        performancePoints,
        isGameContract,
        contractPoints,
        distributionAdjustment: contractPoints - distributionPoints,
        distributionPoints: details.distributionPoints,
        defenderReward: defenderPoints,
        nsPoints: details.isNS ? declarerPoints : defenderPoints,
        ewPoints: details.isNS ? defenderPoints : declarerPoints
      };
      
    } else {
      // ***** SCORING FOR DEFEATED CONTRACTS *****
      
      // STEP 1: Calculate Base Penalty
      const rawScore = Math.abs(details.rawScore);
      const basePenalty = rawScore / 10;
      
      // STEP 2: Add Contract Level Penalties
      let levelPenalties = 0;
      
      // Check if it's a Game contract
      const isGameContract = (
        (details.level === 3 && details.suit === 'NT') ||
        (details.level === 4 && (details.suit === '♥' || details.suit === '♠')) ||
        (details.level === 5 && (details.suit === '♣' || details.suit === '♦')) ||
        details.level >= 6
      );
      
      if (isGameContract) {
        // For defeated Game contracts
        levelPenalties += 3;
      }
      
      if (details.level === 6) {
        // For defeated Small Slams
        levelPenalties += 5;
      } else if (details.level === 7) {
        // For defeated Grand Slams
        levelPenalties += 7;
      }
      
      // STEP 3: Calculate Defender Performance Bonus
      let performanceBonus = 0;
      
      const declarerHCPPercentage = (totalHCP / 40) * 100;
      
      if (declarerHCPPercentage > 60) {
        // If Declarer HCP% > 60%
        performanceBonus += (declarerHCPPercentage - 50) / 5;
      }
      
      if (Math.abs(details.result) >= 2) {
        // If contract defeated by 2+ tricks
        performanceBonus += 2;
        
        if (Math.abs(details.result) >= 3) {
          // If contract defeated by 3+ tricks
          performanceBonus += 3;
        }
      }
      
      // STEP 4: Calculate Declarer Consolation (Optional)
      let consolationPoints = 0;
      
      if (declarerHCPPercentage < 40) {
        // If Declarer HCP% < 40%
        consolationPoints = (50 - declarerHCPPercentage) / 10;
      }
      
      // STEP 5: Finalize Scores
      const totalDefenderPoints = basePenalty + levelPenalties + performanceBonus;
      defenderPoints = Math.max(3, Math.round(totalDefenderPoints)); // Minimum defender score: 3 points
      declarerPoints = Math.round(consolationPoints);
      
      // Return calculation details for display
      return {
        declarerPoints,
        defenderPoints,
        
        // Include calculation steps for display
        rawScore,
        basePenalty,
        isGameContract,
        levelPenalties,
        declarerHCPPercentage,
        performanceBonus,
        consolationPoints,
        totalDefenderPoints,
        nsPoints: details.isNS ? declarerPoints : defenderPoints,
        ewPoints: details.isNS ? defenderPoints : declarerPoints
      };
    }
  };
  
  // Get contract summary for display
  const getContractSummary = () => {
    if (!currentDeal || !currentDeal.contract) return "";
    
    const match = currentDeal.contract.match(/(\d)([♣♦♥♠]|NT)\s+([NESW])(X{0,2})/);
    if (!match) return "";
    
    const level = match[1];
    const suit = match[2];
    const declarer = match[3];
    const doubled = match[4];
    
    // Get full declarer name
    let declarerName = "";
    switch (declarer) {
      case 'N': declarerName = "North"; break;
      case 'E': declarerName = "East"; break;
      case 'S': declarerName = "South"; break;
      case 'W': declarerName = "West"; break;
      default: declarerName = declarer;
    }
    
    // Get result display
    const madeContract = currentDeal.result >= 0;
    const resultDisplay = madeContract ? 
      (currentDeal.result > 0 ? `Made +${currentDeal.result}` : "Made") :
      `Down ${Math.abs(currentDeal.result)}`;
    
    // Format doubled
    const doubledDisplay = doubled ? ` ${doubled}` : '';
    
    return `${level}${suit} by ${declarerName}${doubledDisplay} ${resultDisplay}`;
  };
  
  // Calculate final scores
  const scoreDetails = calculateBonusBridgeScores();
  
  // Handle save button click
  const handleSave = () => {
    onSave({
      nsPoints: scoreDetails.nsPoints,
      ewPoints: scoreDetails.ewPoints
    });
  };
  
  // Inline styles for a consistent look
  const styles = {
    container: {
      border: '2px solid #2c3e50',
      borderRadius: '10px',
      overflow: 'hidden',
      margin: '10px',
      backgroundColor: '#f5f5f5',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
      height: 'calc(100% - 20px)',
      display: 'flex',
      flexDirection: 'column'
    },
    header: {
      backgroundColor: '#1e5c8b',
      color: 'white',
      padding: '20px',
      textAlign: 'center'
    },
    headerTitle: {
      fontSize: '24px',
      fontWeight: 'bold',
      margin: 0
    },
    subHeader: {
      backgroundColor: '#1e5c8b',
      color: 'white',
      padding: '15px',
      textAlign: 'center'
    },
    subHeaderTitle: {
      fontSize: '20px',
      fontWeight: 'bold',
      margin: 0
    },
    contractSection: {
      padding: '15px',
      backgroundColor: '#e8f4ff',
      textAlign: 'center',
      borderBottom: '1px solid #d0d0d0'
    },
    contractText: {
      fontSize: '18px',
      fontWeight: 'bold',
      margin: '0 0 5px'
    },
    rawScoreText: {
      fontSize: '16px',
      margin: 0
    },
    contentArea: {
      flex: 1,
      padding: '15px',
      overflow: 'auto'
    },
    section: {
      backgroundColor: 'white',
      border: '1px solid #d0d0d0',
      borderRadius: '8px',
      padding: '15px',
      marginBottom: '15px'
    },
    sectionTitle: {
      fontSize: '18px',
      fontWeight: 'bold',
      textAlign: 'center',
      margin: '0 0 15px',
      color: '#1e5c8b',
      borderBottom: '1px solid #d0d0d0',
      paddingBottom: '10px'
    },
    row: {
      display: 'flex',
      justifyContent: 'space-between',
      padding: '8px 0',
      borderBottom: '1px solid #eeeeee'
    },
    label: {
      fontWeight: '500'
    },
    value: {
      fontWeight: 'bold',
      color: '#1e5c8b'
    },
    highlightedRow: {
      display: 'flex',
      justifyContent: 'space-between',
      padding: '12px 0',
      margin: '10px 0',
      backgroundColor: '#e8f4ff',
      borderRadius: '5px',
      fontSize: '20px',
      fontWeight: 'bold'
    },
    highlightedLabel: {
      color: '#1e5c8b',
      paddingLeft: '10px'
    },
    highlightedValue: {
      color: '#1e5c8b',
      paddingRight: '10px'
    },
    footer: {
      display: 'flex',
      padding: '15px',
      gap: '15px',
      backgroundColor: '#1e5c8b'
    },
    editBtn: {
      flex: 1,
      height: '60px',
      backgroundColor: '#468bbf',
      color: 'white',
      border: 'none',
      borderRadius: '5px',
      fontSize: '18px',
      fontWeight: 'bold',
      cursor: 'pointer'
    },
    saveBtn: {
      flex: 1,
      height: '60px',
      backgroundColor: '#3c8c5c',
      color: 'white',
      border: 'none',
      borderRadius: '5px',
      fontSize: '18px',
      fontWeight: 'bold',
      cursor: 'pointer'
    }
  };

  // Determine which sections to show based on contract success/failure
  const details = parseContractDetails();
  const isMadeContract = details?.madeContract;
  
  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.headerTitle}>Final Score Analysis</h1>
      </div>
      
      <div style={styles.subHeader}>
        <h2 style={styles.subHeaderTitle}>Bonus Bridge Score Calculation</h2>
      </div>
      
      <div style={styles.contractSection}>
        <p style={styles.contractText}>Contract: {getContractSummary()}</p>
        <p style={styles.rawScoreText}>Raw score: {scoreDetails.rawScore} points</p>
      </div>
      
      <div style={styles.contentArea}>
        <div style={styles.section}>
          <h3 style={styles.sectionTitle}>Hand Analysis</h3>
          <div style={styles.row}>
            <div style={styles.label}>Combined HCP (Declarer + Dummy):</div>
            <div style={styles.value}>{totalHCP}</div>
          </div>
          {isMadeContract && (
            <>
              <div style={styles.row}>
                <div style={styles.label}>Declarer HCP Percentage:</div>
                <div style={styles.value}>{Math.round(scoreDetails.declarerHCPPercentage)}%</div>
              </div>
              <div style={styles.row}>
                <div style={styles.label}>Defender HCP Percentage:</div>
                <div style={styles.value}>{Math.round(scoreDetails.defenderHCPPercentage)}%</div>
              </div>
              <div style={styles.row}>
                <div style={styles.label}>HCP Advantage:</div>
                <div style={styles.value}>{Math.round(scoreDetails.hcpAdvantage)}% to {scoreDetails.advantageSide}</div>
              </div>
            </>
          )}
          <div style={styles.row}>
            <div style={styles.label}>Number of Singletons:</div>
            <div style={styles.value}>{singletons}</div>
          </div>
          <div style={styles.row}>
            <div style={styles.label}>Number of Voids:</div>
            <div style={styles.value}>{voids}</div>
          </div>
          <div style={styles.row}>
            <div style={styles.label}>Number of Long Suits (6+ cards):</div>
            <div style={styles.value}>{longSuits}</div>
          </div>
          <div style={styles.row}>
            <div style={styles.label}>Distribution Points:</div>
            <div style={styles.value}>{details?.distributionPoints || 0}</div>
          </div>
          {isMadeContract && (
            <>
              <div style={styles.row}>
                <div style={styles.label}>Expected HCP for Contract:</div>
                <div style={styles.value}>{scoreDetails.expectedHCP}</div>
              </div>
              <div style={styles.row}>
                <div style={styles.label}>Contract Expected Tricks:</div>
                <div style={styles.value}>{scoreDetails.contractExpectedTricks}</div>
              </div>
              <div style={{...styles.row, borderBottom: 'none'}}>
                <div style={styles.label}>Hand Expected Tricks:</div>
                <div style={styles.value}>{scoreDetails.handExpectedTricks}</div>
              </div>
            </>
          )}
        </div>
        
        {isMadeContract ? (
          // Made Contract Calculation Steps
          <div style={styles.section}>
            <h3 style={styles.sectionTitle}>Calculation Steps (Made Contract)</h3>
            <div style={styles.row}>
              <div style={styles.label}>1. Raw Score / 20:</div>
              <div style={styles.value}>{scoreDetails.initialPoints.toFixed(1)} points</div>
            </div>
            <div style={styles.row}>
              <div style={styles.label}>2. HCP Adjustment:</div>
              <div style={styles.value}>{scoreDetails.hcpAdjustment.toFixed(2)} points</div>
            </div>
            <div style={styles.row}>
              <div style={styles.label}>3. After HCP Adjustment:</div>
              <div style={styles.value}>{scoreDetails.adjustedPoints.toFixed(2)} points</div>
            </div>
            <div style={styles.row}>
              <div style={styles.label}>4. Performance Variance:</div>
              <div style={styles.value}>{scoreDetails.performanceVariance} tricks</div>
            </div>
            <div style={styles.row}>
              <div style={styles.label}>5. After Performance Assessment:</div>
              <div style={styles.value}>{scoreDetails.performancePoints.toFixed(2)} points</div>
            </div>
            <div style={styles.row}>
              <div style={styles.label}>6. Contract Type Adjustment:</div>
              <div style={styles.value}>
                {scoreDetails.isGameContract ? "+2 (Game)" : ""}
                {details.level === 6 ? " +4 (Small Slam)" : ""}
                {details.level === 7 ? " +6 (Grand Slam)" : ""}
                {details.suit === 'NT' ? " +1 (NT)" : ""}
              </div>
            </div>
            <div style={styles.row}>
              <div style={styles.label}>7. After Contract Adjustments:</div>
              <div style={styles.value}>{scoreDetails.contractPoints.toFixed(2)} points</div>
            </div>
            <div style={styles.row}>
              <div style={styles.label}>8. Distribution Adjustment:</div>
              <div style={styles.value}>{scoreDetails.distributionAdjustment.toFixed(2)} points</div>
            </div>
            <div style={{...styles.row, borderBottom: 'none'}}>
              <div style={styles.label}>9. Defender Reward:</div>
              <div style={styles.value}>{scoreDetails.defenderReward.toFixed(2)} points</div>
            </div>
          </div>
        ) : (
          // Defeated Contract Calculation Steps
          <div style={styles.section}>
            <h3 style={styles.sectionTitle}>Calculation Steps (Defeated Contract)</h3>
            <div style={styles.row}>
              <div style={styles.label}>1. Base Penalty (|Raw Score| / 10):</div>
              <div style={styles.value}>{scoreDetails.basePenalty.toFixed(1)} points</div>
            </div>
            <div style={styles.row}>
              <div style={styles.label}>2. Contract Level Penalties:</div>
              <div style={styles.value}>{scoreDetails.levelPenalties.toFixed(1)} points</div>
            </div>
            <div style={styles.row}>
              <div style={styles.label}>3. Defender Performance Bonus:</div>
              <div style={styles.value}>{scoreDetails.performanceBonus.toFixed(1)} points</div>
            </div>
            <div style={styles.row}>
              <div style={styles.label}>4. Declarer Consolation:</div>
              <div style={styles.value}>{scoreDetails.consolationPoints.toFixed(1)} points</div>
            </div>
            <div style={{...styles.row, borderBottom: 'none'}}>
              <div style={styles.label}>5. Total Defender Points:</div>
              <div style={styles.value}>{scoreDetails.totalDefenderPoints.toFixed(1)} points</div>
            </div>
          </div>
        )}
        
        <div style={styles.section}>
          <h3 style={styles.sectionTitle}>Final Scores</h3>
          <div style={styles.highlightedRow}>
            <div style={styles.highlightedLabel}>NS Score:</div>
            <div style={styles.highlightedValue}>{scoreDetails.nsPoints} points</div>
          </div>
          <div style={styles.highlightedRow}>
            <div style={styles.highlightedLabel}>EW Score:</div>
            <div style={styles.highlightedValue}>{scoreDetails.ewPoints} points</div>
          </div>
        </div>
      </div>
      
      <div style={styles.footer}>
        <button style={styles.editBtn} onClick={onEdit}>
          Edit Adjustments
        </button>
        <button style={styles.saveBtn} onClick={handleSave}>
          Continue to Next Deal
        </button>
      </div>
    </div>
  );
};

export default FinalScoreAnalysis;
