import React, { useState, useEffect } from 'react';

// Ultra-compact TrickInput component with height constraints
const TrickInput = ({ onTrickChange, initialResult = null, contract = '', onChangeContract }) => {
  // State for selected trick button
  const [selectedTrick, setSelectedTrick] = useState(null);
  // State for required tricks (based on contract level)
  const [requiredTricks, setRequiredTricks] = useState(7);
  
  // Ultra-compact container with strict size limits
  const containerStyle = {
    display: 'flex',
    flexDirection: 'column',
    gap: '4px',
    padding: '5px',
    maxWidth: '100%',
    boxSizing: 'border-box',
    maxHeight: '270px !important', // Strict height limit
    overflow: 'hidden !important',
    margin: '0 !important',
    background: 'white !important',
    borderRadius: '8px !important'
  };
  
  // Minimalist required tricks banner
  const requiredTricksStyle = {
    backgroundColor: '#f0f3f8',
    padding: '4px !important',
    borderRadius: '4px !important',
    textAlign: 'center',
    fontSize: '12px !important',
    fontWeight: 'bold',
    color: '#2c3e50',
    border: '1px solid #dfe6e9',
    marginBottom: '4px !important',
    marginTop: '0 !important'
  };
  
  // Compact grid with minimal spacing
  const gridStyle = {
    display: 'flex',
    flexDirection: 'column',
    gap: '3px !important'
  };
  
  // Compact row with minimal spacing
  const rowStyle = {
    display: 'flex',
    gap: '3px !important',
    marginBottom: '0 !important'
  };
  
  // Very small change contract button
  const changeButtonStyle = {
    width: '100%',
    height: '30px !important',
    marginTop: '4px !important',
    border: '1px solid #ddd !important',
    borderRadius: '4px !important',
    backgroundColor: '#f5f5f5 !important',
    color: '#2c3e50 !important',
    fontSize: '13px !important',
    fontWeight: 'bold',
    cursor: 'pointer',
    padding: '0 !important',
    margin: '0 !important',
    marginTop: '4px !important'
  };
  
  // Calculate required tricks when contract changes
  useEffect(() => {
    if (contract) {
      const contractMatch = contract.match(/(\d)([♣♦♥♠]|NT)\s+([NESW])(X{0,2})/);
      if (contractMatch) {
        const level = parseInt(contractMatch[1]);
        setRequiredTricks(level + 6);
      }
    }
  }, [contract]);
  
  // Set selected trick from initialResult if provided
  useEffect(() => {
    if (initialResult !== null && requiredTricks) {
      // Calculate actual tricks from result
      const actualTricks = requiredTricks + initialResult;
      setSelectedTrick(actualTricks);
    }
  }, [initialResult, requiredTricks]);
  
  // Handle trick button click
  const handleTrickClick = (numTricks) => {
    setSelectedTrick(numTricks);
    
    // Calculate result based on tricks and required tricks
    const result = numTricks - requiredTricks;
    onTrickChange(result, numTricks);
  };
  
  // Ultra-compact trick button style
  const getTrickButtonStyle = (trickNum) => {
    const isSelected = selectedTrick === trickNum;
    const isExact = trickNum === requiredTricks;
    const isMade = trickNum > requiredTricks;
    const isDown = trickNum < requiredTricks;
    
    let backgroundColor, borderColor, textColor;
    
    if (isSelected) {
      if (isExact) {
        backgroundColor = '#ff9800 !important';
        borderColor = '#ff9800 !important';
      } else if (isMade) {
        backgroundColor = '#4caf50 !important';
        borderColor = '#4caf50 !important';
      } else {
        backgroundColor = '#f44336 !important';
        borderColor = '#f44336 !important';
      }
      textColor = 'white !important';
    } else {
      if (isExact) {
        backgroundColor = '#fff3e0 !important';
        borderColor = '#ffe0b2 !important';
      } else if (isMade) {
        backgroundColor = '#e8f5e9 !important';
        borderColor = '#c8e6c9 !important';
      } else {
        backgroundColor = '#ffebee !important';
        borderColor = '#ffcdd2 !important';
      }
      textColor = 'black !important';
    }
    
    return {
      flex: '1',
      height: '32px !important', // Very small height
      minHeight: '32px !important',
      maxHeight: '32px !important',
      width: '32px !important',
      minWidth: '32px !important',
      border: `1px solid ${borderColor}`,
      borderRadius: '4px !important',
      fontSize: '14px !important', // Smaller font
      fontWeight: 'bold',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: backgroundColor,
      color: textColor,
      boxShadow: 'none !important', // No shadow
      padding: '0 !important', // No padding
      margin: '0 !important' // No margin
    };
  };
  
  // Handle change contract button click
  const handleChangeContract = () => {
    if (onChangeContract && typeof onChangeContract === 'function') {
      onChangeContract();
    }
  };
  
  // Generate trick buttons in rows of 4
  const renderTrickButtons = () => {
    const rows = [];
    
    // First two rows with 4 buttons each
    for (let row = 0; row < 3; row++) {
      const tricks = [];
      for (let col = 0; col < 4; col++) {
        const trickNum = row * 4 + col;
        if (trickNum <= 13) {
          tricks.push(
            <button
              key={`trick-${trickNum}`}
              style={getTrickButtonStyle(trickNum)}
              onClick={() => handleTrickClick(trickNum)}
            >
              {trickNum}
            </button>
          );
        }
      }
      rows.push(
        <div key={`row-${row}`} style={rowStyle}>
          {tricks}
        </div>
      );
    }
    
    // Last row with 2 buttons (12, 13) in the middle
    rows.push(
      <div key="row-3" style={{...rowStyle, justifyContent: 'center'}}>
        <div style={{width: '80px', display: 'flex', gap: '3px'}}>
          <button
            key="trick-12"
            style={getTrickButtonStyle(12)}
            onClick={() => handleTrickClick(12)}
          >
            12
          </button>
          <button
            key="trick-13"
            style={getTrickButtonStyle(13)}
            onClick={() => handleTrickClick(13)}
          >
            13
          </button>
        </div>
      </div>
    );
    
    return rows;
  };
  
  return (
    <div style={containerStyle}>
      <div style={requiredTricksStyle}>
        Required tricks: {requiredTricks}
      </div>
      
      <div style={gridStyle}>
        {renderTrickButtons()}
      </div>
      
      <button 
        style={changeButtonStyle}
        onClick={handleChangeContract}
      >
        Change Contract
      </button>
    </div>
  );
};

export default TrickInput;
