import React from 'react';

const ScoreTracking = ({ scores, currentDeal }) => {
  // This component displays the current scores and calculates bridge scoring
  
  // Function to calculate score for the current deal (using standard bridge rules)
  const calculateDealScore = () => {
    if (!currentDeal || !currentDeal.contract || currentDeal.result === null) {
      return { nsPoints: 0, ewPoints: 0 };
    }
    
    // Extract information from the contract
    const contractMatch = currentDeal.contract.match(/(\d)([♣♦♥♠]|NT)\s+([NESW])(X{0,2})/);
    if (!contractMatch) return { nsPoints: 0, ewPoints: 0 };
    
    const level = parseInt(contractMatch[1]);
    const suit = contractMatch[2];
    const declarer = contractMatch[3];
    const doubled = contractMatch[4];
    
    // Determine if the contract was made or went down
    const madeContract = currentDeal.result >= 0;
    
    // Determine vulnerability
    const isNS = declarer === 'N' || declarer === 'S';
    const declarerVulnerable = isNS ? currentDeal.vulnerable.ns : currentDeal.vulnerable.ew;
    
    let points = 0;
    
    // Calculate points based on standard bridge scoring rules
    if (madeContract) {
      // Trick score
      const trickValue = suit === '♣' || suit === '♦' ? 20 : 30;
      const firstTrickValue = suit === 'NT' ? 40 : trickValue;
      
      // Calculate trick score
      points += firstTrickValue + (level - 1) * trickValue;
      
      // Apply doubling
      if (doubled === 'X') points *= 2;
      if (doubled === 'XX') points *= 4;
      
      // Game and slam bonuses
      if (points >= 100) {
        // Game bonus
        points += declarerVulnerable ? 500 : 300;
      } else {
        // Part score bonus
        points += 50;
      }
      
      // Slam bonuses
      if (level === 6) {
        points += declarerVulnerable ? 750 : 500; // Small slam
      } else if (level === 7) {
        points += declarerVulnerable ? 1500 : 1000; // Grand slam
      }
      
      // Overtrick bonuses
      if (currentDeal.result > 0) {
        const overtrickValue = doubled 
          ? (declarerVulnerable ? 200 : 100) * (doubled === 'XX' ? 2 : 1)
          : (suit === '♣' || suit === '♦' ? 20 : 30);
        
        points += overtrickValue * currentDeal.result;
      }
      
      // Doubled and redoubled bonuses
      if (doubled === 'X') points += 50;
      if (doubled === 'XX') points += 100;
      
    } else {
      // Contract went down
      const undertricks = Math.abs(currentDeal.result);
      
      if (!doubled) {
        // Not doubled
        points = undertricks * (declarerVulnerable ? 100 : 50);
      } else {
        // Doubled or redoubled
        const multiplier = doubled === 'XX' ? 2 : 1;
        
        if (declarerVulnerable) {
          points = (300 * multiplier) + ((undertricks - 1) * 300 * multiplier);
        } else {
          // First undertrick
          points = 100 * multiplier;
          
          // Second and third undertricks
          if (undertricks > 1) {
            points += Math.min(undertricks - 1, 2) * 200 * multiplier;
          }
          
          // Fourth and subsequent undertricks
          if (undertricks > 3) {
            points += (undertricks - 3) * 300 * multiplier;
          }
        }
      }
      
      // For undertricks, points go to the opponents
      points = -points;
    }
    
    // Assign points to the appropriate side
    return isNS 
      ? { nsPoints: points, ewPoints: 0 }
      : { nsPoints: 0, ewPoints: points };
  };
  
  // Get the score for the current deal
  const dealScore = currentDeal ? calculateDealScore() : { nsPoints: 0, ewPoints: 0 };
  
  return (
    <div className="score-tracking">
      <div className="score-header">
        <h3>Score Summary</h3>
      </div>
      
      <div className="score-table">
        <div className="score-row header">
          <div className="team">Team</div>
          <div className="current">This Deal</div>
          <div className="total">Running Total</div>
        </div>
        
        <div className="score-row">
          <div className="team">North-South</div>
          <div className="current">{dealScore.nsPoints}</div>
          <div className="total">{scores.nsScore + dealScore.nsPoints}</div>
        </div>
        
        <div className="score-row">
          <div className="team">East-West</div>
          <div className="current">{dealScore.ewPoints}</div>
          <div className="total">{scores.ewScore + dealScore.ewPoints}</div>
        </div>
      </div>
      
      {currentDeal && currentDeal.contract && (
        <div className="deal-details">
          <p><strong>Contract:</strong> {currentDeal.contract}</p>
          <p>
            <strong>Result:</strong> {
              currentDeal.result > 0 
                ? `Made ${currentDeal.result} overtrick${currentDeal.result !== 1 ? 's' : ''}`
                : currentDeal.result < 0
                  ? `Down ${Math.abs(currentDeal.result)}`
                  : 'Contract made exactly'
            }
          </p>
        </div>
      )}
    </div>
  );
};

export default ScoreTracking;
