import React, { useState, useEffect } from 'react';
import ScoreTracking from './components/scoreTracking';
import ContractInput from './components/ContractInput';
import TrickInput from './components/TrickInput';
import LoadingSpinner from './components/LoadingSpinner';
import { requestWakeLock, releaseWakeLock, isWakeLockSupported } from './utils/wakeLock';
import { vulnerabilityDescription } from './utils/scoring';
import './global.css';

const BridgeGameUI = ({ gameState, updateCurrentDeal, onNextDeal, onNewGame, setGameState }) => {
  // Function to keep the device awake
  const toggleKeepAwake = async () => {
    const newAwakeState = !gameState.isAwake;
    
    // Update state first for immediate UI feedback
    setGameState(prev => ({
      ...prev,
      isAwake: newAwakeState
    }));
    
    if (newAwakeState) {
      // Turn on wake lock
      if (isWakeLockSupported()) {
        const success = await requestWakeLock();
        if (!success) {
          // Wake lock request failed, revert state
          setGameState(prev => ({
            ...prev,
            isAwake: false
          }));
        }
      } else {
        console.log('Wake Lock API not supported in this browser');
      }
    } else {
      // Turn off wake lock
      await releaseWakeLock();
    }
  };

  // Show score popup
  const showScorePopup = () => {
    setGameState(prev => ({
      ...prev,
      showScorePopup: true
    }));
  };

  // Hide score popup
  const hideScorePopup = () => {
    setGameState(prev => ({
      ...prev,
      showScorePopup: false
    }));
  };

  // Handle contract input changes
  const handleContractChange = (contract) => {
    updateCurrentDeal({ contract });
  };

  // Handle trick input changes
  const handleTrickChange = (result, tricks) => {
    updateCurrentDeal({ result, tricks });
  };

  return (
    <div className="bridge-game-container">
      <header className="game-header">
        <div className="game-info-left">
          <span>Game #{gameState.gameNumber}</span>
        </div>
        <div className="game-title">
          <h1>Bonus Bridge</h1>
        </div>
        <div className="game-info-right">
          <span>Deal #{gameState.dealNumber}</span>
        </div>
        <div className="vulnerability-info">
          {vulnerabilityDescription(gameState.currentDeal.vulnerable)}
        </div>
      </header>
      
      <div className="game-board">
        <div className="player north-player">North</div>
        <div className="player west-player">West</div>
        <div className="center-area">
          <div className="contract-display">
            {gameState.currentDeal.contract || "No Contract"}
          </div>
          <div className="result-display">
            {gameState.currentDeal.result !== null ? 
              (gameState.currentDeal.result > 0 ? 
                `Made +${gameState.currentDeal.result}` : 
                gameState.currentDeal.result < 0 ? 
                `Down ${Math.abs(gameState.currentDeal.result)}` : 
                "Made exactly") 
              : "Result"}
          </div>
        </div>
        <div className="player east-player">East</div>
        <div className="player south-player">South</div>
        
        <div className="contract-input-container">
          <ContractInput 
            onContractChange={handleContractChange}
            initialContract={gameState.currentDeal.contract}
          />
        </div>
        
        <div className="trick-input-container">
          <TrickInput 
            onTrickChange={handleTrickChange}
            initialResult={gameState.currentDeal.result}
            contract={gameState.currentDeal.contract}
          />
        </div>
      </div>
      
      <footer className="game-controls">
        <div className="keep-awake-container">
          <button 
            className={`keep-awake-btn ${gameState.isAwake ? 'active' : ''}`}
            onClick={toggleKeepAwake}
          >
            {gameState.isAwake ? 'Keep Awake: ON' : 'Keep Awake: OFF'}
          </button>
          
          <button 
            className="score-btn"
            onClick={showScorePopup}
          >
            Score
          </button>
        </div>
        
        <div className="game-actions">
          <button 
            className="next-deal-btn"
            onClick={onNextDeal}
            disabled={!gameState.currentDeal.contract || gameState.currentDeal.result === null}
          >
            Next Deal
          </button>
          
          <button 
            className="new-game-btn"
            onClick={onNewGame}
          >
            New Game
          </button>
        </div>
      </footer>
      
      {gameState.showScorePopup && (
        <div className="score-popup">
          <div className="score-popup-content">
            <h2>Current Scores</h2>
            <ScoreTracking 
              scores={gameState.scores} 
              currentDeal={gameState.currentDeal}
            />
            <button className="primary-btn" onClick={hideScorePopup}>
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default BridgeGameUI;
