import React, { useState, useEffect } from 'react';

// Compact ContractInput component with fixed height
const ContractInput = ({ onContractChange, initialContract = '' }) => {
  // Define state variables
  const [level, setLevel] = useState('');
  const [suit, setSuit] = useState('');
  const [declarer, setDeclarer] = useState('');
  const [doubled, setDoubled] = useState('');
  
  // Container styles - fixed height and width constraints
  const containerStyle = {
    padding: '10px 0',
    maxWidth: '100%',
    boxSizing: 'border-box',
    maxHeight: '350px', // Constrain height
    overflow: 'hidden'
  };
  
  // Section styles - reduced margins
  const sectionStyle = {
    marginBottom: '10px'
  };
  
  // Label styles - smaller
  const labelStyle = {
    fontWeight: 'bold',
    marginBottom: '5px',
    fontSize: '14px',
    color: '#1e5c8b',
    display: 'block'
  };
  
  // Button row styles
  const buttonRowStyle = {
    display: 'flex',
    gap: '4px', // Reduced gap
    marginBottom: '8px', // Reduced margin
    width: '100%'
  };
  
  // Parse initialContract if provided
  useEffect(() => {
    if (initialContract) {
      const contractMatch = initialContract.match(/(\d)([♣♦♥♠]|NT)\s+([NESW])(X{0,2})/);
      if (contractMatch) {
        setLevel(contractMatch[1]);
        setSuit(contractMatch[2]);
        setDeclarer(contractMatch[3]);
        setDoubled(contractMatch[4] || '');
      }
    } else {
      // Initialize with default values
      setDoubled(''); // Explicitly set to empty string (no doubled)
    }
  }, [initialContract]);
  
  // Update contract when any component changes
  useEffect(() => {
    if (level && suit && declarer) {
      const newContract = `${level}${suit} ${declarer}${doubled}`;
      onContractChange(newContract);
    }
  }, [level, suit, declarer, doubled, onContractChange]);
  
  // Create a normal button style function - reduced height
  const getButtonStyle = (isSelected) => {
    return {
      flex: '1',
      height: '40px', // Reduced height
      border: isSelected ? '1px solid #3498db' : '1px solid #ddd',
      backgroundColor: isSelected ? '#3498db' : '#f8f8f8',
      borderRadius: '6px',
      fontSize: '16px', // Reduced font size
      fontWeight: isSelected ? 'bold' : 'normal',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      color: isSelected ? 'white' : 'black'
    };
  };
  
  // Create a suit button style function
  const getSuitButtonStyle = (s, isSelected) => {
    const baseStyle = getButtonStyle(isSelected);
    
    // Determine color based on suit
    let color = 'black';
    let fontSize = '22px'; // Reduced font size
    
    if (s === '♦' || s === '♥') {
      color = '#e74c3c'; // Red for diamonds and hearts
    } else if (s === 'NT') {
      fontSize = '18px'; // Smaller for NT
    }
    
    return {
      ...baseStyle,
      fontSize: fontSize,
      color: isSelected ? 'white' : color
    };
  };
  
  return (
    <div style={containerStyle}>
      {/* Level Input */}
      <div style={sectionStyle}>
        <div style={labelStyle}>Level and Suit:</div>
        
        {/* Level Buttons (1-7) */}
        <div style={buttonRowStyle}>
          {[1, 2, 3, 4, 5, 6, 7].map((num) => (
            <button
              key={`level-${num}`}
              style={getButtonStyle(level === num.toString())}
              onClick={() => setLevel(num.toString())}
            >
              {num}
            </button>
          ))}
        </div>
        
        {/* Suit Buttons */}
        <div style={buttonRowStyle}>
          {['♣', '♦', '♥', '♠', 'NT'].map((s) => (
            <button
              key={`suit-${s}`}
              style={getSuitButtonStyle(s, suit === s)}
              onClick={() => setSuit(s)}
            >
              {s}
            </button>
          ))}
        </div>
      </div>
      
      {/* Declarer Input */}
      <div style={sectionStyle}>
        <div style={labelStyle}>Declarer:</div>
        <div style={buttonRowStyle}>
          {['N', 'S', 'E', 'W'].map((d) => (
            <button
              key={`declarer-${d}`}
              style={getButtonStyle(declarer === d)}
              onClick={() => setDeclarer(d)}
            >
              {d}
            </button>
          ))}
        </div>
      </div>
      
      {/* Doubled Input */}
      <div style={sectionStyle}>
        <div style={labelStyle}>Doubled:</div>
        <div style={buttonRowStyle}>
          {/* Set default selection for the "-" option */}
          <button
            style={{
              ...getButtonStyle(doubled === ''),
              // Force not-doubled to be selected by default when no other selection is made
              backgroundColor: doubled === '' || doubled === null ? '#3498db' : '#f8f8f8',
              color: doubled === '' || doubled === null ? 'white' : 'black',
              borderColor: doubled === '' || doubled === null ? '#3498db' : '#ddd'
            }}
            onClick={() => setDoubled('')}
          >
            -
          </button>
          <button
            style={getButtonStyle(doubled === 'X')}
            onClick={() => setDoubled('X')}
          >
            X
          </button>
          <button
            style={getButtonStyle(doubled === 'XX')}
            onClick={() => setDoubled('XX')}
          >
            XX
          </button>
        </div>
      </div>
    </div>
  );
};

export default ContractInput;
