import React, { useState, useEffect, useCallback } from 'react';
import BridgeGameUI from './BridgeGameUI';
import ScoreAdjustment from './components/ScoreAdjustment';
import FinalScoreAnalysis from './components/FinalScoreAnalysis';
import ScoreProcessor from './components/ScoreProcessor';
import GameSummary from './components/GameSummary';
import { 
  determineVulnerability,
  parseContract,
  calculateBridgeScore
} from './utils/scoring';
import './global.css';

const App = () => {
  // State to track current view (main game, score adjustment, or final analysis)
  const [currentView, setCurrentView] = useState('game');
  
  // Game state
  const [gameState, setGameState] = useState({
    gameNumber: 1,
    dealNumber: 1,
    deals: [],
    scores: {
      nsPoints: 0,
      ewPoints: 0,
      nsTotal: 0,
      ewTotal: 0
    },
    currentDeal: {
      dealNumber: 1,
      vulnerable: determineVulnerability(1),
      contract: '',
      result: null,
      tricks: null,
      nsPoints: 0,
      ewPoints: 0,
      rawScore: 0,
      bonusScoringEnabled: true,
      handAnalysis: null
    },
    showScorePopup: false,
    isAwake: false,
    gameEnded: false
  });
  
  // Function to handle score calculation from ScoreProcessor
  const handleScoreCalculated = (scoreResult) => {
    const { nsPoints, ewPoints, rawScore } = scoreResult;
    
    console.log('App received score calculation:', scoreResult);
    
    // Update the current deal with the calculated scores
    setGameState(prevState => {
      // Check if scores have changed
      if (prevState.currentDeal.nsPoints === nsPoints && 
          prevState.currentDeal.ewPoints === ewPoints) {
        return prevState; // No change needed
      }
      
      const updatedCurrentDeal = {
        ...prevState.currentDeal,
        nsPoints,
        ewPoints,
        rawScore
      };
      
      // Also update the score display
      const updatedScores = {
        ...prevState.scores,
        nsPoints,
        ewPoints
      };
      
      return {
        ...prevState,
        currentDeal: updatedCurrentDeal,
        scores: updatedScores
      };
    });
  };
  
  // Function to update current deal
  const updateCurrentDeal = useCallback((dealUpdates) => {
    setGameState(prevState => ({
      ...prevState,
      currentDeal: {
        ...prevState.currentDeal,
        ...dealUpdates
      }
    }));
  }, []);
  
  // Function to handle adjustment input
  const handleSaveAdjustment = (adjustmentData) => {
    setGameState(prevState => ({
      ...prevState,
      currentDeal: {
        ...prevState.currentDeal,
        handAnalysis: adjustmentData,
        bonusScoringEnabled: true
      }
    }));
    
    setCurrentView('analysis');
  };
  
  // Function to handle score analysis saving
  const handleSaveAnalysis = (scoreResult) => {
    // First, save the current deal to the deals array
    const currentDealWithFinalScores = {
      ...gameState.currentDeal,
      dealNumber: gameState.dealNumber,
      nsPoints: scoreResult.nsPoints,
      ewPoints: scoreResult.ewPoints
    };
    
    // Calculate running totals
    const nsTotal = (gameState.scores.nsTotal || 0) + (scoreResult.nsPoints || 0);
    const ewTotal = (gameState.scores.ewTotal || 0) + (scoreResult.ewPoints || 0);
    
    // Update game state with new deal
    setGameState(prevState => {
      return {
        ...prevState,
        dealNumber: prevState.dealNumber + 1,
        deals: [...prevState.deals, currentDealWithFinalScores],
        scores: {
          nsPoints: scoreResult.nsPoints || 0,
          ewPoints: scoreResult.ewPoints || 0,
          nsTotal,
          ewTotal
        },
        currentDeal: {
          dealNumber: prevState.dealNumber + 1,
          vulnerable: determineVulnerability(prevState.dealNumber + 1),
          contract: '',
          result: null,
          tricks: null,
          nsPoints: 0,
          ewPoints: 0,
          rawScore: 0,
          bonusScoringEnabled: true,
          handAnalysis: null
        }
      };
    });
    
    // Return to the game view
    setCurrentView('game');
  };
  
  // Function to handle next deal without adjustments
  const handleNextDeal = () => {
    // If no scores have been calculated, calculate standard scores
    if (!gameState.currentDeal.nsPoints && !gameState.currentDeal.ewPoints && gameState.currentDeal.contract) {
      const contractDetails = parseContract(
        gameState.currentDeal.contract, 
        gameState.currentDeal.result, 
        gameState.currentDeal.vulnerable
      );
      
      if (contractDetails) {
        const standardScore = calculateBridgeScore(contractDetails);
        
        // Calculate raw score
        const rawScore = Math.max(
          Math.abs(standardScore.nsPoints || 0), 
          Math.abs(standardScore.ewPoints || 0)
        );
        
        // Update current deal with standard scores
        const currentDealWithScores = {
          ...gameState.currentDeal,
          dealNumber: gameState.dealNumber,
          nsPoints: standardScore.nsPoints || 0,
          ewPoints: standardScore.ewPoints || 0,
          rawScore
        };
        
        // Calculate running totals - use raw scores instead of bonus points
        const nsRawTotal = (gameState.scores.nsTotal || 0) + (standardScore.nsPoints > 0 ? rawScore : 0);
        const ewRawTotal = (gameState.scores.ewTotal || 0) + (standardScore.ewPoints > 0 ? rawScore : 0);
        
        // Update game state with new deal
        setGameState(prevState => {
          return {
            ...prevState,
            dealNumber: prevState.dealNumber + 1,
            deals: [...prevState.deals, currentDealWithScores],
            scores: {
              nsPoints: standardScore.nsPoints || 0,
              ewPoints: standardScore.ewPoints || 0,
              nsTotal: nsRawTotal,
              ewTotal: ewRawTotal
            },
            currentDeal: {
              dealNumber: prevState.dealNumber + 1,
              vulnerable: determineVulnerability(prevState.dealNumber + 1),
              contract: '',
              result: null,
              tricks: null,
              nsPoints: 0,
              ewPoints: 0,
              rawScore: 0,
              bonusScoringEnabled: true,
              handAnalysis: null
            }
          };
        });
      }
    } else {
      // Scores have been calculated, proceed as usual
      const currentDealWithScores = {
        ...gameState.currentDeal,
        dealNumber: gameState.dealNumber
      };
      
      // Calculate running totals - use raw scores instead of bonus points
      const rawScore = currentDealWithScores.rawScore || 0;
      const nsRawTotal = (gameState.scores.nsTotal || 0) + 
                        (currentDealWithScores.nsPoints > 0 ? rawScore : 0);
      const ewRawTotal = (gameState.scores.ewTotal || 0) + 
                        (currentDealWithScores.ewPoints > 0 ? rawScore : 0);
      
      // Update game state with new deal
      setGameState(prevState => {
        return {
          ...prevState,
          dealNumber: prevState.dealNumber + 1,
          deals: [...prevState.deals, currentDealWithScores],
          scores: {
            nsPoints: currentDealWithScores.nsPoints || 0,
            ewPoints: currentDealWithScores.ewPoints || 0,
            nsTotal: nsRawTotal,
            ewTotal: ewRawTotal
          },
          currentDeal: {
            dealNumber: prevState.dealNumber + 1,
            vulnerable: determineVulnerability(prevState.dealNumber + 1),
            contract: '',
            result: null,
            tricks: null,
            nsPoints: 0,
            ewPoints: 0,
            rawScore: 0,
            bonusScoringEnabled: true,
            handAnalysis: null
          }
        };
      });
    }
  };
  
  // Function to start a new game
  const handleNewGame = () => {
    // Check if we're ending the current game or starting fresh
    if (gameState.deals.length > 0) {
      // Mark game as ended to show summary
      setGameState(prevState => ({
        ...prevState,
        gameEnded: true
      }));
    } else {
      // Reset the game state for a fresh start
      setGameState({
        gameNumber: gameState.gameNumber + 1,
        dealNumber: 1,
        deals: [],
        scores: {
          nsPoints: 0,
          ewPoints: 0,
          nsTotal: 0,
          ewTotal: 0
        },
        currentDeal: {
          dealNumber: 1,
          vulnerable: determineVulnerability(1),
          contract: '',
          result: null,
          tricks: null,
          nsPoints: 0,
          ewPoints: 0,
          rawScore: 0,
          bonusScoringEnabled: true,
          handAnalysis: null
        },
        showScorePopup: false,
        isAwake: gameState.isAwake,
        gameEnded: false
      });
    }
  };
  
  // Function to return to the game after viewing summary
  const handleReturnToGame = () => {
    // Reset the game state for a fresh start
    setGameState({
      gameNumber: gameState.gameNumber + 1,
      dealNumber: 1,
      deals: [],
      scores: {
        nsPoints: 0,
        ewPoints: 0,
        nsTotal: 0,
        ewTotal: 0
      },
      currentDeal: {
        dealNumber: 1,
        vulnerable: determineVulnerability(1),
        contract: '',
        result: null,
        tricks: null,
        nsPoints: 0,
        ewPoints: 0,
        rawScore: 0,
        bonusScoringEnabled: true,
        handAnalysis: null
      },
      showScorePopup: false,
      isAwake: gameState.isAwake,
      gameEnded: false
    });
  };
  
  // Function to handle choosing Bonus Bridge scoring
  const handleChooseBonusBridge = () => {
    setCurrentView('adjustment');
  };
  
  // Function to handle choosing Party Bridge scoring (standard)
  const handleChoosePartyBridge = () => {
    // Calculate standard bridge score
    const contractDetails = parseContract(
      gameState.currentDeal.contract, 
      gameState.currentDeal.result, 
      gameState.currentDeal.vulnerable
    );
    
    if (contractDetails) {
      const standardScore = calculateBridgeScore(contractDetails);
      
      // Update current deal with standard scores
      const currentDealWithScores = {
        ...gameState.currentDeal,
        nsPoints: standardScore.nsPoints || 0,
        ewPoints: standardScore.ewPoints || 0,
        rawScore: Math.max(
          Math.abs(standardScore.nsPoints || 0), 
          Math.abs(standardScore.ewPoints || 0)
        ),
        bonusScoringEnabled: false
      };
      
      // Calculate running totals
      const nsTotal = (gameState.scores.nsTotal || 0) + (standardScore.nsPoints || 0);
      const ewTotal = (gameState.scores.ewTotal || 0) + (standardScore.ewPoints || 0);
      
      // Update game state with scores
      setGameState(prevState => {
        return {
          ...prevState,
          currentDeal: currentDealWithScores,
          scores: {
            ...prevState.scores,
            nsPoints: standardScore.nsPoints || 0,
            ewPoints: standardScore.ewPoints || 0
          }
        };
      });
      
      // Proceed to next deal
      handleNextDeal();
    }
  };
  
  // Determine which view to show
  const renderCurrentView = () => {
    if (gameState.gameEnded) {
      return (
        <GameSummary 
          gameState={gameState}
          onNewGame={handleReturnToGame}
          onGoToHome={handleReturnToGame}
        />
      );
    }
    
    switch(currentView) {
      case 'adjustment':
        return (
          <ScoreAdjustment 
            currentDeal={gameState.currentDeal}
            onSaveAdjustment={handleSaveAdjustment}
            onCancel={() => setCurrentView('game')}
          />
        );
      case 'analysis':
        return (
          <FinalScoreAnalysis 
            analysisData={gameState.currentDeal.handAnalysis}
            currentDeal={gameState.currentDeal}
            onSave={handleSaveAnalysis}
            onEdit={() => setCurrentView('adjustment')}
          />
        );
      case 'game':
      default:
        return (
          <>
            <ScoreProcessor 
              currentDeal={gameState.currentDeal}
              onScoreCalculated={handleScoreCalculated}
            />
            <BridgeGameUI 
              gameState={gameState}
              updateCurrentDeal={updateCurrentDeal}
              onNextDeal={handleNextDeal}
              onNewGame={handleNewGame}
              setGameState={setGameState}
              onChooseBonusBridge={handleChooseBonusBridge}
              onChoosePartyBridge={handleChoosePartyBridge}
            />
          </>
        );
    }
  };
  
  return (
    <div className="app-container">
      {renderCurrentView()}
    </div>
  );
};

export default App;
