import React, { useState, useEffect } from 'react';
import './ContractInput.css';

const ContractInput = ({ onContractChange, initialContract = '' }) => {
  // State for levels (1-7)
  const [level, setLevel] = useState('');
  // State for suits (♣, ♦, ♥, ♠, NT)
  const [suit, setSuit] = useState('');
  // State for declarer (N, E, S, W)
  const [declarer, setDeclarer] = useState('');
  // State for doubled status
  const [doubledState, setDoubledState] = useState('');
  
  // Parse initialContract if provided
  useEffect(() => {
    if (initialContract) {
      const contractMatch = initialContract.match(/(\d)([♣♦♥♠]|NT)\s+([NESW])(X{0,2})/);
      if (contractMatch) {
        setLevel(contractMatch[1]);
        setSuit(contractMatch[2]);
        setDeclarer(contractMatch[3]);
        setDoubledState(contractMatch[4] || '');
      }
    }
  }, [initialContract]);
  
  // Update contract when any component changes
  useEffect(() => {
    if (level && suit && declarer) {
      const newContract = `${level}${suit} ${declarer}${doubledState}`;
      onContractChange(newContract);
    }
  }, [level, suit, declarer, doubledState, onContractChange]);
  
  // Handle level selection
  const handleLevelClick = (selectedLevel) => {
    setLevel(selectedLevel);
  };
  
  // Handle suit selection
  const handleSuitClick = (selectedSuit) => {
    setSuit(selectedSuit);
  };
  
  // Handle declarer selection
  const handleDeclarerClick = (selectedDeclarer) => {
    setDeclarer(selectedDeclarer);
  };
  
  // Create styled doubled section (complete replacement)
  const createDoubledSection = () => {
    // Custom styled section separate from CSS
    return (
      <div style={{ marginBottom: '15px' }}>
        <div style={{ 
          fontWeight: 'bold',
          marginBottom: '10px',
          fontSize: '16px',
          color: '#1e5c8b'
        }}>
          Doubled:
        </div>
        
        <div style={{ display: 'flex', gap: '8px' }}>
          {/* Custom button with direct styling */}
          <div 
            onClick={() => setDoubledState('')}
            style={{
              flex: 1,
              height: '54px',
              border: doubledState === '' ? '2px solid #3498db' : '1px solid #ddd',
              backgroundColor: doubledState === '' ? '#3498db' : '#f8f8f8',
              color: doubledState === '' ? 'white' : 'black',
              borderRadius: '8px',
              fontSize: '20px',
              fontWeight: 'bold',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              userSelect: 'none'
            }}
          >
            -
          </div>
          
          <div 
            onClick={() => setDoubledState('X')}
            style={{
              flex: 1,
              height: '54px',
              border: doubledState === 'X' ? '2px solid #3498db' : '1px solid #ddd',
              backgroundColor: doubledState === 'X' ? '#3498db' : '#f8f8f8',
              color: doubledState === 'X' ? 'white' : 'black',
              borderRadius: '8px',
              fontSize: '20px',
              fontWeight: 'bold',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              userSelect: 'none'
            }}
          >
            X
          </div>
          
          <div 
            onClick={() => setDoubledState('XX')}
            style={{
              flex: 1,
              height: '54px',
              border: doubledState === 'XX' ? '2px solid #3498db' : '1px solid #ddd',
              backgroundColor: doubledState === 'XX' ? '#3498db' : '#f8f8f8',
              color: doubledState === 'XX' ? 'white' : 'black',
              borderRadius: '8px',
              fontSize: '20px',
              fontWeight: 'bold',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              userSelect: 'none'
            }}
          >
            XX
          </div>
        </div>
      </div>
    );
  };
  
  return (
    <div className="contract-input-container">
      {/* Level Input */}
      <div className="level-suit-section">
        <div className="input-section-label">Level and Suit:</div>
        
        {/* Level Buttons (1-7) */}
        <div className="level-buttons">
          {[1, 2, 3, 4, 5, 6, 7].map((num) => (
            <button
              key={`level-${num}`}
              className={`level-btn ${level === num.toString() ? 'selected' : ''}`}
              onClick={() => handleLevelClick(num.toString())}
              data-level={num}
            >
              {num}
            </button>
          ))}
        </div>
        
        {/* Suit Buttons */}
        <div className="suit-buttons">
          {['♣', '♦', '♥', '♠', 'NT'].map((s) => (
            <button
              key={`suit-${s}`}
              className={`suit-btn ${suit === s ? 'selected' : ''}`}
              onClick={() => handleSuitClick(s)}
              data-suit={s}
            >
              {s}
            </button>
          ))}
        </div>
      </div>
      
      {/* Declarer Input */}
      <div className="declarer-section">
        <div className="input-section-label">Declarer:</div>
        <div className="declarer-buttons">
          {['N', 'S', 'E', 'W'].map((d) => (
            <button
              key={`declarer-${d}`}
              className={`declarer-btn ${declarer === d ? 'selected' : ''}`}
              onClick={() => handleDeclarerClick(d)}
            >
              {d}
            </button>
          ))}
        </div>
      </div>
      
      {/* Custom doubled section using div instead of buttons */}
      {createDoubledSection()}
    </div>
  );
};

export default ContractInput;
