import React, { useState, useEffect } from 'react';
import ScoreTracking from './components/scoreTracking';
import ContractInput from './components/ContractInput';
import TrickInput from './components/TrickInput';
import LoadingSpinner from './components/LoadingSpinner';
import { 
  requestWakeLock, 
  releaseWakeLock, 
  isWakeLockSupported 
} from './utils/wakeLock';
import { 
  vulnerabilityDescription, 
  calculateBridgeScore,
  parseContract 
} from './utils/scoring';
import './global.css';

const BridgeGameUI = ({ gameState, updateCurrentDeal, onNextDeal, onNewGame, setGameState }) => {
  // Function to keep the device awake
  const toggleKeepAwake = async () => {
    const newAwakeState = !gameState.isAwake;
    
    // Update state first for immediate UI feedback
    setGameState(prev => ({
      ...prev,
      isAwake: newAwakeState
    }));
    
    if (newAwakeState) {
      // Turn on wake lock
      if (isWakeLockSupported()) {
        const success = await requestWakeLock();
        if (!success) {
          // Wake lock request failed, revert state
          setGameState(prev => ({
            ...prev,
            isAwake: false
          }));
        }
      } else {
        console.log('Wake Lock API not supported in this browser');
      }
    } else {
      // Turn off wake lock
      await releaseWakeLock();
    }
  };

  // Show score popup
  const showScorePopup = () => {
    setGameState(prev => ({
      ...prev,
      showScorePopup: true
    }));
  };

  // Hide score popup
  const hideScorePopup = () => {
    setGameState(prev => ({
      ...prev,
      showScorePopup: false
    }));
  };

  // Handle contract input changes
  const handleContractChange = (contract) => {
    updateCurrentDeal({ contract });
  };

  // Handle trick input changes
  const handleTrickChange = (result, tricks) => {
    updateCurrentDeal({ result, tricks });
  };
  
  // Determine dealer based on deal number
  const determineDealer = (dealNumber) => {
    const dealerPattern = (dealNumber - 1) % 4;
    
    switch (dealerPattern) {
      case 0:
        return "North";
      case 1:
        return "East";
      case 2:
        return "South";
      case 3:
        return "West";
      default:
        return "North";
    }
  };
  
  // Get a full description of the contract and result
  const getFullContractDescription = (currentDeal) => {
    if (!currentDeal || !currentDeal.contract) return "";
    
    const contractMatch = currentDeal.contract.match(/(\d)([♣♦♥♠]|NT)\s+([NESW])(X{0,2})/);
    if (!contractMatch) return "";
    
    const level = contractMatch[1];
    const suit = contractMatch[2];
    const declarer = contractMatch[3];
    const doubled = contractMatch[4];
    
    // Determine declarer's full name
    let declarerName = "";
    switch (declarer) {
      case 'N': declarerName = "North"; break;
      case 'E': declarerName = "East"; break;
      case 'S': declarerName = "South"; break;
      case 'W': declarerName = "West"; break;
    }
    
    // Calculate required tricks
    const requiredTricks = parseInt(level) + 6;
    
    // Calculate actual tricks
    let actualTricks = requiredTricks + (currentDeal.result || 0);
    
    // Determine if the contract was made or went down
    const madeContract = currentDeal.result >= 0;
    
    // Get vulnerability text
    const vulText = vulnerabilityDescription(currentDeal.vulnerable).toLowerCase().replace(" vulnerable", "");
    
    // Determine which side scores
    const isNS = declarer === 'N' || declarer === 'S';
    const scoringSide = isNS ? 'NS' : 'EW';
    
    // Calculate standard bridge score
    const score = calculateBridgeScore(
      parseContract(currentDeal.contract, currentDeal.result, currentDeal.vulnerable)
    );
    const scoreValue = isNS ? score.nsPoints : score.ewPoints;
    const scoreDisplay = scoreValue > 0 ? scoreValue : -scoreValue;
    const scoringText = scoreValue > 0 ? 
      `${scoreDisplay} to ${scoringSide}` : 
      `${scoreDisplay} to ${isNS ? 'EW' : 'NS'}`;
    
    // Format doubling
    const doubleText = doubled === 'X' ? ' doubled' : doubled === 'XX' ? ' redoubled' : '';
    
    // Create the full description
    const description = `${level}${suit} by ${declarerName}${doubleText} (${vulText}), ` + 
      `made ${actualTricks} tricks` + 
      (madeContract ? 
        (currentDeal.result > 0 ? ` (+${currentDeal.result})` : ` (=)`) : 
        ` (down ${Math.abs(currentDeal.result)})`) + 
      `. Score: ${scoringText}`;
    
    return description;
  };

  return (
    <div className="bridge-game-container">
      <header className="game-header">
        <div className="game-info-left">
          <span>Game #{gameState.gameNumber}</span>
        </div>
        <div className="game-title">
          <h1>Bonus Bridge</h1>
        </div>
        <div className="game-info-right">
          <span>Deal #{gameState.dealNumber}</span>
        </div>
        <div className="vulnerability-info">
          {vulnerabilityDescription(gameState.currentDeal.vulnerable)}
        </div>
      </header>
      
      <div className="game-board">
        <div className="player-position">
          Dealer: {determineDealer(gameState.dealNumber)}
        </div>
        
        <div className="contract-input-container">
          <ContractInput 
            onContractChange={handleContractChange}
            initialContract={gameState.currentDeal.contract}
          />
        </div>
        
        <div className="trick-input-container">
          <div className="tricks-label">Tricks Taken:</div>
          <TrickInput 
            onTrickChange={handleTrickChange}
            initialResult={gameState.currentDeal.result}
            contract={gameState.currentDeal.contract}
          />
        </div>
        
        <div className="result-display">
          {gameState.currentDeal.contract && gameState.currentDeal.result !== null ? 
            getFullContractDescription(gameState.currentDeal)
            : ""}
        </div>
      </div>
      
      <footer className="game-controls">
        <div className="keep-awake-container">
          <button 
            className={`keep-awake-btn ${gameState.isAwake ? 'active' : ''}`}
            onClick={toggleKeepAwake}
          >
            Keep Awake: {gameState.isAwake ? 'ON' : 'OFF'}
          </button>
          
          <button 
            className="score-btn"
            onClick={showScorePopup}
          >
            Score
          </button>
        </div>
        
        <div className="game-actions">
          <button 
            className="next-deal-btn"
            onClick={onNextDeal}
            disabled={!gameState.currentDeal.contract || gameState.currentDeal.result === null}
          >
            Next Deal
          </button>
          
          <button 
            className="new-game-btn"
            onClick={onNewGame}
          >
            New Game
          </button>
        </div>
      </footer>
      
      {gameState.showScorePopup && (
        <div className="score-popup">
          <div className="score-popup-content">
            <h2>Current Scores</h2>
            <ScoreTracking 
              scores={gameState.scores} 
              currentDeal={gameState.currentDeal}
            />
            <button className="close-btn" onClick={hideScorePopup}>
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default BridgeGameUI;
