// File: src/components/ScoreAdjustment.js
import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';
import '../global.css';

const ScoreAdjustment = () => {
  const { gameState, dispatch } = useContext(GameContext);
  const { rawScore, adjustedScore, scoringMethod } = gameState.currentDeal;

  // Handle scoring method change
  const handleScoringMethodChange = (method) => {
    dispatch({
      type: 'SET_SCORING_METHOD',
      payload: method
    });
  };

  // Handle bonus adjustment change
  const handleBonusChange = (e) => {
    const bonus = parseInt(e.target.value) || 0;
    dispatch({
      type: 'SET_BONUS',
      payload: bonus
    });
  };

  // Calculate the defender bonus based on the contract outcome
  const calculateDefenderBonus = () => {
    const { contract, tricksRequired, tricksMade } = gameState.currentDeal;
    
    if (!contract || tricksRequired === null || tricksMade === null) {
      return 0;
    }

    // If contract was made, no bonus for defenders
    if (tricksMade >= tricksRequired) {
      return 0;
    }

    // Bonus scaling based on how difficult the contract was to defeat
    const contractLevel = parseInt(contract.charAt(0));
    const difference = tricksRequired - tricksMade;
    
    // Base formula: higher contracts give more points when defeated
    return contractLevel * 10 * difference;
  };

  // Helper function to display the adjusted score breakdown
  const renderScoreBreakdown = () => {
    if (rawScore === null) return null;
    
    const defenderBonus = calculateDefenderBonus();
    const userBonus = gameState.currentDeal.bonus || 0;
    
    return (
      <div className="score-breakdown">
        <div>Raw Score: {rawScore}</div>
        {defenderBonus > 0 && (
          <div>Defender Bonus: +{defenderBonus}</div>
        )}
        {userBonus !== 0 && (
          <div>Manual Adjustment: {userBonus > 0 ? '+' : ''}{userBonus}</div>
        )}
        <div className="total-score">Total: {adjustedScore}</div>
      </div>
    );
  };

  return (
    <div className="component-container">
      <h2>Score Adjustment</h2>
      
      <div className="scoring-options">
        <div className="scoring-method">
          <div className="radio-group">
            <input
              type="radio"
              id="standard"
              name="scoring-method"
              value="standard"
              checked={scoringMethod === 'standard'}
              onChange={() => handleScoringMethodChange('standard')}
            />
            <label htmlFor="standard">Standard Scoring</label>
          </div>
          
          <div className="radio-group">
            <input
              type="radio"
              id="enhanced"
              name="scoring-method"
              value="enhanced"
              checked={scoringMethod === 'enhanced'}
              onChange={() => handleScoringMethodChange('enhanced')}
            />
            <label htmlFor="enhanced">Enhanced Scoring</label>
            <div className="helper-text">Includes extra rewards for defenders</div>
          </div>
        </div>
        
        <div className="manual-adjustment">
          <label htmlFor="bonus-adjustment">Manual Adjustment:</label>
          <input
            type="number"
            id="bonus-adjustment"
            value={gameState.currentDeal.bonus || 0}
            onChange={handleBonusChange}
            className="input-number"
          />
        </div>
      </div>
      
      {renderScoreBreakdown()}
    </div>
  );
};

export default ScoreAdjustment;
