// TrialPopup.js - Complete version with 7-digit code input and validation
import React, { useState } from 'react';
import './TrialPopup.css';

const TrialPopup = ({ 
  trialManager, 
  onClose,
  onExtended,
  type = 'warning' // 'warning', 'expired', or 'info'
}) => {
  const [showExtensionForm, setShowExtensionForm] = useState(false);
  const [extensionCode, setExtensionCode] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [codeError, setCodeError] = useState('');

  // Get trial statistics
  const stats = trialManager.getStats();
  const remainingDeals = trialManager.getRemainingDeals();

  // Handle sending feedback email
  const handleSendFeedback = () => {
    const emailData = trialManager.generateFeedbackEmail();
    if (emailData) {
      const mailtoLink = `mailto:mike.smith@yourdomain.com?subject=${emailData.subject}&body=${emailData.body}`;
      window.open(mailtoLink);
    }
  };

  // Format extension code input (add spaces for readability)
  const formatCode = (value) => {
    // Remove all non-digits
    const digits = value.replace(/\D/g, '');
    // Limit to 7 digits
    const limited = digits.substring(0, 7);
    // Add spaces: XXX XXXX
    if (limited.length > 3) {
      return limited.substring(0, 3) + ' ' + limited.substring(3);
    }
    return limited;
  };

  // Handle code input change
  const handleCodeChange = (e) => {
    const formatted = formatCode(e.target.value);
    setExtensionCode(formatted);
    setCodeError(''); // Clear error when user types
  };

  // Validate code format in real-time
  const validateCode = (code) => {
    const cleanCode = code.replace(/\s/g, '');
    if (cleanCode.length !== 7) return 'Code must be exactly 7 digits';
    if (!/^\d{7}$/.test(cleanCode)) return 'Code must contain only numbers';
    
    // Check sum of first 6 digits
    const firstSix = cleanCode.substring(0, 6);
    const sum = firstSix.split('').reduce((acc, digit) => acc + parseInt(digit), 0);
    if (sum !== 100) return 'Invalid code format';
    
    const lastDigit = parseInt(cleanCode.substring(6, 7));
    if (lastDigit < 1 || lastDigit > 9) return 'Invalid code format';
    
    return null;
  };

  // Handle extension code submission
  const handleExtensionSubmit = async (e) => {
    e.preventDefault();
    
    // Validate format first
    const validationError = validateCode(extensionCode);
    if (validationError) {
      setCodeError(validationError);
      return;
    }
    
    setIsSubmitting(true);
    setCodeError('');

    try {
      // Remove spaces and submit to trial manager
      const result = trialManager.extendTrial(extensionCode.replace(/\s/g, ''));
      
      if (result.success) {
        // Success message with details
        const message = `Success! ${result.message}\n\nDeals added: ${result.dealsAdded}\nTotal deals: ${result.totalDeals}\nRemaining: ${result.remainingDeals}`;
        alert(message);
        onExtended && onExtended();
        setShowExtensionForm(false);
        setExtensionCode('');
        // If it's not just a warning, close the popup completely
        if (type !== 'warning') onClose();
      } else {
        // Show error from trial manager (includes "already used" message)
        setCodeError(result.message);
      }
    } catch (error) {
      setCodeError('An error occurred while processing the extension code.');
    } finally {
      setIsSubmitting(false);
    }
  };

  // Get popup content based on type
  const getContent = () => {
    if (type === 'expired') {
      return {
        title: '‚è∞ Trial Period Expired',
        message: `Your evaluation period has ended after ${stats.dealsPlayed} deals. Thank you for testing Bonus Bridge!`,
        emoji: 'üö´',
        color: 'red'
      };
    } else if (type === 'warning') {
      return {
        title: '‚ö†Ô∏è Trial Nearly Complete',
        message: `You have ${remainingDeals} deal${remainingDeals === 1 ? '' : 's'} remaining in your evaluation period.`,
        emoji: '‚ö†Ô∏è',
        color: 'orange'
      };
    } else {
      return {
        title: 'üéØ Welcome to Bonus Bridge',
        message: `You have ${remainingDeals} deals to evaluate the application.`,
        emoji: 'üéØ',
        color: 'blue'
      };
    }
  };

  const content = getContent();

  return (
    <div className="trial-popup-overlay">
      <div className={`trial-popup trial-popup-${content.color}`}>
        {/* Header */}
        <div className="trial-popup-header">
          <h2>{content.title}</h2>
          {type !== 'expired' && (
            <button className="close-btn" onClick={onClose}>√ó</button>
          )}
        </div>

        {/* Content */}
        <div className="trial-popup-content">
          <div className="trial-message">
            <span className="trial-emoji">{content.emoji}</span>
            <p>{content.message}</p>
          </div>

          {/* Usage Statistics */}
          {stats && (
            <div className="trial-stats">
              <h3>üìä Your Usage</h3>
              <div className="stats-grid">
                <div className="stat-item">
                  <span className="stat-label">Deals Played:</span>
                  <span className="stat-value">{stats.dealsPlayed}/{stats.maxDeals}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Remaining:</span>
                  <span className="stat-value">{stats.remainingDeals}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Games Completed:</span>
                  <span className="stat-value">{stats.gamesCompleted}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Days Used:</span>
                  <span className="stat-value">{stats.daysUsed}</span>
                </div>
                {stats.extensions.length > 0 && (
                  <div className="stat-item">
                    <span className="stat-label">Extensions Used:</span>
                    <span className="stat-value">{stats.extensions.length}</span>
                  </div>
                )}
              </div>
            </div>
          )}

          {/* Action Buttons */}
          <div className="trial-actions">
            {(type === 'warning' || type === 'expired') && (
              <>
                <button 
                  className="feedback-btn primary"
                  onClick={handleSendFeedback}
                >
                  üìß Request Extension Code
                </button>

                <button 
                  className="extension-btn secondary"
                  onClick={() => setShowExtensionForm(!showExtensionForm)}
                >
                  üîë Enter 7-Digit Extension Code
                </button>
              </>
            )}

            {type === 'info' && (
              <button className="continue-btn primary" onClick={onClose}>
                Start Evaluating
              </button>
            )}

            {type === 'warning' && (
              <button className="continue-btn secondary" onClick={onClose}>
                Continue Playing
              </button>
            )}
          </div>

          {/* Extension Form */}
          {showExtensionForm && (
            <div className="extension-form">
              <h3>üîë Enter 7-Digit Extension Code</h3>
              <p style={{ fontSize: '0.9rem', color: '#666', marginBottom: '15px' }}>
                Enter the 7-digit code you received from Mike Smith.
              </p>
              
              <form onSubmit={handleExtensionSubmit}>
                <div className="form-group">
                  <label htmlFor="code">Extension Code:</label>
                  <input
                    type="text"
                    id="code"
                    value={extensionCode}
                    onChange={handleCodeChange}
                    required
                    placeholder="XXX XXXX"
                    maxLength="8" // 7 digits + 1 space
                    style={{ 
                      fontSize: '1.2rem', 
                      letterSpacing: '0.1rem',
                      textAlign: 'center',
                      fontFamily: 'monospace'
                    }}
                  />
                  <div style={{ fontSize: '0.8rem', color: '#666', marginTop: '5px' }}>
                    Format: 7 digits (first 6 must sum to 100)
                  </div>
                  {codeError && (
                    <div style={{ color: '#f44336', fontSize: '0.9rem', marginTop: '5px' }}>
                      {codeError}
                    </div>
                  )}
                </div>
                
                <div className="form-actions">
                  <button 
                    type="button" 
                    className="cancel-btn"
                    onClick={() => {
                      setShowExtensionForm(false);
                      setExtensionCode('');
                      setCodeError('');
                    }}
                  >
                    Cancel
                  </button>
                  <button 
                    type="submit" 
                    className="submit-btn"
                    disabled={isSubmitting || !!validateCode(extensionCode)}
                  >
                    {isSubmitting ? 'Validating...' : 'Apply Code'}
                  </button>
                </div>
              </form>
              
              {/* Code format help */}
              <div style={{ 
                marginTop: '15px', 
                padding: '10px', 
                backgroundColor: '#f0f8ff', 
                borderRadius: '6px',
                fontSize: '0.85rem',
                color: '#1e5c8b'
              }}>
                <strong>How it works:</strong><br/>
                ‚Ä¢ Your 7-digit code grants additional deals<br/>
                ‚Ä¢ Last digit indicates package size (1=100 deals, 2=200 deals, etc.)<br/>
                ‚Ä¢ Each code can only be used once<br/>
                ‚Ä¢ First 6 digits must sum to exactly 100
              </div>
            </div>
          )}

          {/* Extension History (if any) */}
          {stats && stats.extensions.length > 0 && (
            <div style={{ 
              marginTop: '15px', 
              padding: '10px', 
              backgroundColor: '#f8f9fa', 
              borderRadius: '6px',
              fontSize: '0.85rem'
            }}>
              <strong>Previous Extensions:</strong>
              {stats.extensions.map((ext, index) => (
                <div key={index} style={{ marginTop: '5px' }}>
                  ‚Ä¢ {ext.dealsGranted} deals added on {new Date(ext.appliedDate).toLocaleDateString()}
                </div>
              ))}
            </div>
          )}

          {/* Expired Message */}
          {type === 'expired' && (
            <div className="expired-note">
              <p><strong>Note:</strong> You can still view previous games, but cannot start new deals without an extension code.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default TrialPopup;
