import React, { useEffect, useRef } from 'react';

// A radical approach that uses React minimally and manipulates the DOM directly
const ContractInput = ({ onContractChange, initialContract = '' }) => {
  // Create refs for the container
  const containerRef = useRef(null);
  
  // Parse initialContract
  let initialLevel = '';
  let initialSuit = '';
  let initialDeclarer = '';
  let initialDoubled = '';
  
  if (initialContract) {
    const contractMatch = initialContract.match(/(\d)([♣♦♥♠]|NT)\s+([NESW])(X{0,2})/);
    if (contractMatch) {
      initialLevel = contractMatch[1];
      initialSuit = contractMatch[2];
      initialDeclarer = contractMatch[3];
      initialDoubled = contractMatch[4] || '';
    }
  }
  
  // Direct DOM manipulation effect
  useEffect(() => {
    // Get current state values
    let level = initialLevel;
    let suit = initialSuit;
    let declarer = initialDeclarer;
    let doubled = initialDoubled;
    
    // Create utility to update contract
    const updateContract = () => {
      if (level && suit && declarer) {
        const newContract = `${level}${suit} ${declarer}${doubled}`;
        onContractChange(newContract);
      }
    };
    
    // Create utility to create a button
    const createButton = (text, value, currentValue, setter, customStyle = {}) => {
      const btn = document.createElement('button');
      btn.textContent = text;
      btn.style.flex = '1';
      btn.style.height = '40px';
      btn.style.border = '1px solid #ddd';
      btn.style.borderRadius = '6px';
      btn.style.fontSize = '16px';
      btn.style.cursor = 'pointer';
      btn.style.display = 'flex';
      btn.style.alignItems = 'center';
      btn.style.justifyContent = 'center';
      
      // Apply custom styles
      Object.keys(customStyle).forEach(key => {
        btn.style[key] = customStyle[key];
      });
      
      // Set initial selected state
      if (currentValue === value) {
        btn.style.backgroundColor = '#3498db';
        btn.style.color = 'white';
        btn.style.borderColor = '#3498db';
        btn.style.fontWeight = 'bold';
      } else {
        btn.style.backgroundColor = '#f8f8f8';
        btn.style.color = customStyle.color || 'black';
      }
      
      // Add click handler
      btn.addEventListener('click', () => {
        // Update state
        setter(value);
        
        // Update UI
        btn.parentElement.querySelectorAll('button').forEach(b => {
          b.style.backgroundColor = '#f8f8f8';
          b.style.color = b.getAttribute('data-color') || 'black';
          b.style.borderColor = '#ddd';
          b.style.fontWeight = 'normal';
        });
        
        btn.style.backgroundColor = '#3498db';
        btn.style.color = 'white';
        btn.style.borderColor = '#3498db';
        btn.style.fontWeight = 'bold';
        
        // Update contract
        updateContract();
      });
      
      // Store original color for reset
      if (customStyle.color) {
        btn.setAttribute('data-color', customStyle.color);
      }
      
      return btn;
    };
    
    // Create a section with label
    const createSection = (labelText) => {
      const section = document.createElement('div');
      section.style.marginBottom = '10px';
      
      const label = document.createElement('div');
      label.textContent = labelText;
      label.style.fontWeight = 'bold';
      label.style.marginBottom = '5px';
      label.style.fontSize = '14px';
      label.style.color = '#1e5c8b';
      
      section.appendChild(label);
      return section;
    };
    
    // Create a button row
    const createButtonRow = () => {
      const row = document.createElement('div');
      row.style.display = 'flex';
      row.style.gap = '6px';
      row.style.marginBottom = '10px';
      row.style.width = '100%';
      return row;
    };
    
    // Create setter functions
    const setLevel = (newLevel) => {
      level = newLevel;
    };
    
    const setSuit = (newSuit) => {
      suit = newSuit;
    };
    
    const setDeclarer = (newDeclarer) => {
      declarer = newDeclarer;
    };
    
    const setDoubled = (newDoubled) => {
      doubled = newDoubled;
    };
    
    // Clear and build the container
    if (containerRef.current) {
      // Clear existing content
      containerRef.current.innerHTML = '';
      
      // Set container style
      containerRef.current.style.padding = '10px 0';
      containerRef.current.style.maxWidth = '100%';
      containerRef.current.style.boxSizing = 'border-box';
      containerRef.current.style.maxHeight = '250px';
      containerRef.current.style.overflow = 'hidden';
      
      // Create level and suit section
      const levelSuitSection = createSection('Level and Suit:');
      
      // Create level buttons
      const levelRow = createButtonRow();
      [1, 2, 3, 4, 5, 6, 7].forEach(num => {
        levelRow.appendChild(createButton(num, num.toString(), level, setLevel));
      });
      levelSuitSection.appendChild(levelRow);
      
      // Create suit buttons
      const suitRow = createButtonRow();
      const suitStyles = {
        '♣': { fontSize: '24px' },
        '♦': { fontSize: '24px', color: '#e74c3c' },
        '♥': { fontSize: '24px', color: '#e74c3c' },
        '♠': { fontSize: '24px' },
        'NT': { fontSize: '20px' }
      };
      
      ['♣', '♦', '♥', '♠', 'NT'].forEach(s => {
        suitRow.appendChild(createButton(s, s, suit, setSuit, suitStyles[s]));
      });
      levelSuitSection.appendChild(suitRow);
      
      // Create declarer section
      const declarerSection = createSection('Declarer:');
      const declarerRow = createButtonRow();
      ['N', 'S', 'E', 'W'].forEach(d => {
        declarerRow.appendChild(createButton(d, d, declarer, setDeclarer));
      });
      declarerSection.appendChild(declarerRow);
      
      // Create doubled section
      const doubledSection = createSection('Doubled:');
      const doubledRow = createButtonRow();
      
      // Special buttons for doubled
      const noneBtn = createButton('-', '', doubled, setDoubled);
      const xBtn = createButton('X', 'X', doubled, setDoubled);
      const xxBtn = createButton('XX', 'XX', doubled, setDoubled);
      
      doubledRow.appendChild(noneBtn);
      doubledRow.appendChild(xBtn);
      doubledRow.appendChild(xxBtn);
      doubledSection.appendChild(doubledRow);
      
      // Add all sections to container
      containerRef.current.appendChild(levelSuitSection);
      containerRef.current.appendChild(declarerSection);
      containerRef.current.appendChild(doubledSection);
    }
  }, [initialContract, onContractChange]);
  
  return <div ref={containerRef}></div>;
};

export default ContractInput;
