import React, { useState, useEffect } from 'react';
import '../styles/scoreAdjustment.css';

const ScoreAdjustment = ({ currentDeal, onSaveAdjustment, onCancel }) => {
  // State for HCP and distribution
  const [totalHCP, setTotalHCP] = useState(20); // Default to 20 HCP
  const [singletons, setSingletons] = useState(0);
  const [voids, setVoids] = useState(0);
  const [longSuits, setLongSuits] = useState(0);
  const [showExplanation, setShowExplanation] = useState(false);
  
  // Get contract details for display
  const getContractDetails = () => {
    if (!currentDeal || !currentDeal.contract) return { level: 0, suit: '', declarer: '' };
    
    const match = currentDeal.contract.match(/(\d)([♣♦♥♠]|NT)\s+([NESW])(X{0,2})/);
    if (!match) return { level: 0, suit: '', declarer: '' };
    
    const level = parseInt(match[1]);
    const suit = match[2];
    const declarer = match[3];
    const doubled = match[4];
    
    // Get full declarer name
    let declarerName = "";
    switch (declarer) {
      case 'N': declarerName = "North"; break;
      case 'E': declarerName = "East"; break;
      case 'S': declarerName = "South"; break;
      case 'W': declarerName = "West"; break;
      default: declarerName = declarer;
    }
    
    return {
      level,
      suit,
      declarer: declarerName,
      doubled: doubled || '',
      requiredTricks: level + 6,
      actualTricks: (level + 6) + (currentDeal.result || 0),
      madeContract: currentDeal.result >= 0,
      contractResult: currentDeal.result || 0,
      rawScore: 420 // Placeholder - we'll need to calculate this from the contract details
    };
  };
  
  const contractDetails = getContractDetails();
  
  // Format contract for display
  const getContractSummary = () => {
    const details = contractDetails;
    const downCount = Math.abs(details.contractResult);
    const madeStr = details.madeContract ? 
      (details.contractResult > 0 ? `Made +${details.contractResult}` : "Made") :
      `Down ${downCount}`;
      
    const doubledStr = details.doubled ? ` ${details.doubled}` : '';
    
    return `${details.level}${details.suit} by ${details.declarer}${doubledStr} ${madeStr}`;
  };
  
  // Calculate expected tricks based on HCP
  const calculateExpectedTricks = () => {
    // Simple formula: 6 + (totalHCP - 20) / 3
    const hcpAbove20 = Math.max(0, totalHCP - 20);
    return Math.min(13, 6 + Math.floor(hcpAbove20 / 3));
  };
  
  // Calculate expected tricks with distribution points
  const calculateExpectedWithDistribution = () => {
    const distributionPoints = singletons * 1 + voids * 3 + longSuits * 1;
    return Math.min(13, calculateExpectedTricks() + Math.floor(distributionPoints / 4));
  };
  
  // Calculate adjustment percentage
  const calculateAdjustment = () => {
    const expectedTricks = calculateExpectedWithDistribution();
    const actualTricks = contractDetails.actualTricks;
    
    // Base adjustment based on HCP
    let adjustmentPercent = 100; // Start at 100%
    
    // If HCP is very strong (26+), reduce adjustment
    if (totalHCP >= 26) {
      adjustmentPercent -= (totalHCP - 25) * 5; // -5% per HCP above 25
    }
    
    // If HCP is weak (under 20), increase adjustment
    if (totalHCP < 20) {
      adjustmentPercent += (20 - totalHCP) * 10; // +10% per HCP below 20
    }
    
    // Performance adjustment
    if (contractDetails.madeContract) {
      // Exceeded expected tricks (good performance)
      if (actualTricks > expectedTricks) {
        adjustmentPercent += (actualTricks - expectedTricks) * 15; // +15% per trick above expected
      }
    } else {
      // Failed contract
      adjustmentPercent = Math.max(50, adjustmentPercent - 30); // At least 50% reduction for failed contracts
    }
    
    // Cap adjustments
    return Math.max(50, Math.min(200, adjustmentPercent));
  };
  
  // Calculate adjusted score
  const calculateAdjustedScore = () => {
    const adjustmentPercent = calculateAdjustment();
    return Math.round((contractDetails.rawScore * adjustmentPercent) / 100);
  };
  
  // Handle go to analysis
  const handleAnalyze = () => {
    const adjustmentData = {
      dealId: currentDeal.id,
      totalHCP,
      singletons,
      voids,
      longSuits,
      expectedTricks: calculateExpectedWithDistribution(),
      adjustmentPercent: calculateAdjustment(),
      preliminaryScore: calculateAdjustedScore()
    };
    
    onSaveAdjustment(adjustmentData);
  };
  
  // Inline styles to ensure they work
  const styles = {
    container: {
      border: '2px solid #2c3e50',
      borderRadius: '10px',
      overflow: 'hidden',
      margin: '10px',
      backgroundColor: '#f5f5f5',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
      height: 'calc(100% - 20px)',
      display: 'flex',
      flexDirection: 'column'
    },
    header: {
      backgroundColor: '#1e5c8b',
      color: 'white',
      padding: '20px',
      textAlign: 'center'
    },
    headerTitle: {
      fontSize: '24px',
      fontWeight: 'bold',
      margin: 0
    },
    instruction: {
      fontSize: '18px',
      margin: '10px 0 0'
    },
    subHeader: {
      backgroundColor: '#1e5c8b',
      color: 'white',
      padding: '15px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center'
    },
    subHeaderTitle: {
      fontSize: '20px',
      fontWeight: 'bold',
      margin: 0
    },
    whatIsThisBtn: {
      backgroundColor: '#468bbf',
      color: 'white',
      border: 'none',
      borderRadius: '5px',
      padding: '8px 12px',
      fontSize: '16px',
      cursor: 'pointer'
    },
    contractSection: {
      padding: '15px',
      backgroundColor: '#e8f4ff',
      textAlign: 'center',
      borderBottom: '1px solid #d0d0d0'
    },
    contractText: {
      fontSize: '18px',
      fontWeight: 'bold',
      margin: '0 0 5px'
    },
    rawScoreText: {
      fontSize: '16px',
      margin: 0
    },
    contentArea: {
      flex: 1,
      padding: '15px',
      overflow: 'auto'
    },
    inputSection: {
      backgroundColor: 'white',
      border: '1px solid #d0d0d0',
      borderRadius: '8px',
      padding: '15px',
      marginBottom: '15px'
    },
    sectionTitle: {
      fontSize: '18px',
      fontWeight: 'bold',
      textAlign: 'center',
      margin: '0 0 15px',
      color: '#1e5c8b'
    },
    numberControl: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '15px'
    },
    numberBtn: {
      width: '60px',
      height: '60px',
      backgroundColor: '#1e5c8b',
      color: 'white',
      border: 'none',
      borderRadius: '5px',
      fontSize: '30px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      cursor: 'pointer',
      boxShadow: '0 2px 4px rgba(0, 0, 0, 0.2)'
    },
    numberDisplay: {
      width: '80px',
      height: '60px',
      border: '2px solid #d0d0d0',
      borderRadius: '5px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '24px',
      fontWeight: 'bold',
      backgroundColor: 'white'
    },
    disabledBtn: {
      backgroundColor: '#cccccc',
      cursor: 'not-allowed'
    },
    analysisSection: {
      backgroundColor: 'white',
      border: '1px solid #d0d0d0',
      borderRadius: '8px',
      padding: '15px',
      marginBottom: '15px'
    },
    analysisRow: {
      display: 'flex',
      justifyContent: 'space-between',
      padding: '8px 0',
      borderBottom: '1px solid #eeeeee'
    },
    analysisLabel: {
      fontWeight: '500'
    },
    analysisValue: {
      fontWeight: 'bold',
      color: '#1e5c8b'
    },
    footer: {
      display: 'flex',
      padding: '15px',
      gap: '15px',
      backgroundColor: '#1e5c8b'
    },
    cancelBtn: {
      flex: 1,
      height: '60px',
      backgroundColor: '#468bbf',
      color: 'white',
      border: 'none',
      borderRadius: '5px',
      fontSize: '18px',
      fontWeight: 'bold',
      cursor: 'pointer'
    },
    analyzeBtn: {
      flex: 1,
      height: '60px',
      backgroundColor: '#3c8c5c',
      color: 'white',
      border: 'none',
      borderRadius: '5px',
      fontSize: '18px',
      fontWeight: 'bold',
      cursor: 'pointer'
    }
  };
  
  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.headerTitle}>Game Paused for Analysis</h1>
        <p style={styles.instruction}>Declarer and Dummy, please show your cards to all players.</p>
      </div>
      
      <div style={styles.subHeader}>
        <h2 style={styles.subHeaderTitle}>Bonus Bridge Score Adjustment</h2>
        <button 
          style={styles.whatIsThisBtn}
          onClick={() => setShowExplanation(!showExplanation)}
        >
          What is this?
        </button>
      </div>
      
      {showExplanation && (
        <div style={{
          backgroundColor: 'white',
          padding: '15px',
          margin: '15px',
          border: '1px solid #d0d0d0',
          borderRadius: '5px'
        }}>
          <h3 style={{fontSize: '18px', marginTop: 0, color: '#1e5c8b'}}>About Bonus Bridge Scoring</h3>
          <p>
            Bonus Bridge enhances standard bridge scoring by adjusting based on hand strength and 
            distribution. Stronger hands receive lower bonuses while weaker hands get larger bonuses.
          </p>
          <p>
            Enter the combined High Card Points (HCP) for Declarer and Dummy, plus distribution factors:
          </p>
          <ul>
            <li><strong>HCP</strong>: Ace=4, King=3, Queen=2, Jack=1</li>
            <li><strong>Singletons</strong>: Having only one card of a suit</li>
            <li><strong>Voids</strong>: Having no cards of a suit</li>
            <li><strong>Long suits</strong>: Having 6+ cards in a suit</li>
          </ul>
          <button 
            style={{
              backgroundColor: '#468bbf',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              padding: '10px',
              width: '100%',
              fontSize: '16px',
              fontWeight: 'bold',
              cursor: 'pointer'
            }}
            onClick={() => setShowExplanation(false)}
          >
            Got it
          </button>
        </div>
      )}
      
      <div style={styles.contractSection}>
        <p style={styles.contractText}>Contract: {getContractSummary()}</p>
        <p style={styles.rawScoreText}>Raw score: {contractDetails.rawScore} points</p>
      </div>
      
      <div style={styles.contentArea}>
        <div style={styles.inputSection}>
          <h3 style={styles.sectionTitle}>Combined HCP (Declarer + Dummy)</h3>
          <div style={styles.numberControl}>
            <button 
              style={{
                ...styles.numberBtn,
                ...(totalHCP <= 16 ? styles.disabledBtn : {})
              }}
              onClick={() => setTotalHCP(Math.max(16, totalHCP - 1))}
              disabled={totalHCP <= 16}
            >
              -
            </button>
            <div style={styles.numberDisplay}>{totalHCP}</div>
            <button 
              style={{
                ...styles.numberBtn,
                ...(totalHCP >= 34 ? styles.disabledBtn : {})
              }}
              onClick={() => setTotalHCP(Math.min(34, totalHCP + 1))}
              disabled={totalHCP >= 34}
            >
              +
            </button>
          </div>
        </div>
        
        <div style={styles.inputSection}>
          <h3 style={styles.sectionTitle}>Number of Singletons</h3>
          <div style={styles.numberControl}>
            <button 
              style={{
                ...styles.numberBtn,
                ...(singletons <= 0 ? styles.disabledBtn : {})
              }}
              onClick={() => setSingletons(Math.max(0, singletons - 1))}
              disabled={singletons <= 0}
            >
              -
            </button>
            <div style={styles.numberDisplay}>{singletons}</div>
            <button 
              style={{
                ...styles.numberBtn,
                ...(singletons >= 4 ? styles.disabledBtn : {})
              }}
              onClick={() => setSingletons(Math.min(4, singletons + 1))}
              disabled={singletons >= 4}
            >
              +
            </button>
          </div>
        </div>
        
        <div style={styles.inputSection}>
          <h3 style={styles.sectionTitle}>Number of Voids</h3>
          <div style={styles.numberControl}>
            <button 
              style={{
                ...styles.numberBtn,
                ...(voids <= 0 ? styles.disabledBtn : {})
              }}
              onClick={() => setVoids(Math.max(0, voids - 1))}
              disabled={voids <= 0}
            >
              -
            </button>
            <div style={styles.numberDisplay}>{voids}</div>
            <button 
              style={{
                ...styles.numberBtn,
                ...(voids >= 4 ? styles.disabledBtn : {})
              }}
              onClick={() => setVoids(Math.min(4, voids + 1))}
              disabled={voids >= 4}
            >
              +
            </button>
          </div>
        </div>
        
        <div style={styles.inputSection}>
          <h3 style={styles.sectionTitle}>Number of Long Suits (6+ cards)</h3>
          <div style={styles.numberControl}>
            <button 
              style={{
                ...styles.numberBtn,
                ...(longSuits <= 0 ? styles.disabledBtn : {})
              }}
              onClick={() => setLongSuits(Math.max(0, longSuits - 1))}
              disabled={longSuits <= 0}
            >
              -
            </button>
            <div style={styles.numberDisplay}>{longSuits}</div>
            <button 
              style={{
                ...styles.numberBtn,
                ...(longSuits >= 4 ? styles.disabledBtn : {})
              }}
              onClick={() => setLongSuits(Math.min(4, longSuits + 1))}
              disabled={longSuits >= 4}
            >
              +
            </button>
          </div>
        </div>
        
        <div style={styles.analysisSection}>
          <div style={styles.analysisRow}>
            <div style={styles.analysisLabel}>Expected tricks based on HCP:</div>
            <div style={styles.analysisValue}>{calculateExpectedTricks()}</div>
          </div>
          <div style={styles.analysisRow}>
            <div style={styles.analysisLabel}>Expected tricks with distribution:</div>
            <div style={styles.analysisValue}>{calculateExpectedWithDistribution()}</div>
          </div>
          <div style={styles.analysisRow}>
            <div style={styles.analysisLabel}>Actual tricks taken:</div>
            <div style={styles.analysisValue}>{contractDetails.actualTricks}</div>
          </div>
          <div style={{...styles.analysisRow, borderBottom: 'none'}}>
            <div style={styles.analysisLabel}>Score adjustment percentage:</div>
            <div style={styles.analysisValue}>{calculateAdjustment()}%</div>
          </div>
        </div>
      </div>
      
      <div style={styles.footer}>
        <button style={styles.cancelBtn} onClick={onCancel}>
          Cancel
        </button>
        <button style={styles.analyzeBtn} onClick={handleAnalyze}>
          Calculate Final Score
        </button>
      </div>
    </div>
  );
};

export default ScoreAdjustment;
