import React, { useState, useEffect } from 'react';
import './ContractInput.css';

const ContractInput = ({ onContractChange, initialContract = '' }) => {
  // State for levels (1-7)
  const [level, setLevel] = useState('');
  // State for suits (♣, ♦, ♥, ♠, NT)
  const [suit, setSuit] = useState('');
  // State for declarer (N, E, S, W)
  const [declarer, setDeclarer] = useState('');
  // State for doubled status - separate from actual contract representation
  const [doubledState, setDoubledState] = useState(null); // null means not yet set
  
  // Parse initialContract if provided
  useEffect(() => {
    if (initialContract) {
      const contractMatch = initialContract.match(/(\d)([♣♦♥♠]|NT)\s+([NESW])(X{0,2})/);
      if (contractMatch) {
        setLevel(contractMatch[1]);
        setSuit(contractMatch[2]);
        setDeclarer(contractMatch[3]);
        // Set doubled state based on contract
        const doubledValue = contractMatch[4] || '';
        setDoubledState(doubledValue);
      }
    } else {
      // Reset doubled state when contract is reset
      setDoubledState(null);
    }
  }, [initialContract]);
  
  // Update contract when any component changes
  useEffect(() => {
    // Only update contract if level, suit, and declarer are set
    if (level && suit && declarer) {
      // Only include doubled part if explicitly selected (not null)
      const doubledPart = doubledState !== null ? doubledState : '';
      const newContract = `${level}${suit} ${declarer}${doubledPart}`;
      onContractChange(newContract);
    }
  }, [level, suit, declarer, doubledState, onContractChange]);
  
  // Handle level selection
  const handleLevelClick = (selectedLevel) => {
    setLevel(selectedLevel);
  };
  
  // Handle suit selection
  const handleSuitClick = (selectedSuit) => {
    setSuit(selectedSuit);
  };
  
  // Handle declarer selection
  const handleDeclarerClick = (selectedDeclarer) => {
    setDeclarer(selectedDeclarer);
  };
  
  // Handle doubled selection - now explicitly tracks selection state
  const handleDoubledClick = (selectedDoubled) => {
    // Update doubled state with explicit selection
    setDoubledState(selectedDoubled);
  };
  
  // Return styles based on doubled button state
  const getDoubledButtonStyle = (value) => {
    // Default styles for all buttons
    const baseStyle = {
      flex: 1,
      padding: '0',
      border: '1px solid #ddd',
      borderRadius: '8px',
      fontSize: '20px',
      fontWeight: 'normal',
      cursor: 'pointer',
      backgroundColor: '#f8f8f8',
      color: 'black',
      height: '54px',
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'center'
    };
    
    // Selected state styles
    if (doubledState === value) {
      return {
        ...baseStyle,
        backgroundColor: '#3498db',
        color: 'white',
        borderColor: '#3498db',
        fontWeight: 'bold'
      };
    }
    
    return baseStyle;
  };
  
  return (
    <div className="contract-input-container">
      {/* Level Input */}
      <div className="level-suit-section">
        <div className="input-section-label">Level and Suit:</div>
        
        {/* Level Buttons (1-7) */}
        <div className="level-buttons">
          {[1, 2, 3, 4, 5, 6, 7].map((num) => (
            <button
              key={`level-${num}`}
              className={`level-btn ${level === num.toString() ? 'selected' : ''}`}
              onClick={() => handleLevelClick(num.toString())}
              data-level={num}
            >
              {num}
            </button>
          ))}
        </div>
        
        {/* Suit Buttons */}
        <div className="suit-buttons">
          {['♣', '♦', '♥', '♠', 'NT'].map((s) => (
            <button
              key={`suit-${s}`}
              className={`suit-btn ${suit === s ? 'selected' : ''}`}
              onClick={() => handleSuitClick(s)}
              data-suit={s}
            >
              {s}
            </button>
          ))}
        </div>
      </div>
      
      {/* Declarer Input */}
      <div className="declarer-section">
        <div className="input-section-label">Declarer:</div>
        <div className="declarer-buttons">
          {['N', 'S', 'E', 'W'].map((d) => (
            <button
              key={`declarer-${d}`}
              className={`declarer-btn ${declarer === d ? 'selected' : ''}`}
              onClick={() => handleDeclarerClick(d)}
            >
              {d}
            </button>
          ))}
        </div>
      </div>
      
      {/* Doubled Input - using inline styles but keeping button elements */}
      <div className="doubled-section">
        <div className="input-section-label">Doubled:</div>
        <div style={{ display: 'flex', gap: '8px' }}>
          <button 
            style={getDoubledButtonStyle('')}
            onClick={() => handleDoubledClick('')}
          >
            -
          </button>
          <button 
            style={getDoubledButtonStyle('X')}
            onClick={() => handleDoubledClick('X')}
          >
            X
          </button>
          <button 
            style={getDoubledButtonStyle('XX')}
            onClick={() => handleDoubledClick('XX')}
          >
            XX
          </button>
        </div>
      </div>
      
      {/* Debug info - set display to 'block' to see */}
      <div style={{ 
        display: 'none', 
        margin: '10px 0',
        padding: '5px',
        backgroundColor: '#f5f5f5',
        borderRadius: '4px',
        fontSize: '12px'
      }}>
        <div>Level: {level || 'not set'}</div>
        <div>Suit: {suit || 'not set'}</div>
        <div>Declarer: {declarer || 'not set'}</div>
        <div>Doubled State: {doubledState === null ? 'null (not set)' : `"${doubledState}"`}</div>
        <div>Contract: {level && suit && declarer ? `${level}${suit} ${declarer}${doubledState !== null ? doubledState : ''}` : 'incomplete'}</div>
      </div>
    </div>
  );
};

export default ContractInput;
