import React, { useState, useEffect } from 'react';
import './ContractInput.css';

const ContractInput = ({ onContractChange, initialContract = '' }) => {
  // State for levels (1-7)
  const [level, setLevel] = useState('');
  // State for suits (♣, ♦, ♥, ♠, NT)
  const [suit, setSuit] = useState('');
  // State for declarer (N, E, S, W)
  const [declarer, setDeclarer] = useState('');
  
  // Replace the doubled state with separate boolean flags
  const [isDoubled, setIsDoubled] = useState(false);
  const [isRedoubled, setIsRedoubled] = useState(false);
  
  // Parse initialContract if provided
  useEffect(() => {
    if (initialContract) {
      const contractMatch = initialContract.match(/(\d)([♣♦♥♠]|NT)\s+([NESW])(X{0,2})/);
      if (contractMatch) {
        setLevel(contractMatch[1]);
        setSuit(contractMatch[2]);
        setDeclarer(contractMatch[3]);
        
        // Parse doubled status from contract
        const doubledPart = contractMatch[4] || '';
        setIsDoubled(doubledPart === 'X' || doubledPart === 'XX');
        setIsRedoubled(doubledPart === 'XX');
      }
    } else {
      // Reset doubled/redoubled state when contract is reset
      setIsDoubled(false);
      setIsRedoubled(false);
    }
  }, [initialContract]);
  
  // Update contract when any component changes
  useEffect(() => {
    if (level && suit && declarer) {
      // Determine doubled part based on flags
      let doubledPart = '';
      if (isRedoubled) {
        doubledPart = 'XX';
      } else if (isDoubled) {
        doubledPart = 'X';
      }
      
      const newContract = `${level}${suit} ${declarer}${doubledPart}`;
      onContractChange(newContract);
    }
  }, [level, suit, declarer, isDoubled, isRedoubled, onContractChange]);
  
  // Handle level selection
  const handleLevelClick = (selectedLevel) => {
    setLevel(selectedLevel);
  };
  
  // Handle suit selection
  const handleSuitClick = (selectedSuit) => {
    setSuit(selectedSuit);
  };
  
  // Handle declarer selection
  const handleDeclarerClick = (selectedDeclarer) => {
    setDeclarer(selectedDeclarer);
  };
  
  // Handle doubled toggle
  const handleDoubledChange = (doubled) => {
    setIsDoubled(doubled);
    // If undoubled, also remove redoubled
    if (!doubled) {
      setIsRedoubled(false);
    }
  };
  
  // Handle redoubled toggle
  const handleRedoubledChange = (redoubled) => {
    setIsRedoubled(redoubled);
    // If redoubled, must also be doubled
    if (redoubled) {
      setIsDoubled(true);
    }
  };
  
  // Button style for Yes/No buttons
  const getYesNoButtonStyle = (isSelected) => {
    return {
      flex: 1,
      padding: '6px 0',
      border: '1px solid #ddd',
      borderRadius: '6px',
      backgroundColor: isSelected ? '#3498db' : '#f8f8f8',
      color: isSelected ? 'white' : 'black',
      fontWeight: isSelected ? 'bold' : 'normal',
      cursor: 'pointer',
      fontSize: '14px'
    };
  };
  
  // Row style for Yes/No options
  const optionRowStyle = {
    display: 'flex',
    gap: '8px',
    marginBottom: '8px'
  };
  
  // Option label style
  const optionLabelStyle = {
    flex: '0 0 80px',
    display: 'flex',
    alignItems: 'center',
    fontWeight: 'bold',
    fontSize: '14px'
  };
  
  // Options container style
  const optionsContainerStyle = {
    display: 'flex',
    gap: '4px',
    flex: 1
  };
  
  return (
    <div className="contract-input-container">
      {/* Level Input */}
      <div className="level-suit-section">
        <div className="input-section-label">Level and Suit:</div>
        
        {/* Level Buttons (1-7) */}
        <div className="level-buttons">
          {[1, 2, 3, 4, 5, 6, 7].map((num) => (
            <button
              key={`level-${num}`}
              className={`level-btn ${level === num.toString() ? 'selected' : ''}`}
              onClick={() => handleLevelClick(num.toString())}
              data-level={num}
            >
              {num}
            </button>
          ))}
        </div>
        
        {/* Suit Buttons */}
        <div className="suit-buttons">
          {['♣', '♦', '♥', '♠', 'NT'].map((s) => (
            <button
              key={`suit-${s}`}
              className={`suit-btn ${suit === s ? 'selected' : ''}`}
              onClick={() => handleSuitClick(s)}
              data-suit={s}
            >
              {s}
            </button>
          ))}
        </div>
      </div>
      
      {/* Declarer Input */}
      <div className="declarer-section">
        <div className="input-section-label">Declarer:</div>
        <div className="declarer-buttons">
          {['N', 'S', 'E', 'W'].map((d) => (
            <button
              key={`declarer-${d}`}
              className={`declarer-btn ${declarer === d ? 'selected' : ''}`}
              onClick={() => handleDeclarerClick(d)}
            >
              {d}
            </button>
          ))}
        </div>
      </div>
      
      {/* New Doubled/Redoubled Input with Yes/No options */}
      <div style={{ marginBottom: '10px' }}>
        <div className="input-section-label">Contract Status:</div>
        
        {/* Doubled Options */}
        <div style={optionRowStyle}>
          <div style={optionLabelStyle}>Doubled:</div>
          <div style={optionsContainerStyle}>
            <button 
              style={getYesNoButtonStyle(isDoubled)}
              onClick={() => handleDoubledChange(true)}
            >
              Yes
            </button>
            <button 
              style={getYesNoButtonStyle(!isDoubled)}
              onClick={() => handleDoubledChange(false)}
            >
              No
            </button>
          </div>
        </div>
        
        {/* Redoubled Options - only enabled if doubled is set */}
        <div style={optionRowStyle}>
          <div style={optionLabelStyle}>Redoubled:</div>
          <div style={optionsContainerStyle}>
            <button 
              style={{
                ...getYesNoButtonStyle(isRedoubled),
                opacity: isDoubled ? 1 : 0.5,
                cursor: isDoubled ? 'pointer' : 'not-allowed'
              }}
              onClick={() => isDoubled && handleRedoubledChange(true)}
              disabled={!isDoubled}
            >
              Yes
            </button>
            <button 
              style={{
                ...getYesNoButtonStyle(!isRedoubled),
                opacity: isDoubled ? 1 : 0.5,
                cursor: isDoubled ? 'pointer' : 'not-allowed'
              }}
              onClick={() => isDoubled && handleRedoubledChange(false)}
              disabled={!isDoubled}
            >
              No
            </button>
          </div>
        </div>
      </div>
      
      {/* Current contract preview - uncomment if needed */}
      {/*
      <div style={{
        marginTop: '10px',
        padding: '8px',
        backgroundColor: '#f0f7ff',
        borderRadius: '6px',
        fontSize: '14px',
        textAlign: 'center'
      }}>
        {level && suit && declarer ? (
          <span>
            Contract: {level}{suit} {declarer}
            {isRedoubled ? 'XX' : isDoubled ? 'X' : ''}
          </span>
        ) : (
          <span>Complete the contract...</span>
        )}
      </div>
      */}
    </div>
  );
};

export default ContractInput;
