// File: src/components/ScoreTracking.js
import React, { useContext, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import './styles/global.css';

const ScoreTracking = () => {
  const { gameState, dispatch } = useContext(GameContext);
  const { deals, dealCount, totalScore, currentDeal } = gameState;
  const MAX_DEALS = 100; // Evaluation version limit

  // Check if the current deal is complete and can be submitted
  const canSubmitDeal = () => {
    const { contract, tricksMade, adjustedScore } = currentDeal;
    return contract !== null && tricksMade !== null && adjustedScore !== null;
  };

  // Handle submitting the current deal
  const handleSubmitDeal = () => {
    if (!canSubmitDeal()) return;
    
    dispatch({ type: 'SUBMIT_DEAL' });
  };

  // Handle starting a new game
  const handleNewGame = () => {
    if (window.confirm('Are you sure you want to start a new game? All current scores will be reset.')) {
      dispatch({ type: 'NEW_GAME' });
    }
  };

  // Check for evaluation limit
  useEffect(() => {
    if (dealCount >= MAX_DEALS) {
      alert(`You've reached the ${MAX_DEALS} deal limit for the evaluation version. Please consider purchasing the full version for unlimited deals.`);
    }
  }, [dealCount]);

  // Render the score history table
  const renderScoreHistory = () => {
    if (deals.length === 0) {
      return <p>No deals played yet.</p>;
    }

    return (
      <div className="score-table-container">
        <table className="score-table">
          <thead>
            <tr>
              <th>Deal</th>
              <th>Contract</th>
              <th>Result</th>
              <th>Score</th>
              <th>Running Total</th>
            </tr>
          </thead>
          <tbody>
            {deals.map((deal, index) => {
              const tricksResult = deal.tricksMade - deal.tricksRequired;
              const resultText = tricksResult === 0 
                ? 'Made' 
                : tricksResult > 0 
                  ? `Made +${tricksResult}` 
                  : `Down ${Math.abs(tricksResult)}`;
              
              const runningTotal = deals
                .slice(0, index + 1)
                .reduce((sum, d) => sum + d.adjustedScore, 0);
                
              return (
                <tr key={index}>
                  <td>{index + 1}</td>
                  <td>{deal.contract}</td>
                  <td className={tricksResult >= 0 ? 'made' : 'down'}>
                    {resultText}
                  </td>
                  <td>{deal.adjustedScore}</td>
                  <td>{runningTotal}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  };

  return (
    <div className="component-container">
      <h2>Score Tracking</h2>
      
      <div className="score-summary">
        <div className="summary-item">
          <span>Deals Played:</span>
          <span>{dealCount} / {MAX_DEALS}</span>
        </div>
        <div className="summary-item">
          <span>Total Score:</span>
          <span className={totalScore >= 0 ? 'positive' : 'negative'}>
            {totalScore}
          </span>
        </div>
      </div>
      
      <div className="action-buttons">
        <button 
          className="primary-button"
          onClick={handleSubmitDeal}
          disabled={!canSubmitDeal() || dealCount >= MAX_DEALS}
        >
          Submit Deal
        </button>
        
        <button 
          className="secondary-button"
          onClick={handleNewGame}
        >
          New Game
        </button>
      </div>
      
      <h3>Deal History</h3>
      {renderScoreHistory()}
    </div>
  );
};

export default ScoreTracking;
