import React, { useState, useEffect } from 'react';
import './ContractInput.css'; // Make sure this import comes last

const ContractInput = ({ onContractChange, initialContract = '' }) => {
  // State for levels (1-7)
  const [level, setLevel] = useState('');
  // State for suits (♣, ♦, ♥, ♠, NT)
  const [suit, setSuit] = useState('');
  // State for declarer (N, E, S, W)
  const [declarer, setDeclarer] = useState('');
  // State for doubled status - use a different approach with a string enum
  // Use 'NONE', 'X', or 'XX' to be more explicit than empty string
  const [doubled, setDoubled] = useState('NONE');
  
  // Parse initialContract if provided
  useEffect(() => {
    if (initialContract) {
      const contractMatch = initialContract.match(/(\d)([♣♦♥♠]|NT)\s+([NESW])(X{0,2})/);
      if (contractMatch) {
        setLevel(contractMatch[1]);
        setSuit(contractMatch[2]);
        setDeclarer(contractMatch[3]);
        // Map the doubled part to our enum values
        setDoubled(contractMatch[4] === 'X' ? 'X' : 
                  contractMatch[4] === 'XX' ? 'XX' : 'NONE');
      }
    }
  }, [initialContract]);
  
  // Update contract when any component changes
  useEffect(() => {
    if (level && suit && declarer) {
      // Convert our enum doubled value to the actual contract representation
      const doubledText = doubled === 'X' ? 'X' : 
                         doubled === 'XX' ? 'XX' : '';
      
      const newContract = `${level}${suit} ${declarer}${doubledText}`;
      onContractChange(newContract);
    }
  }, [level, suit, declarer, doubled, onContractChange]);
  
  // Handle level selection
  const handleLevelClick = (selectedLevel) => {
    setLevel(selectedLevel);
  };
  
  // Handle suit selection
  const handleSuitClick = (selectedSuit) => {
    setSuit(selectedSuit);
  };
  
  // Handle declarer selection
  const handleDeclarerClick = (selectedDeclarer) => {
    setDeclarer(selectedDeclarer);
  };
  
  // Display-only debug values (will be hidden in production)
  const debugValues = {
    display: 'none', // Change to 'block' to show
    padding: '5px',
    margin: '5px',
    backgroundColor: '#f5f5f5',
    border: '1px solid #ddd',
    borderRadius: '4px',
    fontSize: '12px'
  };
  
  return (
    <div className="contract-input-container">
      {/* Debug values - hidden in production */}
      <div style={debugValues}>
        Level: {level}, Suit: {suit}, Declarer: {declarer}, Doubled: {doubled}
      </div>
      
      {/* Level Input */}
      <div className="level-suit-section">
        <div className="input-section-label">Level and Suit:</div>
        
        {/* Level Buttons (1-7) */}
        <div className="level-buttons">
          {[1, 2, 3, 4, 5, 6, 7].map((num) => (
            <button
              key={`level-${num}`}
              className={`level-btn ${level === num.toString() ? 'selected' : ''}`}
              onClick={() => handleLevelClick(num.toString())}
              data-level={num}
            >
              {num}
            </button>
          ))}
        </div>
        
        {/* Suit Buttons */}
        <div className="suit-buttons">
          {['♣', '♦', '♥', '♠', 'NT'].map((s) => (
            <button
              key={`suit-${s}`}
              className={`suit-btn ${suit === s ? 'selected' : ''}`}
              onClick={() => handleSuitClick(s)}
              data-suit={s}
            >
              {s}
            </button>
          ))}
        </div>
      </div>
      
      {/* Declarer Input */}
      <div className="declarer-section">
        <div className="input-section-label">Declarer:</div>
        <div className="declarer-buttons">
          {['N', 'S', 'E', 'W'].map((d) => (
            <button
              key={`declarer-${d}`}
              className={`declarer-btn ${declarer === d ? 'selected' : ''}`}
              onClick={() => handleDeclarerClick(d)}
            >
              {d}
            </button>
          ))}
        </div>
      </div>
      
      {/* Doubled Input - Using a completely new approach with inline styles */}
      <div className="doubled-section">
        <div className="input-section-label">Doubled:</div>
        <div className="doubled-buttons">
          {/* Apply custom direct styles to doubled buttons to override any external CSS */}
          <button
            className="doubled-btn"
            onClick={() => setDoubled('NONE')}
            style={{
              flex: 1,
              height: '54px',
              border: '1px solid #ddd',
              backgroundColor: doubled === 'NONE' ? '#3498db' : '#f8f8f8',
              color: doubled === 'NONE' ? 'white' : 'black',
              borderColor: doubled === 'NONE' ? '#3498db' : '#ddd',
              borderRadius: '8px',
              fontSize: '20px',
              fontWeight: doubled === 'NONE' ? 'bold' : 'normal',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              opacity: 1
            }}
          >
            -
          </button>
          <button
            className="doubled-btn"
            onClick={() => setDoubled('X')}
            style={{
              flex: 1,
              height: '54px',
              border: '1px solid #ddd',
              backgroundColor: doubled === 'X' ? '#3498db' : '#f8f8f8',
              color: doubled === 'X' ? 'white' : 'black',
              borderColor: doubled === 'X' ? '#3498db' : '#ddd',
              borderRadius: '8px',
              fontSize: '20px',
              fontWeight: doubled === 'X' ? 'bold' : 'normal',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              opacity: 1
            }}
          >
            X
          </button>
          <button
            className="doubled-btn"
            onClick={() => setDoubled('XX')}
            style={{
              flex: 1,
              height: '54px',
              border: '1px solid #ddd',
              backgroundColor: doubled === 'XX' ? '#3498db' : '#f8f8f8',
              color: doubled === 'XX' ? 'white' : 'black',
              borderColor: doubled === 'XX' ? '#3498db' : '#ddd',
              borderRadius: '8px',
              fontSize: '20px',
              fontWeight: doubled === 'XX' ? 'bold' : 'normal',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              opacity: 1
            }}
          >
            XX
          </button>
        </div>
      </div>
    </div>
  );
};

export default ContractInput;
