import React, { useState, useEffect } from 'react';
import './GameScoreSheet.css';

/**
 * Game Score Sheet component to display final game results and all deals played
 * @param {Object} props - Component props
 * @param {Object} props.gameState - Current game state
 * @param {Function} props.onNewGame - Function to start a new game
 */
const GameScoreSheet = ({ gameState, onNewGame }) => {
  // State for selected tab
  const [activeTab, setActiveTab] = useState('summary');
  // State for deal detail popup
  const [selectedDeal, setSelectedDeal] = useState(null);
  // State for player stats
  const [playerStats, setPlayerStats] = useState(null);
  
  useEffect(() => {
    // Scroll to top when component mounts
    window.scrollTo(0, 0);
    
    // Calculate player statistics
    calculatePlayerStats();
  }, []);
  
  // Calculate player statistics from game state
  const calculatePlayerStats = () => {
    if (!gameState.deals || gameState.deals.length === 0) {
      return;
    }
    
    // Initialize player stats
    const stats = {
      north: { totalHCP: 0, totalPoints: 0, deals: 0, madeBids: 0 },
      east: { totalHCP: 0, totalPoints: 0, deals: 0, madeBids: 0 },
      south: { totalHCP: 0, totalPoints: 0, deals: 0, madeBids: 0 },
      west: { totalHCP: 0, totalPoints: 0, deals: 0, madeBids: 0 }
    };
    
    // Process each deal
    gameState.deals.forEach(deal => {
      if (!deal.contract) return;
      
      // Extract declarer from contract
      const contractMatch = deal.contract.match(/(\d)([♣♦♥♠]|NT)\s+([NESW])(X{0,2})/);
      if (!contractMatch) return;
      
      const declarer = contractMatch[3].toLowerCase();
      const madeContract = deal.result >= 0;
      let player = '';
      
      // Map direction to player
      switch(declarer) {
        case 'n': player = 'north'; break;
        case 'e': player = 'east'; break;
        case 's': player = 'south'; break;
        case 'w': player = 'west'; break;
        default: return;
      }
      
      // Update player stats
      stats[player].deals++;
      if (madeContract) {
        stats[player].madeBids++;
      }
      
      // Add HCP if available
      if (deal.handAnalysis && deal.handAnalysis.totalHCP) {
        stats[player].totalHCP += deal.handAnalysis.totalHCP;
      }
      
      // Add bonus points based on declarer
      const isNS = declarer === 'n' || declarer === 's';
      if (isNS) {
        if (madeContract && deal.bonusNsPoints) {
          stats[player].totalPoints += deal.bonusNsPoints;
        }
      } else {
        if (madeContract && deal.bonusEwPoints) {
          stats[player].totalPoints += deal.bonusEwPoints;
        }
      }
    });
    
    // Calculate averages and set state
    setPlayerStats(stats);
  };
  
  // Format contract for display
  const formatContract = (deal) => {
    if (!deal || !deal.contract) return "No contract";
    
    const contractMatch = deal.contract.match(/(\d)([♣♦♥♠]|NT)\s+([NESW])(X{0,2})/);
    if (!contractMatch) return deal.contract;
    
    const level = contractMatch[1];
    const suit = contractMatch[2];
    const declarer = contractMatch[3];
    const doubled = contractMatch[4] || "";
    
    let doubledText = "";
    if (doubled === "X") {
      doubledText = "X";
    } else if (doubled === "XX") {
      doubledText = "XX";
    }
    
    return `${level}${suit} ${declarer}${doubledText}`;
  };
  
  // Format result for display
  const formatResult = (deal) => {
    if (!deal || deal.result === undefined) return "No result";
    
    if (deal.result >= 0) {
      return deal.result > 0 ? `+${deal.result}` : "=";
    } else {
      return deal.result; // Already negative
    }
  };
  
  // View deal details
  const viewDealDetails = (deal) => {
    setSelectedDeal(deal);
  };
  
  // Close deal details popup
  const closeDealDetails = () => {
    setSelectedDeal(null);
  };
  
  // Change active tab
  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };
  
  // Get final scores
  const getFinalScores = () => {
    return {
      partyNS: gameState.scores?.nsTotal || 0,
      partyEW: gameState.scores?.ewTotal || 0,
      bonusNS: gameState.scores?.bonusNsTotal || 0,
      bonusEW: gameState.scores?.bonusEwTotal || 0
    };
  };
  
  // Determine winners
  const getWinners = () => {
    const scores = getFinalScores();
    
    return {
      partyWinner: scores.partyNS > scores.partyEW ? "North-South" : (scores.partyEW > scores.partyNS ? "East-West" : "Tie"),
      bonusWinner: scores.bonusNS > scores.bonusEW ? "North-South" : (scores.bonusEW > scores.bonusNS ? "East-West" : "Tie")
    };
  };
  
  // Calculate game statistics
  const getGameStats = () => {
    if (!gameState.deals || gameState.deals.length === 0) {
      return {
        totalDeals: 0,
        nsDeals: 0,
        ewDeals: 0,
        nsMade: 0,
        ewMade: 0,
        slams: 0,
        games: 0,
        partScores: 0
      };
    }
    
    let nsDeals = 0;
    let ewDeals = 0;
    let nsMade = 0;
    let ewMade = 0;
    let slams = 0;
    let games = 0;
    let partScores = 0;
    
    gameState.deals.forEach(deal => {
      if (!deal.contract) return;
      
      const contractMatch = deal.contract.match(/(\d)([♣♦♥♠]|NT)\s+([NESW])(X{0,2})/);
      if (!contractMatch) return;
      
      const level = parseInt(contractMatch[1]);
      const suit = contractMatch[2];
      const declarer = contractMatch[3];
      const madeContract = deal.result >= 0;
      
      // Count deals by declarer
      const isNS = declarer === 'N' || declarer === 'S';
      if (isNS) {
        nsDeals++;
        if (madeContract) nsMade++;
      } else {
        ewDeals++;
        if (madeContract) ewMade++;
      }
      
      // Categorize contract
      if (level >= 6) {
        slams++;
      } else if ((level === 3 && suit === 'NT') || 
                 (level === 4 && (suit === '♥' || suit === '♠')) || 
                 (level === 5 && (suit === '♣' || suit === '♦'))) {
        games++;
      } else {
        partScores++;
      }
    });
    
    return {
      totalDeals: gameState.deals.length,
      nsDeals,
      ewDeals,
      nsMade,
      ewMade,
      slams,
      games,
      partScores
    };
  };
  
  // Get HCP analysis
  const getHCPAnalysis = () => {
    if (!gameState.deals || gameState.deals.length === 0) {
      return {
        avgDeclarer: 0,
        avgDefender: 0,
        avgSuccess: 0
      };
    }
    
    let totalDeclarerHCP = 0;
    let totalDefenderHCP = 0;
    let dealsWithHCP = 0;
    let successfulDealsWithHCP = 0;
    let totalHCPSuccess = 0;
    
    gameState.deals.forEach(deal => {
      if (!deal.handAnalysis || !deal.handAnalysis.totalHCP) return;
      
      dealsWithHCP++;
      totalDeclarerHCP += deal.handAnalysis.totalHCP;
      
      // Calculate defender HCP as 40 - declarer HCP
      const defenderHCP = 40 - deal.handAnalysis.totalHCP;
      totalDefenderHCP += defenderHCP;
      
      // Calculate success rate based on HCP
      if (deal.result >= 0) {
        successfulDealsWithHCP++;
        totalHCPSuccess += deal.handAnalysis.totalHCP;
      }
    });
    
    return {
      avgDeclarer: dealsWithHCP > 0 ? (totalDeclarerHCP / dealsWithHCP).toFixed(1) : 0,
      avgDefender: dealsWithHCP > 0 ? (totalDefenderHCP / dealsWithHCP).toFixed(1) : 0,
      avgSuccess: successfulDealsWithHCP > 0 ? (totalHCPSuccess / successfulDealsWithHCP).toFixed(1) : 0
    };
  };
  
  // Get best player based on bonus points and success rate
  const getBestPlayer = () => {
    if (!playerStats) return { name: "None", points: 0 };
    
    const players = Object.keys(playerStats);
    let bestPlayer = { name: "None", points: 0, successRate: 0 };
    
    players.forEach(player => {
      const stats = playerStats[player];
      if (stats.deals === 0) return;
      
      const successRate = stats.madeBids / stats.deals;
      const avgPoints = stats.totalPoints / stats.deals;
      
      // Consider both points and success rate
      if (avgPoints > bestPlayer.points || 
          (avgPoints === bestPlayer.points && successRate > bestPlayer.successRate)) {
        bestPlayer = {
          name: player.charAt(0).toUpperCase() + player.slice(1),
          points: avgPoints,
          successRate
        };
      }
    });
    
    return bestPlayer;
  };
  
  const scores = getFinalScores();
  const winners = getWinners();
  const stats = getGameStats();
  const hcpAnalysis = getHCPAnalysis();
  const bestPlayer = getBestPlayer();
  
  return (
    <div className="game-score-sheet">
      <h2>Game #{gameState.gameNumber} Results</h2>
      
      {/* Tab Navigation */}
      <div className="tab-navigation">
        <button 
          className={`tab-button ${activeTab === 'summary' ? 'active' : ''}`}
          onClick={() => handleTabChange('summary')}
        >
          Summary
        </button>
        <button 
          className={`tab-button ${activeTab === 'scoresheet' ? 'active' : ''}`}
          onClick={() => handleTabChange('scoresheet')}
        >
          Score Sheet
        </button>
        <button 
          className={`tab-button ${activeTab === 'analysis' ? 'active' : ''}`}
          onClick={() => handleTabChange('analysis')}
        >
          Analysis
        </button>
      </div>
      
      <div className="tab-content">
        {/* Summary Tab */}
        {activeTab === 'summary' && (
          <div className="summary-tab">
            <div className="score-summaries">
              {/* Party Bridge Scores */}
              <div className="score-summary party-bridge">
                <h3>Party Bridge Scores</h3>
                <div className="final-scores">
                  <div className="team-score">
                    <span className="team-label">North-South:</span>
                    <span className="team-value">{scores.partyNS}</span>
                  </div>
                  <div className="team-score">
                    <span className="team-label">East-West:</span>
                    <span className="team-value">{scores.partyEW}</span>
                  </div>
                  <div className="winner">
                    <span className="winner-label">Winner:</span>
                    <span className="winner-value">{winners.partyWinner}</span>
                  </div>
                </div>
              </div>
              
              {/* Bonus Bridge Scores */}
              <div className="score-summary bonus-bridge">
                <h3>Bonus Bridge Scores</h3>
                <div className="final-scores">
                  <div className="team-score">
                    <span className="team-label">North-South:</span>
                    <span className="team-value">{scores.bonusNS}</span>
                  </div>
                  <div className="team-score">
                    <span className="team-label">East-West:</span>
                    <span className="team-value">{scores.bonusEW}</span>
                  </div>
                  <div className="winner">
                    <span className="winner-label">Winner:</span>
                    <span className="winner-value">{winners.bonusWinner}</span>
                  </div>
                </div>
                <button className="explanation-button" onClick={() => alert("Bonus Bridge scoring rewards both declarers and defenders based on high card points, distribution, and performance relative to expectations.")}>
                  How Bonus Scoring Works
                </button>
              </div>
            </div>
            
            {/* Game Statistics */}
            <div className="deals-table-container">
              <h3>Game Statistics</h3>
              <table className="deals-table">
                <thead>
                  <tr>
                    <th>Statistic</th>
                    <th>Value</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>Total Deals</td>
                    <td>{stats.totalDeals}</td>
                  </tr>
                  <tr>
                    <td>North-South Contracts</td>
                    <td>{stats.nsDeals} ({stats.nsDeals > 0 ? Math.round((stats.nsMade / stats.nsDeals) * 100) : 0}% made)</td>
                  </tr>
                  <tr>
                    <td>East-West Contracts</td>
                    <td>{stats.ewDeals} ({stats.ewDeals > 0 ? Math.round((stats.ewMade / stats.ewDeals) * 100) : 0}% made)</td>
                  </tr>
                  <tr>
                    <td>Slam Contracts</td>
                    <td>{stats.slams}</td>
                  </tr>
                  <tr>
                    <td>Game Contracts</td>
                    <td>{stats.games}</td>
                  </tr>
                  <tr>
                    <td>Part Score Contracts</td>
                    <td>{stats.partScores}</td>
                  </tr>
                  <tr>
                    <td>Average Declarer HCP</td>
                    <td>{hcpAnalysis.avgDeclarer}</td>
                  </tr>
                  <tr>
                    <td>Best Player</td>
                    <td>{bestPlayer.name} ({bestPlayer.points.toFixed(1)} avg points)</td>
                  </tr>
                </tbody>
              </table>
            </div>
            
            <div className="sheet-actions">
              <button className="new-game-button" onClick={onNewGame}>
                Start New Game
              </button>
            </div>
          </div>
        )}
        
        {/* Score Sheet Tab */}
        {activeTab === 'scoresheet' && (
          <div className="scoresheet-tab">
            <h3>Deal History</h3>
            <div className="deals-table-container">
              <table className="deals-table">
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Contract</th>
                    <th>Result</th>
                    <th>NS</th>
                    <th>EW</th>
                    <th>Bonus NS</th>
                    <th>Bonus EW</th>
                    <th></th>
                  </tr>
                </thead>
                <tbody>
                  {gameState.deals.map((deal, index) => (
                    <tr key={index}>
                      <td>{deal.dealNumber}</td>
                      <td>{formatContract(deal)}</td>
                      <td>{formatResult(deal)}</td>
                      <td>{deal.nsPoints || 0}</td>
                      <td>{deal.ewPoints || 0}</td>
                      <td>{deal.bonusNsPoints || 0}</td>
                      <td>{deal.bonusEwPoints || 0}</td>
                      <td>
                        <button 
                          className="detail-button"
                          onClick={() => viewDealDetails(deal)}
                        >
                          Details
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
                <tfoot>
                  <tr>
                    <td colSpan="3">Total:</td>
                    <td>{scores.partyNS}</td>
                    <td>{scores.partyEW}</td>
                    <td>{scores.bonusNS}</td>
                    <td>{scores.bonusEW}</td>
                    <td></td>
                  </tr>
                </tfoot>
              </table>
            </div>
            
            <div className="sheet-actions">
              <button className="new-game-button" onClick={onNewGame}>
                Start New Game
              </button>
            </div>
          </div>
        )}
        
        {/* Analysis Tab */}
        {activeTab === 'analysis' && (
          <div className="analysis-tab">
            <h3>Contract Distribution</h3>
            <div className="categories-container">
              <div className="category-item">
                <div className="category-name">Slams</div>
                <div className="category-count">{stats.slams}</div>
                <div className="category-percentage">
                  {stats.totalDeals > 0 ? Math.round((stats.slams / stats.totalDeals) * 100) : 0}%
                </div>
              </div>
              <div className="category-item">
                <div className="category-name">Games</div>
                <div className="category-count">{stats.games}</div>
                <div className="category-percentage">
                  {stats.totalDeals > 0 ? Math.round((stats.games / stats.totalDeals) * 100) : 0}%
                </div>
              </div>
              <div className="category-item">
                <div className="category-name">Part Scores</div>
                <div className="category-count">{stats.partScores}</div>
                <div className="category-percentage">
                  {stats.totalDeals > 0 ? Math.round((stats.partScores / stats.totalDeals) * 100) : 0}%
                </div>
              </div>
            </div>
            
            <h3>HCP and Distribution Analysis</h3>
            <div className="comparison-container">
              <div className="comparison-item">
                <div className="comparison-label">Average Declarer HCP</div>
                <div className="comparison-value">{hcpAnalysis.avgDeclarer}</div>
              </div>
              <div className="comparison-item">
                <div className="comparison-label">Average Defender HCP</div>
                <div className="comparison-value">{hcpAnalysis.avgDefender}</div>
              </div>
              <div className="comparison-item">
                <div className="comparison-label">Average Successful Bid HCP</div>
                <div className="comparison-value">{hcpAnalysis.avgSuccess}</div>
              </div>
            </div>
            
            <h3>Player Performance</h3>
            <div className="team-comparison">
              <div className="team-stats">
                <h4>North-South Players</h4>
                <div className="stat-row">
                  <span>North Contracts:</span>
                  <span>{playerStats?.north.deals || 0}</span>
                </div>
                <div className="stat-row">
                  <span>North Made:</span>
                  <span>{playerStats?.north.madeBids || 0} ({playerStats?.north.deals > 0 ? Math.round((playerStats.north.madeBids / playerStats.north.deals) * 100) : 0}%)</span>
                </div>
                <div className="stat-row">
                  <span>North Avg Points:</span>
                  <span>{playerStats?.north.deals > 0 ? (playerStats.north.totalPoints / playerStats.north.deals).toFixed(1) : 0}</span>
                </div>
                <div className="stat-row">
                  <span>South Contracts:</span>
                  <span>{playerStats?.south.deals || 0}</span>
                </div>
                <div className="stat-row">
                  <span>South Made:</span>
                  <span>{playerStats?.south.madeBids || 0} ({playerStats?.south.deals > 0 ? Math.round((playerStats.south.madeBids / playerStats.south.deals) * 100) : 0}%)</span>
                </div>
                <div className="stat-row">
                  <span>South Avg Points:</span>
                  <span>{playerStats?.south.deals > 0 ? (playerStats.south.totalPoints / playerStats.south.deals).toFixed(1) : 0}</span>
                </div>
              </div>
              
              <div className="team-stats">
                <h4>East-West Players</h4>
                <div className="stat-row">
                  <span>East Contracts:</span>
                  <span>{playerStats?.east.deals || 0}</span>
                </div>
                <div className="stat-row">
                  <span>East Made:</span>
                  <span>{playerStats?.east.madeBids || 0} ({playerStats?.east.deals > 0 ? Math.round((playerStats.east.madeBids / playerStats.east.deals) * 100) : 0}%)</span>
                </div>
                <div className="stat-row">
                  <span>East Avg Points:</span>
                  <span>{playerStats?.east.deals > 0 ? (playerStats.east.totalPoints / playerStats.east.deals).toFixed(1) : 0}</span>
                </div>
                <div className="stat-row">
                  <span>West Contracts:</span>
                  <span>{playerStats?.west.deals || 0}</span>
                </div>
                <div className="stat-row">
                  <span>West Made:</span>
                  <span>{playerStats?.west.madeBids || 0} ({playerStats?.west.deals > 0 ? Math.round((playerStats.west.madeBids / playerStats.west.deals) * 100) : 0}%)</span>
                </div>
                <div className="stat-row">
                  <span>West Avg Points:</span>
                  <span>{playerStats?.west.deals > 0 ? (playerStats.west.totalPoints / playerStats.west.deals).toFixed(1) : 0}</span>
                </div>
              </div>
            </div>
            
            <div className="sheet-actions">
              <button className="new-game-button" onClick={onNewGame}>
                Start New Game
              </button>
            </div>
          </div>
        )}
      </div>
      
      {/* Deal Detail Popup */}
      {selectedDeal && (
        <div className="popup-overlay">
          <div className="detail-popup">
            <h3>Deal #{selectedDeal.dealNumber} Details</h3>
            
            <div className="deal-info">
              <div className="deal-basic-info">
                <div className="info-row">
                  <span className="info-label">Contract:</span>
                  <span>{formatContract(selectedDeal)}</span>
                </div>
                <div className="info-row">
                  <span className="info-label">Result:</span>
                  <span>{formatResult(selectedDeal)}</span>
                </div>
                <div className="info-row">
                  <span className="info-label">Vulnerability:</span>
                  <span>
                    {selectedDeal.vulnerable?.ns ? 'NS Vul' : 'NS Not Vul'}, 
                    {selectedDeal.vulnerable?.ew ? ' EW Vul' : ' EW Not Vul'}
                  </span>
                </div>
              </div>
              
              <div className="deal-scores">
                <h4>Standard Bridge Scores</h4>
                <div className="info-row">
                  <span className="info-label">NS Points:</span>
                  <span>{selectedDeal.nsPoints || 0}</span>
                </div>
                <div className="info-row">
                  <span className="info-label">EW Points:</span>
                  <span>{selectedDeal.ewPoints || 0}</span>
                </div>
                
                <h4>Bonus Bridge Scores</h4>
                <div className="info-row">
                  <span className="info-label">NS Bonus:</span>
                  <span>{selectedDeal.bonusNsPoints || 0}</span>
                </div>
                <div className="info-row">
                  <span className="info-label">EW Bonus:</span>
                  <span>{selectedDeal.bonusEwPoints || 0}</span>
                </div>
              </div>
            </div>
            
            {selectedDeal.handAnalysis && (
              <div className="analysis-details">
                <h4>Hand Analysis</h4>
                <div className="info-row">
                  <span className="info-label">Total HCP:</span>
                  <span>{selectedDeal.handAnalysis.totalHCP || 0}</span>
                </div>
                <div className="info-row">
                  <span className="info-label">HCP Advantage:</span>
                  <span>{selectedDeal.handAnalysis.hcpAdvantage || 0}% to {selectedDeal.handAnalysis.advantageSide || 'None'}</span>
                </div>
                <div className="info-row">
                  <span className="info-label">Distribution:</span>
                  <span>
                    {selectedDeal.handAnalysis.singletons || 0} singletons, 
                    {selectedDeal.handAnalysis.voids || 0} voids,
                    {selectedDeal.handAnalysis.longSuits || 0} long suits
                  </span>
                </div>
                <div className="info-row">
                  <span className="info-label">Expected HCP:</span>
                  <span>{selectedDeal.handAnalysis.expectedHCP || 0}</span>
                </div>
                <div className="info-row">
                  <span className="info-label">Expected Tricks:</span>
                  <span>{selectedDeal.handAnalysis.handExpectedTricks || 0}</span>
                </div>
                
                <h4>Bonus Score Calculation</h4>
                <ul className="calculation-steps">
                  <li>
                    Base Score: 
                    {selectedDeal.result >= 0 ? 
                      `${((selectedDeal.handAnalysis.rawScore || 0) / 20).toFixed(1)} points` : 
                      `${(selectedDeal.handAnalysis.basePenalty || 0).toFixed(1)} points`}
                  </li>
                  {selectedDeal.result >= 0 ? (
                    // For made contracts
                    <>
                      <li>HCP Adjustment: {(selectedDeal.handAnalysis.hcpAdjustment || 0).toFixed(2)} points</li>
                      <li>Performance Adjustment: {(selectedDeal.handAnalysis.performanceVariance || 0)} tricks</li>
                      <li>Contract Type: {selectedDeal.handAnalysis.contractTypeDescription || ''}</li>
                      <li>Distribution Adjustment: {(selectedDeal.handAnalysis.distributionAdjustment || 0).toFixed(1)} points</li>
                    </>
                  ) : (
                    // For defeated contracts
                    <>
                      <li>Level Penalties: {(selectedDeal.handAnalysis.levelPenalties || 0).toFixed(1)} points</li>
                      <li>Performance Bonus: {(selectedDeal.handAnalysis.performanceBonus || 0).toFixed(1)} points</li>
                      <li>Declarer Consolation: {(selectedDeal.handAnalysis.consolationPoints || 0).toFixed(1)} points</li>
                    </>
                  )}
                  <li>Final NS Bonus: {selectedDeal.bonusNsPoints || 0} points</li>
                  <li>Final EW Bonus: {selectedDeal.bonusEwPoints || 0} points</li>
                </ul>
              </div>
            )}
            
            <div className="popup-actions">
              <button onClick={closeDealDetails}>Close</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default GameScoreSheet;
